css:
@-moz-document url-prefix("http://s1.zetaboards.com"),  url-prefix("http://s2.zetaboards.com"), url-prefix("http://s3.zetaboards.com"), url-prefix("http://s4.zetaboards.com"), url-prefix("http://s5.zetaboards.com"), url-prefix("http://s6.zetaboards.com"), url-prefix("http://s7.zetaboards.com"), url-prefix("http://s8.zetaboards.com"), url-prefix("http://s9.zetaboards.com"), url-prefix("http://s10.zetaboards.com"), url-prefix("http://s11.zetaboards.com"), url-prefix("http://s12.zetaboards.com"), url-prefix("http://s13.zetaboards.com"), url-prefix("http://s14.zetaboards.com"), url-prefix("http://s15.zetaboards.com") {
html,body {
background:#001440;
background-image:url(http://web.archive.org/web/20131231042849/http://img40.imageshack.us/img40/9149/grad1bgfinal.gif);
background-repeat:repeat-y;
background-attachment:fixed;
background-position:center; 
color:#1a1a1a;
font-family:"Helvetica", "Arial", "Bitstream Vera Sans", "Verdana", sans-serif;
font-size:93.3%;
margin:0;
padding:0;
}

a {
color:#cfcfcf;
text-decoration:none;
}

a:hover {
color:#bcbcbc;
text-decoration:underline;
}

.meta_box h3,.meta_box a,.meta_box a:hover {
color: #ffffff;
}

#ucp_gallery a:hover {
text-decoration: none;
}

#profile_menuwrap {
padding: 1px;
font-size: 85%
}

hr {
background:#0a0a0a;
border:0;
clear:both;
color:#ffffff;
height:1px;
}

table {
border-collapse:collapse;
border-spacing:0;
clear:both;
empty-cells:show;
width:100%;
}

th,td {
border:1px solid #072151;
}

th {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
color:#ffffff;
font-weight:400;
padding:3px 5px;
text-align:left;
}

td {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#ffffff;
padding:5px 10px;
}

input.row1 {
background: none;
}

th a {
color:#ffffff;
font-weight:700;
}

th a:hover {
color:#5ba9f1;
font-weight:700;
}

h1 {
font-size:200%;
}

h2,h3 {
font-size:100%;
}

.category h2 {
background:url(http://web.archive.org/web/20131231042849/http://i90.photobucket.com/albums/k279/Kellywizkid/EXE%20Wired/headgradspecial.gif) no-repeat top center #021d64;
color:#001440;
font-size:110%;
font-weight:700;
}

.topic h2,#portal h2,#portal_foot h2 {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
border:1px solid #0a0a0a;
color:#ffffff;
font-size:110%;
font-weight:700;
border-bottom-width:1px;
padding:50px;
text-align:center;
}

#portal_foot h2 {
clear:none;
margin-right: -1px;
margin-left: -1px;
}

thead th {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
border:1px solid #0a0a0a;
color:#ffffff;
font-size:110%;
font-weight:700;
border-bottom-width:1px;
padding:5px;
text-align:center;
}

h2.special {
background:url(http://web.archive.org/web/20131231042849/http://i90.photobucket.com/albums/k279/Kellywizkid/EXE%20Wired/headgradspecial.gif) no-repeat top center #021d64;
border: none;
}

input,select,textarea,button,a.btn_fake {
background: #0a2961;
border:1px solid #0A0A0A;
color:#ffffff;
padding:3px 5px;
vertical-align:middle;
}

toptable_foot {
background: #0a2961;
}


textarea {
font-family:"Verdana", sans-serif;
font-size:100%;
margin:auto;
width:98%;
}

input:focus,input.focus,textarea:focus,textarea.focus {
border-color:#000000;
}

.submit_error {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
}

button,a.btn_fake {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
color:#ffffff;
cursor:default;
font-family:Verdana, Arial, Helvetica, sans-serif;
font-size:90%;
overflow:visible;
width:auto;
margin: 1px;
}

button:hover,a.btn_fake:hover {
border-color:#000000;
}

button.btn_text {
background:none;
border:0;
cursor:pointer;
font-family:Arial, "Bitstream Vera Sans", Verdana, sans-serif;
font-size:100%;
}

.notice {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#ffffff;
border:2px solid #0A0A0A;
margin:10px auto 0;
width:70%;
}

.notice dt {
font-weight:700;
}

.notice dd {
font-style:italic;
}

dl.info {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border-color:#0A0A0A;
}

.info dt {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
}

dl.error {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#ffffff;
border-color:#0A0A0A;
}

.error dt {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#ffffff;
}

#top ul,#nav,ul.cat-pages,ul.cat-topicpages,table.calendar ul,div.meta_menu ul,#pm_folderlist,#profile_menuwrap ul {
list-style:none;
padding:0;
}

#profile_menuwrap ul,ul.cat-topicpages,blockquote dl,#fast-reply dd {
margin:0;
}

#top li,#nav li,ul.cat-pages li,ul.cat-topicpages li,table.calendar li,#pm_folderlist li,#profile_menuwrap li,#ucp_gallery li {
display:inline;
}

#top_bar {
display: none;
}

#top {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
border:1px solid #0A0A0A;
height:20px;
padding:0 2% 4px;
margin-bottom:12px;
}

#top_info {
color:#ffffff;
float:left;
margin-top:4px;
}

#top_menu {
float:right;
margin-top:0px;
}

ul#top_menu.drop_menu {
float:left;
}
.drop_menu li {
float:left;
margin-right:1px;
text-align:center;
}

.drop_menu a {
color:#ffffff;
display:block;
padding:4px .25em;
position:relative;
}

.drop_menu ul {
z-index: 1;
-moz-opacity:0.85;
border-top:1px solid #0A0A0A;
color:#39474E;
left:-999em;
margin:0;
opacity:0.85;
position:absolute;
width:9.4em;
}

.drop_menu ul li {
font-size:85%;
margin:0;
padding:0;
width:100%;
}

.drop_menu ul li a {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:0 solid #0A0A0A;
border-width:0 1px 1px;
display:block;
padding:4px 5%;
width:90%;
}

.drop_menu li ul li a:hover {
background: #0a2961;
color: #ffffff;
text-decoration: none;
}

#logo {
background-color: #002874;
background-image:url(http://z4.ifrm.com/30571/170/0/f5059277/bgcbgif.jpg);
clear:both;
margin:0 2%;
margin-bottom: 5px;
}

#submenu_bar {
display: none;
}

#submenu {
background-image:url(http://web.archive.org/web/20131231042849/http://i90.photobucket.com/albums/k279/Kellywizkid/number2.gif);
background-repeat:no-repeat;
color:#e7e7e7;
font-size:85%;
margin:0 2%;
padding:48px 1% 18px;
text-align:center;
}

#wrap {
margin:auto;
width: 810px;
background-image:url(http://z4.ifrm.com/30571/170/0/f5059277/bgcbgif.jpg);
padding:10px;
}

#nav {
height:1%;
padding:10px 1%;
color:#ffffff;
height:1%;
}

.meta_box {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
font-size:85%;
line-height:150%;
padding:10px 25px;
}

.meta_menu {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
padding:0;
width:49%;
}

.meta_menu h3 {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
border:1px solid #0a0a0a;
clear:both;
color:#ffffff;
font-size:100%;
font-weight:700;
margin:-1px;
text-align:center;
}

.meta_menu a,.meta_menu a:hover {
color:#ffffff;
}

ul.cat-pages {
float:left;
font-size:90%;
margin:15px 0;
padding: 5px 1px;
width:49%;
}

.cat-pages a,.cat-pagesjump i,.cat-topicpages a {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
color:#ffffff;
}

.cat-pages a:hover,.cat-topicpages a:hover {
border: 1px solid #0A0A0A;
color:#bcbcbc;
}

.cat-pages span {
background: #0a2961;
border:1px solid #0A0A0A;
color:#ffffff;
cursor:default;
font-weight:700;
padding:2px 5px;
}

.cat-pages input {
background: #0a2961;
color:#ffffff;
padding:3px 5px;
vertical-align:baseline;
}

ul.cat-topicpages {
float:right;
font-size:80%;
padding:1px 0;
}

tr.generic td,div.generic {
font-size:120%;
font-weight:700;
padding:20px;
text-align:center;
}

td.c_head,td.c_foot,td.c_sort {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
color:#ffffff;
font-size:80%;
}

td.c_desc {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
font-weight:700;
text-align:right;
vertical-align:middle;
width:200px;
}

td.c_desc small {
display:block;
font-weight:400;
}

tr.pm_new td {
font-weight:700;
}

.bday {
background:url(http://web.archive.org/web/20131231042849/http://z5.ifrm.com/static/1/s_minibday.png) no-repeat;
color:#425384;
font-size:90%;
font-weight:700;
height:16px;
padding:2px 2px 2px 18px;
width:20px;
}

.c_mark {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
background-color:#002874;
text-align:center;
width:1%;
}

.c_forum {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#bcbcbc;
font-size:80%;
width:60%;
}

.c_forum strong a,.c_last strong a {
color:#5ba9f1;
text-decoration: none;
}

.c_forum strong a {
color:#ffffff;
font-size:135%;
}

td.c_last {
background: url(http://web.archive.org/web/20131231042849/http://img20.imageshack.us/img20/9902/row3.gif) repeat-x top #003380;
background-color:#003380;
font-size:85%;
}

.c_forum-desc {
color:#333;
font-size:85%;
}

.c_last-title {
background:url(http://web.archive.org/web/20131231042849/http://img15.imageshack.us/img15/6779/mylast.gif) no-repeat left;
display:block;
line-height:16px;
padding-left:10px;
}

td.c_info-v,td.c_info-t,td.c_info-r {
font-size:80%;
padding:5px;
text-align:center;
}

td.c_subforum {
background: url(http://web.archive.org/web/20131231042849/http://img20.imageshack.us/img20/9902/row3.gif) repeat-x top #003380;
background-color:#003380;
color:#bcbcbc;
font-size:80%;
font-style:italic;
}

#stats_legend {
background: repeat-x top #0a2961;
}

.cat_head td {
border: none;
padding:0;
}

#stats .cat_head td {
border-color:#e77700;
}

.description {
color:#ffffff;
font-size:85%;
}

a.member:hover {
color:#bcbcbc;
}

td#stats_events a,td#stats_events span {
background:url(http://web.archive.org/web/20131231042849/http://z5.ifrm.com/static/1/s_minical.png) no-repeat 2px;
padding:4px 0 4px 20px;
}

td.c_username {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
padding:2px 0;
text-align:center;
}

td.c_username a {
font-size:140%;
font-weight:700;
}

td.c_user {
font-size:80%;
}

td.c_user dl {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
margin:10px 0;
padding:5px 7px;
width:160px;
}

dl.user_profile {
overflow:hidden;
word-wrap:break-word;
}

dl.user_profile dd {
font-style:italic;
margin-left:8px;
}

dl.user_info dd {
font-weight:700;
margin-bottom:1px;
}

#profile_menu ul {
-moz-opacity:1;
border-top:0;
opacity:1;
}

#profile_menu a {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
}

#profile_menu li li a {
border-top:0;
}

#profile_menu li ul li a:hover {
background: #0a2961;
}

td.c_poll-choice label {
font-weight:700;
padding-left:1em;
}

td.c_poll-votes {
font-size:120%;
text-align:center;
width:15%;
}

td.c_poll-bar div {
background:url(http://web.archive.org/web/20131231042849/http://z5.ifrm.com/static/1/poll_b.png) no-repeat right;
float:left;
height:12px;
padding-right:3px;
}

td.c_poll-bar span {
background:url(http://web.archive.org/web/20131231042849/http://z5.ifrm.com/static/1/poll_l.png) no-repeat;
display:block;
float:left;
height:12px;
width:3px;
}

.editby, #themecopyright, .queued {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
font-size:85%;
font-style:italic;
margin-top:1em;
padding:3px 5px;
}

blockquote {
background: #0a2961;
border:1px solid #0a0a0a;
margin:10px auto;
padding-bottom:1px;
width:98%;
}

blockquote dl {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
border-bottom:1px solid #0A0A0A;
color:#ffffff;
}

blockquote dl dt {
color:#ffffff;
float:left;
font-weight:700;
}

code {
font-family:"Helvetica", "Arial", "Bitstream Vera Sans", "Verdana", sans-serif;
background: #0a2961;
display:block;
max-height:400px;
overflow:auto;
white-space:pre;
}

div.spoiler {
background: #0a2961;
border:1px solid #0a0a0a;
border-top:0;
padding:5px 7px;
}

div.spoiler_toggle {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
border:1px solid #0a0a0a;
color:#ffffff;
cursor:pointer;
font-weight:700;
padding:2px 4px;
}

td.c_postinfo {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#ffffff;
font-size:80%;
vertical-align:middle;
}

td.c_postinfo input {
vertical-align:middle;
}

td.c_sig {
background: #0a2961;
color:#ffffff;
font-size:80%;
}

td.c_sig blockquote {
background:none transparent;
border:0;
border-left:3px solid #e7e7e7;
padding-left:5px;
}

td.c_sig blockquote dl {
background:none transparent;
border:0;
color:#ffffff;
}

td.c_sig blockquote dl dt,td.c_sig blockquote dl dd {
color:#ffffff;
padding:0;
}

td.c_sig blockquote div {
background:transparent;
margin-left:5px;
padding:0;
}

tr.c_postfoot td {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
clear:both;
}

td.c_username {
width:195px;
}

.c_next a {
display:block;
font-size:125%;
font-weight:700;
text-align:center;
}

td.c_view {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#ffffff;
}

div.c_view-list {
color:#ffffff;
font-size:92.5%;
}

dl#fast-reply dt {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
border:1px solid #0a0a0a;
color:#ffffff;
font-size:110%;
font-weight:700;
margin:0;
padding:5px;
}

dl#fast-reply dd {
background: #0a2961;
border:1px solid #0A0A0A;
border-top: none;
padding:15px 0;
text-align:center;
}

#c_post-preview {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0A0A0A;
margin:0 auto 10px;
padding:5px;
text-align:left;
width:98%;
}

#c_post-resize span {
color:#e7e7e7;
cursor:pointer;
font-size:225%;
padding:20px 5px 0;
}

#emot_wrap {
background: #0a2961;
border:1px solid #0A0A0A;
}

#emot_wrap h3 {
background: #0a2961;
border-bottom:1px solid #0A0A0A;
color:#ffffff;
font-size:120%;
text-align:center;
}

#emot_end {
background: #0a2961;
border-top:1px solid #0A0A0A;
display:block;
font-weight:700;
padding:4px 0;
text-align:center;
}

#pm_folders {
border-left:1px solid #0a0a0a;
border-right:1px solid #0a0a0a;
padding-top:30px;
vertical-align:top;
width:150px;
}

#pm_folderlist {
position: relative;
margin-left:0;
margin-top:0
}

#pm_folderlist li {
background:url(http://web.archive.org/web/20131231042849/http://z5.ifrm.com/static/1/pmfolder.png) no-repeat 5px;
display:list-item;
margin-bottom:4px;
padding-left:28px;
}

#pm_forwarded {
border:1px solid #0a0a0a;
color:#ffffff;
font-size:90%;
font-style:italic;
margin-top:1em;
padding:3px 5px;
}

.calendar td.cal_inactive {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
color:#ffffff!important;
}

.calendar td.cal_custom {
background:#296091;
color:#ffffff!important;
}

.calendar td div {
background: #0a2961;
border-bottom:1px solid #0A0A0A;
font-weight:700;
padding:5px 7px;
}

.calendar div a {
background:url(http://web.archive.org/web/20131231042849/http://z5.ifrm.com/static/1/s_minibday.png) no-repeat 5px;
float:right;
font-size:80%;
font-weight:400;
padding:0 0 6px 24px;
}

.calendar li a,.calendar li span {
border-color:transparent;
border-style:solid;
border-width:1px;
display:block;
padding:3px;
}

.calendar td#cal_current {
border:2px solid #e7e7e7;
}

div.portal_content {
background: url(http://web.archive.org/web/20131231042849/http://img390.imageshack.us/img390/9185/row2.gif) repeat-x top #002874;
border:1px solid #0a0a0a;
font-size:88.3%;
margin:0;
padding:5px 7px;
color:#fff;
}

#portal h2 {
font-size:88.3%;
}

#portal h4 {
background:url(http://web.archive.org/web/20131231042849/http://img266.imageshack.us/img266/6393/myrow.gif) repeat-x top #072151;
color:#ffffff;
border-left:1px solid #0A0A0A;
border-right:1px solid #0A0A0A;
font-size:88.3%;
font-weight:400;
margin:0;
padding:5px 7px;
}

