css:
@-moz-document url-prefix("http://jsfiddle.net"), url-prefix("https://jsfiddle.net") {
	#header{
		background:#434343 !important;
	}
	#header, #header > div{
		height:25px !important;
	}
	#branding{
		padding-right:5px !important;
	}
	#home{
		background-position:0 0 !important;
		transform: scale(0.85, 0.85);
		margin-left:-6px;
		margin-top:-1px;
		width:85px !important;
	}
	#progressbar{
		display:none !important;
	}
	.aiButton, #home{
		color:#aff !important;
		line-height:25px !important;
		height:24px !important;
	}
	.aiButton:hover{
		background-color:rgba(170, 170, 255, 0.5) !important;
	}
	.actionCont:first-child .actionItem a{
		display:block !important;
	}
	.actionCont:first-child .actionItem a:before{
		content:'â‰¡';
		font-family:'Elusive-Icons';
		speak:none;
		font-style:normal;
		font-weight:normal;
		font-variant:normal;
		text-transform:none;
		line-height:1;
		-webkit-font-smoothing:antialiased;
	}
	#actions #toggleSidebarUl{
		position:absolute;
		margin-left:100px !important;
		left:0 !important;
	}
	#actions #toggleSidebarUl:after{
		content:'';
		position:absolute;
		left:116px;
		border-right:2px ridge #6d6d6d;
		height:100%;
	}
	#sidebar{
		background-color:#171714 !important;
		border-right:1px solid #000;
		box-shadow:inset -1px 0 0 hsla(0, 0, 100%, .25);
		margin-top:-1px;
		padding-bottom:10px;
		height:calc(100vh - 25px) !important;
		top:27px !important;
	}
	#sidebar .toggler{
		color:#f0f0f0 !important;
	}
	#sidebar .toggler.active{
		cursor:default;
	}
	#sidebar .toggler:hover{
		background-color:hsla(0, 0, 100%, .5);
	}
	#sidebar .toggler.active:hover{
		background-color:hsla(0, 0, 100%, 0);
	}
	#sidebar .element{
		border-bottom:2px ridge #333 !important;
	}
	#sidebar select{
		background:#222 !important;
		color:white !important;
		border-color:black !important;
	}
	#sidebar label{
		color:white !important;
	}
	.sidebarWrap #sidebar{
		border:0 !important;
		margin:0 !important;
		padding:0 !important;
		box-shadow:none !important;
	}
	.sidebarWrap, .sidebarWrap #sidebar, .sidebarWrap .commonNav{
		background-color:#171714 !important;
	}
	.fiddleList a, .fiddleList h3, .commonNav a, .paginator *, .info, .pageHeader *, .commonForm h3, .commonForm span, .commonForm label, pre{
		color:#FFF !important;
	}
	.fiddleList pre{
		background-color:#272822 !important;
	}
	#content .window, .CodeMirror, #content iframe{
		border:0 !important;
		margin:1px;
		background-color:#272822 !important;
		box-shadow:inset 1px 1px 12px #000 !important;
	}
	.window_label{
		background:rgba(0, 0, 0, 0.5) !important;
		border:none !important;
		color:#f0f0f0;
	}
	body, #content, .handler, .commonNav{
		background-color:#5a5a5a !important;
	}
	::-webkit-scrollbar{
		width:12px;
	}
	::-webkit-scrollbar-track{
		-webkit-box-shadow:inset 0 0 6px rgba(0, 0, 0, 0.5);
		-webkit-border-radius:10px;
		border-radius:10px;
	}
	::-webkit-scrollbar-thumb{
		-webkit-border-radius:10px;
		border-radius:10px;
		background-color:#4a4a4a;
		-webkit-box-shadow:inset 0 0 6px rgba(0, 0, 0, 1);
	}
	#handler_vertical{ background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAPCAYAAADDNm69AAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsSAAALEgHS3X78AAAAB3RJTUUH3gEYAQksrvU8ogAAABlJREFUCNdjYGBg+P///38GGM3EgAaGtQAAs64IFdoN4N4AAAAASUVORK5CYII=) 3.5px 50% no-repeat !important;
	}
	.handler_horizontal{
		background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAECAYAAABREWWJAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsSAAALEgHS3X78AAAAB3RJTUUH3gEYAQkGdU71dAAAABpJREFUCNdjZGBg+M9AJmD8/59svQyMA2YzADVcB/3wK8PzAAAAAElFTkSuQmCC) 50% 4px no-repeat !important;
	}
	.cm-s-default .cm-comment{
		color:#d27b53 !important;
	}
	.cm-s-default .cm-atom{
		color:#a16a94 !important;
	}
	.cm-s-default .cm-number{
		color:#a16a94 !important;
	}
	.cm-s-default .cm-property, .cm-s-default .cm-attribute{
		color:#9c9 !important;
	}
	.cm-s-default .cm-keyword{
		color:#f2777a !important;
	}
	.cm-s-default .cm-string{
		color:#fc6 !important;
	}
	.cm-s-default .cm-variable{
		color:#9c9 !important;
	}
	.cm-s-default .cm-variable-2{
		color:#69c !important;
	}
	.cm-s-default .cm-def{
		color:#f99157 !important;
	}
	.cm-s-default .cm-bracket{
		color:#ccc !important;
	}
	.cm-s-default .cm-tag{
		color:#f2777a !important;
	}
	.cm-s-default .cm-link{
		color:#a16a94 !important;
	}
	.cm-s-default .cm-error{
		background:#f2777a;
		color:#6a6a6a !important;
	}
	.cm-s-default .cm-string-2{
		color:#f96 !important;
	}
	.cm-s-default .cm-qualifier, .cm-s-default .cm-operator, .cm-s-default .cm-builtin{
		color:#FFF !important;
	}
	.cm-s-default .cm-meta{
		color:#c5c5c5 !important;
	}
	.cm-s-default .CodeMirror-gutters{
		background:rgba(0, 0, 0, 0.2);
		border-right:0;
		padding-right:4px;
	}
	.cm-s-default .CodeMirror-cursor{
		border-left:1px solid #6a6a6a !important;
	}
	.cm-s-default .CodeMirror-lines div:first-child div:nth-child(3) div:nth-child(odd) pre{
		background:rgba(0, 0, 0, 0.2) !important;
	}
	.cm-s-default div.CodeMirror-selected{
		background:#000 !important;
	}
	div.CodeMirror span.CodeMirror-matchingbracket{
		text-decoration:underline;
		color:white !important;
	}

}
