css:
@-moz-document domain("starrepublik.com") {

body {
    margin: 0;
    min-height: 100%;
    
    background-attachment: fixed;
    background-color: #010103;
    /* #040404 #020002 #101010*/
    background-image:none;
    background-size: cover;
}
a {
    color: #AED2D9;
    outline: none;
    text-decoration: none;
}
a:hover {
    text-shadow: 0 0 3px #A1C3C9;
}
.select2-container a:hover {
    text-shadow: 0 0 0;
}
ul {
    list-style-type: none;
    padding: 0;
    margin: 0;
}
textarea[placeholder] {
    text-overflow: ellipsis;
}
textarea::-moz-placeholder {
    text-overflow: ellipsis;
}
textarea:-moz-placeholder {
    text-overflow: ellipsis;
}
textarea:-ms-input-placeholder {
    text-overflow: ellipsis;
}
:focus::-webkit-input-placeholder {
    color: transparent
}
:focus::-moz-placeholder {
    color: transparent
}
:focus:-moz-placeholder {
    color: transparent
}
:focus:-ms-input-placeholder {
    color: transparent
}
.content-wrapper {
    min-width: 900px;
    max-width: 900px;
    min-height: 750px;
    width: 50%;
    margin-left: auto;
    margin-right: auto;
    font-family: ubuntu;
    color: white;
    font-size: 14px;
    background-color: black;
    /* #040404 #020002 #101010*/
}
form label {
    margin-top: 5px;
    font-size: 16px;
    display: block;
    width: 98%;
    padding: 1%;
}
form input {
    margin-top: 5px;
    margin-bottom: 10px;
    width: 96%;
    border: 1px solid transparent;
    border-radius: 3px;
    padding: 2%;
    background-color: #fff;
}
form input:focus {
    border-color: #66afe9;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
}
form button {
    margin-top: 10px;
    padding: 5px;
    width: 100%;
}
form .currency-flag {
    float: none;
    filter: alpha(opacity=0.8);
}
form .requirements {
    margin-top: 5px;
}
form .requirements span.value {
    display: inline-block;
    width: 15%;
}
.errorlist {
    text-align: center;
    margin-bottom: 5px;
    color: RGBA(199, 106, 101, 1);
}
#header {
    height: 35px;
    background-color: RGBA(32, 32, 32, 1);
    opacity: 1;
}
.logo {
    float: left;
    margin-top: 7px;
    height: 20px;
    margin-left: 42px;
}
.logo:active {
    position: relative;
    top: 1px;
}
.logo a {
    font-family: grunge;
    font-size: 38px;
    padding: 0;
    height: 35px;
    line-height: 32px;
    color: #c6e6f6;
    text-shadow: 0 0 5px #A9C5E8;
    text-indent: 15px;
}
.nav {
    margin: 0;
    margin-left: 200px;
    padding: 0px;
    list-style: none;
    text-align: right;
    zoom: 1;
}
.nav li {
    margin: 0;
    width: 120px;
    height: 15px;
    padding: 10px;
    position: relative;
    display: inline-block;
    text-align: center;
    text-transform: uppercase;
    color: #c6e6f6;
    text-shadow: 0 0 5px #1D7EA9;
    z-index: 1000;
}
ul.nav li:hover {
    background-color: #0f1f36;
    color: white;
}

ul.nav ul {
    padding: 0;
    margin: 0;
    
    /*IE6 only*/
    
    opacity: 0;
    visibility: hidden;
    position: absolute;
    top: 30px;
    left: 0px;
    z-index: 1;
}
ul.nav li:hover > ul {
    opacity: 1;
    visibility: visible;
    margin: 0;
}
ul.nav ul li {
    float: none;
    margin: 0;
    height: 25px;
    width: 140px;
    padding: 0;
    display: block;
    border: 0;
    border-bottom: 1px solid #2e343e;
    border-radius: 0;
}
ul.nav ul li:first-child {
    margin-top: 5px;
    float: none;
}
.nav ul li:last-child {
    border: none;
}
ul.nav ul a {
    white-space: nowrap;
    margin: 0;
    width: 130px;
    height: 15px;
    padding: 5px;
    display: block;
    float: none;
    border-radius: 1px;
    background-color: #0e151f;
    text-align: center;
    text-transform: none;
    color: #c6e6f6;
    font: 13px ubuntu;
}
ul.nav ul a:hover {
    color: white;
    background-color: #0f1f36;
    transition: all .2s ease-in-out;
}
.registration {
    margin-top: 10px;
    margin-bottom: 30px;
}
#registration-form {
    width: 302px;
    margin-left: auto;
    margin-right: auto;
}
.g-recaptcha {
    margin-top: 10px;
}
#registration-form button {
    font-size: 16px;
}
.sidebar {
    margin-left: 10px;
    float: left;
    width: 200px;
}
#change-lang-form {
    margin-top: 10px;
}
.login-form {
    margin-top: 10px;
}
.login-form input {
    margin-top: 2px;
    padding: 5px;
}
.login-form button {
    margin-top: 10px;
    padding: 5px;
}
.registration {
    text-align: center;
}
.registration button {
    margin-top: 5px;
    width: 100%;
    padding: 5px;
    font-size: 14px;
}
#login-form {
    margin-top: 30px;
    margin-left: 15%;
    width: 40%;
}
.politics-description,
.economy-description,
.military-description {
    height: 230px;
    margin-top: 5px;
    border-radius: 4px;
}
.game-description img {
    margin-top: 10px;
    width: 49%;
    margin-right: 1%;
    height: 180px;
    float: right;
    border-radius: 5px;
}
.game-description .description {
    width: 46%;
    padding: 2%;
    float: right;
    height: 180px;
}
.economy-description img {
    margin-right: 0;
    margin-left: 1%;
    float: left;
}
.economy-description .description {
    float: right;
}
.politics-description {
    color: #dcc9f7;
    background-color: #201a2a;
    text-shadow: 0 0 5px #6127bb;
    border: 1px solid #6127bb;
    box-shadow: 0 0 5px #6127bb;
}
.military-description {
    color: #fecad1;
    background-color: #340003;
    text-shadow: 0 0 5px #92000b;
    border: 1px solid #92000b;
    box-shadow: 0 0 5px #92000b;
}
.economy-description {
    color: #cad6f7;
    background-color: #11192b;
    text-shadow: 0 0 5px #244cba;
    border: 1px solid #244cba;
    box-shadow: 0 0 5px #244cba;
}
.citizens-info {
    margin-top: 10px;
    max-height: 50px;
    padding: 15px;
    text-align: center;
    font-size: 14px;
    font-family: research;
    text-shadow: 0 0 5px #1D7EA9;
}
.citizens-info .new-citizens-today {
    width: 50%;
    float: left;
}
.top-list {
    margin-top: 5px;
    margin-bottom: 20px;
    margin-left: 3%;
    width: 46%;
    background-color: #080a0f;
    box-shadow: 0 0 10px #1D7EA9;
    color: #cecece;
    float: left;
}
.top-list li {
    padding: 8px;
    height: 20px;
    line-height: 20px;
    text-indent: 10px;
    border-bottom: 1px solid #334a6a;
}
.top-list li img {
    width: 30px;
    height: 20px;
    display: block;
    float: left;
}
#top-citizens li img {
    width: 25px;
    height: 25px;
}
.top-list li .name {
    width: 52%;
    float: left;
    height: 20px;
    line-height: 20px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.top-list li .country-citizens {
    margin-top: 0;
    float: right;
}
.search-list li {
    margin-left: 5%;
    width: 90%;
    height: 50px;
    line-height: 50px;
    padding: 10px;
    border-bottom: 1px solid #334a6a;
}
.search-list li .name {
    width: 50%;
    float: left;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.search-list li .name img {
    margin-right: 10px;
    width: 50px;
    height: 50px;
    border-radius: 1px;
    float: left;
}
.search-list li .level {
    width: 25%;
    float: left;
    text-align: center;
    color: #dedede;
}
.search-list li .country {
    text-align: center;
}
.search-list li .country img {
    margin-top: 7px;
    width: 50px;
    height: 36px;
}
.points {
    border-radius: 0;
}
.main-content {
    margin-left: 25%;
    margin-top: 10px;
    margin-right: 10px;
    min-height: 800px;
}
.main-page-content {
    width: 54%;
    min-height: 700px;
    float: left;
}
.section-header {
    padding: 6px;
    text-align: center;
    font-size: 14px;
    color: #7B56F3;
    font-family: research;
}
.newspaper-header {
    background-color: rgba(0, 5, 50, 0.1);
    background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.04), rgba(255, 255, 255, 0));
    background-image: -moz-linear-gradient(top, rgba(255, 255, 255, 0.04), rgba(255, 255, 255, 0));
    background-image: -o-linear-gradient(top, rgba(255, 255, 255, 0.04), rgba(255, 255, 255, 0));
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.04), rgba(255, 255, 255, 0));
    -webkit-box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.04), inset 0 1px rgba(255, 255, 255, 0.04), inset 0 -1px rgba(0, 0, 0, 0.15), 0 1px 1px rgba(0, 0, 0, 0.1);
    box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.04), inset 0 1px rgba(255, 255, 255, 0.04), inset 0 -1px rgba(0, 0, 0, 0.15), 0 1px 1px rgba(0, 0, 0, 0.1);
    -webkit-transition: 0.1s ease-out;
    -moz-transition: 0.1s ease-out;
    -o-transition: 0.1s ease-out;
    transition: 0.1s ease-out;
    color: #c6e6f6;
    text-shadow: 0 0 5px #A9C5E8;
}
.social {
    color: #D8CDFF;
    background-color: #423F37;
    text-shadow: 0 0 5px RGB(32, 136, 181);
    border: 1px solid RGB(32, 136, 181);
}
.military {
    color: #fecad1;
    background-color: #340003;
    text-shadow: 0 0 5px RGB(32, 136, 181);
    border: 1px solid RGB(32, 136, 181);
}
.economy {
    color: #cad6f7;
    background-color: #11192b;
    text-shadow: 0 0 5px #244cba;
    border: 1px solid #244cba;
}
.politics {
    color: #dcc9f7;
    background-color: #201a2a;
    text-shadow: 0 0 5px #6127bb;
    border: 1px solid #6127bb;
}
.section-message {
    padding: 10px;
}
.section-message .nav-btn {
    width: 25%;
    margin-left: 75%;
}
.small {
    font-size: 14px;
    padding: 5px;
}
.beginners-guides-list {
    padding: 5px;
}
.beginners-guides-list li {
    padding: 5px;
    border-bottom: 1px solid #334a6a;
}
.beginners-guides-list li .description {
    margin-top: 5px;
}
.daily-orders {
    margin-bottom: 5px;
}
.order {
    margin-left: -5%;
    margin-top: 5px;
    line-height: 40px;
    padding: 10px;
    font-size: 14px;
    text-align: center;
}
.order button {
    margin-left: 2%;
    padding: 5px;
    width: 35%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
    font-size: 14px;
}
.elections {
    min-height: 90px;
    margin-bottom: 10px;
}
.elections .description {
    margin-left: 1%;
    margin-top: 10px;
    line-height: 25px;
    width: 60%;
    font-size: 14px;
    color: #dedede;
}
.elections .description img {
    width: 50px;
    height: 50px;
    float: left;
    margin-right: 7px;
}
.elections .vote-link {
    width: 30%;
    margin-right: 5%;
    float: right;
}
.elections .market-btn {
    width: 100%;
    margin-top: 20px;
    padding: 4px;
    text-align: center;
}
.rebels-list {
    margin: 10px;
    padding: 0;
}
.rebels-list li {
    display: inline-block;
    margin-left: 2px;
}
.rebels-list li img {
    width: 40px;
    height: 40px;
    border: 1px solid #202020;
    border-radius: 3px;
}
.rebels-list li .name {
    margin-top: -20px;
    color: white;
    font-size: 14px;
    padding: 1px 3px 1px 3px;
    border-radius: 2px;
    background-color: #202020;
    position: absolute;
    opacity: 0.8;
    display: none;
}
.r-sup {
    margin: 5%;
    padding: 5px;
    width: 40%;
    margin-left: 30%;
}
.support-rebellion {
    padding: 10px;
    text-align: center;
}
.ongoing-battles {
    margin-bottom: 10px;
}
.ongoing-battles .small {
    margin-top: 10px;
}
.ongoing-battles li {
    width: 100%;
    height: 45px;
    border-bottom: 1px solid #334a6a;
    line-height: 25px;
}
.ongoing-battles li .info {
    margin-left: 5%;
    margin-top: 10px;
    float: left;
    width: 70%;
}
.ongoing-battles li .sector {
    float: left;
    width: 50%;
    text-align: center;
}
.ongoing-battles img {
    width: 35px;
    height: 25px;
    border-radius: 1px;
    float: left;
}
.ongoing-battles button {
    width: 22%;
    padding: 4px;
    margin-top: 10px;
    font-size: 14px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.news-selector {
    padding: 15px;
    border-bottom: 1px solid #334a6a;
}
.news-selector .select2-container {
    margin-left: 30px;
}
.news-selector .select2-container:first-child {
    margin-left: -10px;
}
.news-list {
    margin-top: 20px;
    background-color: RGBA(33, 32, 33, 1);
}
.news-nav {
    height: 30px;
    text-align: center;
}
.get-news {
    width: 48%;
    padding: 1%;
    background: #080a0f;
    cursor: pointer;
    border-radius: 1px;
    color: #c6e6f6;
    float: left;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
    font-size: 14px;
}
.active-news {
    cursor: default;
    background-color: #191f29;
    text-shadow: 0 0 5px #A9C5E8;
    border-bottom: 1px solid #A9C5E8;
}
.news-list li {
    margin-top: 10px;
    padding-bottom: 5px;
    width: 100%;
    min-height: 30px;
    border-bottom: 1px solid #334a6a;
}
.news-list li.no-news {
    height: 25px;
    line-height: 25px;
    padding: 0;
    text-align: center;
}
.news-list .votes {
    margin-left: 5px;
    float: left;
    width: 50px;
    height: 30px;
    line-height: 30px;
    text-align: center;
    font-size: 20px;
    border-radius: 5px;
    background-color: #262f42;
}
.news-list .article-cnt {
    margin-right: 5px;
    margin-left: -10px;
}
.news-list .article-title {
    font-size: 16px;
}
.news-list .article-title a {
    color: #cecece;
    text-shadow: 0 0 5px #A9C5E8;
}
.news-list .article-info {
    margin-top: 4px;
    font-size: 14px;
}
.news-list .article-newspaper {
    float: left;
    max-width: 50%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.news-list .publication-date {
    margin-left: 10px;
}
.more-news {
    margin-left: 25%;
    margin-top: 5px;
    width: 50%;
}
.shouts-list {
    margin-left: 57%;
    margin-bottom: 100px;
    border: 1px solid #101924;
    background-color: black;
}
.shouts-selector {
    text-align: center;
    height: 30px;
}
.shouts-selector .selector {
    width: 14%;
    padding: 1%;
    float: left;
    background: #080a0f;
    color: #5483b0;
    border-radius: 1px;
    cursor: pointer;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.shouts-selector #friends-shouts #market-shouts,
.shouts-selector #galaxy-shouts {
    width: 16%;
    -webkit-filter: grayscale(0.5);
}
.shouts-selector .selector .selector-image {
    height: 20px;
    width: 20px;
    -webkit-filter: opacity(0.8);
}
.shouts-selector .selector .tiny-country-flag {
    height: 20px;
    width: 30px;
    -webkit-filter: opacity(0.8);
}
.shouts-selector .active {
    background-color: black;
    text-shadow: 0 0 5px #A9C5E8;
    border-bottom: 1px solid #A9C5E8;
}
.shouts-list li {
    min-height: 90px;
    margin-top: 5px;
    border-bottom: 1px solid #101924;
}
.shouts-list li#new-shout {
    height: 105px;
    
}
.shouts-list li#new-shout .name {
    min-width: 95%;
    margin-bottom: 5px;
    text-align: center;
}
.shouts-list li#new-shout img {
    margin-top: 2px;
}
.shouts-list li#new-shout textarea {
    min-height: 40px;
    margin-top: 2px;
    margin-right: 5px;
    padding: 3%;
    width: 71%;
    resize: none;
    color: black;
    border-radius: 3px;
    border: none;
    float: right;
}
.shouts-list li#new-shout textarea:focus {
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
}
.shouts-list li#new-shout button {
    margin-top: 5px;
    margin-right: 5px;
    width: 77%;
    padding: 4px;
    float: right;
}
.shouts-list li .name {
    margin-top: 3px;
    margin-left: 5px;
    max-width: 55%;
    float: left;
    font-size: 11px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.shouts-list li span.shout-time {
    margin-left: 10px;
    font-size: 10px;
    color: #aeaeae;
}
span.report a,
span.delete-comment a,
span.delete-shout a {
    margin-right: 5px;
    margin-top: 3px;
    margin-bottom: 3px;
    color: #232323;
    font-size: 10px;
    text-align: right;
    display: block;
}
span.report a:hover,
span.delete-comment a:hover,
span.delete-shout a:hover {
    color: #cecece;
    text-shadow: 0 0 0;
    cursor: pointer;
}
.comments-list span.report,
.comments-list span.delete-comment {
    margin-left: 80%;
    display: block;
    text-align: right;
    margin-top: -15px;
}
.shouts-list span.report {
    width: 35%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.shouts-list li .shoutlogo {
    margin-left: 10px;
    width: 40px;
    height: 40px;
    border-radius: 3px;
    float: left;
}
.shouts-list li .shout-content {
    margin-top: 5px;
}
.shouts-list li .text {
    margin-left: 60px;
    min-height: 40px;
    width: 73%;
    padding: 2%;
    background-color: #070d15;
    color: #fefefe;
    border-radius: 4px;
    font-size: 14px;
    border: 1px solid #101924;
}
.shouts-list li .text a {
    color: #A9C5E8;
}
.shouts-list li .shout-footer {
    width: 40%;
    float: left;
    font-size: 13px;
    cursor: pointer;
    padding: 3px;
    padding-left: 10px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
    color: RGBA(178, 211, 216, 1);
}
.shouts-list li .shout-footer img {
    display: inline-block;
    width: 10px;
    height: 10px;
    margin-left: 1px;
    -webkit-filter: grayscale(0.5);
}
.shouts-list li .shout-footer .shout-comments:hover,
.shouts-list li .shout-footer .shout-votes:hover,
.shouts-list li .shout-footer .like-shout:hover,
.shouts-list li .shout-footer .dislike-shout:hover {
    text-shadow: 0 0 5px RGBA(178, 211, 216, 1);
}
.shouts-list li .like-shout,
.shouts-list li .dislike-shout {
    margin-left: 3px;
}
.shouts-list li .shout-info {
    display: none;
    padding-left: 20%;
}
.shout-voters {
    font-size: 11px;
    margin-top: 5px;
    padding-left: 2px;
}
.shout-comments-list {
    border-top: 1px solid #101924;
    margin-top: 5px;
}
.shout-comments-list li {
    padding: 3px;
    margin-top: -3px;
    margin-bottom: 3px;
    min-height: 20px;
}
.shout-comments-list li .shoutlogo {
    width: 25px;
    height: 25px;
    float: left;
}
.shout-comments-list li .text {
    margin-left: 40px;
    font-size: 11px;
}
.comment-shout-form textarea {
    border-radius: 2px;
    min-height: 25px;
    margin-top: 5px;
    margin-right: 5px;
    padding: 3%;
    width: 71%;
    margin-left: 21%;
    border: none;
    resize: none;
}
