css:
@-moz-document domain("aws.amazon.com"), domain("amazonaws-us-gov.com") {
/* Base style */
html,
body,
#content,
.CLB,
.DMB,
#bucket-list-container,
#properties-panel,
.GHGJSONCEBF,
#main,
#left-column,
li.awstoc,
ul.awstoc,
.awsui,
tr[__gwt_row],
body.user-type-iam .content-pane table, /* IAM */
body.user-type-iam div#c, /* IAM */
body.user-type-iam .full_wizard, /* IAM wizard */
#bucket-list-view, #object-list-container /* S3 object lists */
{
    background-color: #333;
    color: #ddd
}
/* 
  Overrides for inline styles
*/
div.rds_campaign_cell {
    background-color: #333 !important;
    color: #ddd !important;
}
/* Other */
.OB, /* highlighted AMI in launch wizard */
.IS,  /* left nav on launch wizard */
tr[__gwt_row] .H-C, /* selected instance type in launch wizard */
table[data-table] tbody tr:hover /* hover over IAM table */
{ background-color: #555 } 

#main p,
#main li {
    color: #ddd
}
a.awstoc.selected,
#pagetoc a.pagetoc.selected,
#breadcrumbs span.breadcrumb {
    color: #eee
}
.HMB,.GNG0JBXDG- tbody tr:nth-child(even) {
    background-color: #555
}
.dialogContent,
.LBD,
.GHGJSONCJ1,
.AKB,
#c.awsui .gwt-InlineLabel, /* Cloudwatch metrics graph headers, ec2 instance monitoring tab */
div.JLH /* metrics headers */,
tr[__gwt_row].GKB /* highlighted row, ec2 */
{
    color: #555
}
/* Dark text for lighter stuff (dialogs, table headers, etc */

.JLB,
.gwt-TabLayoutPanelTabs,
.J5B,
.BHB,
.GXH,
.awsui .IKB, /* ec2 instance table highlighted row */
.GHGJSONCH0
tr[__gwt_header_row],
tr[__gwt_header_row] .IJB, /* ec2 instance table headers */
table[data-table="resource"] th /* used on IAM */
{
    color: #333
}
#content div.serviceName,
.awsui .JQ {
    color: #aaa
}
#content .services h2 {
    color: #999
}
#properties-panel-header-item-label,
#properties-panel-header-item-name,
#properties-panel-sections>h2,
.GH2OXPECGOE, /* labels on CloudFront distribution UI */
.GH2OXPECMOE  /* more labels on CloudFront distribution UI */
{
    color: #aaa
}
/* sidebar menu items */

.DS,
.GHGJSONCEBF ul li a,
body.user-type-iam .wx-sidenav a /* left sidenav on IAM */{
    color: #777
}
/* docs styles. Original title header #e47911 */

div.sphinxsidebar p,
div.sphinxsidebar h3,
div.sphinxsidebar h3 a,
div.sphinxsidebar h4,
div.informaltable-contents,
#service-name,
#language-filter {
    color: #aaa
}
code,
pre,
#pagetoc,
#pagetoc p {
    background-color: #555;
    color: #eee
}
#pagetoc a {
    color: #0000A0;
}
tt {
    color: rgba(255, 255, 0, 0.7);
}
.navbar-inner {
    background: #000
}
.navbar .brand,
.navbar .nav>li>a {
    text-shadow: 0 1px 0 #000;
}
/* Marketing page tweaks */

.related-link a {
    color: hsla(210, 50%, 54%, 1);
    /* expressed as HSLA because I just bumped luminosity from original */
}
#aws-page-content h3,
#aws-page-content a {
    color: hsla(210, 50%, 54%, 1);
}

/* Java SDK docs */
ul.blockList ul.blockList ul.blockList li.blockList,
ul.blockList ul.blockList ul.blockListLast li.blockList, tr.rowColor {background-color:#333}
ul.blockList ul.blockList ul.blockList li.blockList,
ul.blockList ul.blockList ul.blockListLast li.blockList,
tr.altColor {background-color:#555}

ul.blockList h3, ul.blockList h4 {color: #333}

.contentContainer .description ul.blockList  dl dt,
.contentContainer .details ul.blockList  dl dt,
.serializedFormContainer ul.blockList dl dt
{color: #aaa}

ul.blockList a:link,  table.overviewSummary a:visited {color: rgba(255, 255, 0, 0.7);}
/* End Java SDK docs */

#f {
  background-color: #333 !important;
}
}
