@-moz-document domain("anilinkz.to") {

body {
    background:url(/*[[background]]*/) repeat;
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}
#wrapper {
    background-color: rgba(0, 0, 0, 0.75);
    box-shadow: 0px 0px 30px 10px rgba(255, 255, 255, 0);
    width: 1036px;
    margin: 0 auto;
    padding: 0 8px;
    position: relative;
}
#sources {
    background: none !important;
}
#wrapper > nav {
    background: rgba(0, 0, 0, 0.3) !important;
}
#wrapper > nav {
    border-style: double;
    border-width: 4px;
    border-color: #282828;
}
/*Nav hover color*/

nav a:hover {
    text-decoration: none;
    color: #86D2F9;
    text-shadow: none;
}
#player > div {
    background: black;
    border-style: solid;
    border-width: 1px;
    border-color: #202020;
}
#playheader {
    background: rgba(0, 0, 0, 0.4);
    border-style: solid;
    border-width: 1px;
    border-color: #202020;
}
#playheader > span.title {
    background: none!important
}
#playheader > span.next {
    background: url(http://i.imgur.com/f4LziUB.png);
    background-repeat: no-repeat;
    display: inline;
    padding: 1px;
    text-indent: -9999px;
}
#playheader > span.prev {
    background: url(http://i.imgur.com/hgd3vE7.png);
    background-repeat: no-repeat;
    display: block;
    padding: 1px;
    text-indent: -9999px;
}
#searchform > input.query {
    background: rgba(10, 40, 64, 0.75) !important;
    border-radius: 8px;
    border-style: double;
    border-color: #254D6F;
    border-width: 1px;
}
/*Homepage title colors*/

.epl2 li a.ser {
    border-style: double;
    border-color: #303030;
}
/* Trend list color*/

#trendinglist > li {
    background: rgba(0, 0, 0, 0.3);
    border-style: double;
    border-width: 1px;
    border-color: #282828;
}
#trendinglist > li:nth-child(n+1) > a.ser {
    background: #181818 !important
}
#trendinglist > li:nth-child(n+1) > a.ep > span.title {
    background: none !important
}
/* Latest list color*/

#latestlist > li {
    background: rgba(0, 0, 0, 0.3);
    border-style: double;
    border-width: 1px;
    border-color: #282828;
}
#latestlist > li:nth-child(n+1) > a.ser {
    background: #181818 !important
}
#latestlist > li:nth-child(n+1) > a.ep > span.title {
    background: none !important
}
/* Preview series and episode list color*/

#nextprevlist > li:nth-child(n+1) {
    background: rgba(0, 0, 0, 0.3);
    border-style: solid;
    border-color: #282828;
}
/*Minor cosmetic removing sub and dub tags on nextprevlist because its distracting - if you like the tags then get rid of this code below*/

#nextprevlist li a span,
#nextprevlist li.on span span {
    display: none!important;
}
/*Anime episode list main color*/

#serieslist > li:nth-child(n+1) {
    background: rgba(0, 0, 0, 0.3) !important;
    border-style: double;
    border-color: #606060;
}
/*Anime episode list inside main color*/

#serieslist > li:nth-child(n+1) > span.epser > span {
    background: #181818 !important;
    border-style: double;
    border-color: #282828;
}
/*Anime list inner most layer color*/

#serieslist > li:nth-child(n+1) > span.epser {
    background: black !important;
    border-style: double;
    border-color: #282828;
}
/*Ongoing series outer main color area*/

#ongoingserieslist > li:nth-child(n+1) {
    background: rgba(0, 0, 0, 0.3)!important;
    border-style: double;
    border-color: #606060;
}
/*Ongoing series inner color area*/

#ongoingserieslist > li:nth-child(n+1) > span.epser {
    background: black !important;
    border-color: #282828;
}
/*Ongoing series second inner color area*/

#ongoingserieslist > li:nth-child(n+1) > span.epser > span > a {
    background: black !important;
    border-color: #282828;
}
/*Some other inside area for ongoing series shit*/

.epl li span.epser span {
    background: black !important;
    border-style: double;
    border-color: #282828;
}
/*Episode summary information box color area*/

div.loontip {
    background: rgba(0, 0, 0, 0.9)!important;
    border-style: double;
    border-color: #606060;
}
/*Source button color*/

#sources > a:nth-child(n+1) {
    background: rgba(0, 0, 0, 0.3);
    border-color: #282828;
    border-style: double;
    width: 77.5px;
}
/*Color for titles to remove orange border */

.srl li a span.title {
    border: 1px solid rgb(0, 0, 0);
    background-color: rgba(0, 0, 0, 0.21);
    display: block;
    overflow: hidden;
}
/*Color for list area */

.column p.sep {
    font-weight: bold;
    border: 1px solid #1E2829;
    text-shadow: 0 0 10px #69C9E6;
    font-size: 20px;
    height: 28px;
    overflow: hidden;
    padding-left: 15px;
    margin: 10px;
    width: 308px;
    border-radius: 10px;
}
.sep {
    background: rgba(62, 61, 61, 0.43) !important;
}
/*Colour for the two H2 titles in the homepage and the main H2 title*/

#wrapper > main h2 {
    color: #FFFFFF;
    text-shadow: 0 0 2.5px #69C9E6;
}
.relative > h2:nth-child(1) {
    color: #FFFFFF;
    text-shadow: 0 0 2.5px #69C9E6;
}
/*Genre check color area*/

.icheckbox_line-blue,
.iradio_line-blue {
    position: relative;
    display: block;
    color: #fff;
    background-color: rgba(103, 103, 103, 0.26);
    border: 1px solid darkslategray;
    cursor: pointer;
}
.icheckbox_line-blue.hover,
.icheckbox_line-blue.checked.hover,
.iradio_line-blue.hover {
    background-color: #004E4E;
}
.icheckbox_line-blue.checked,
.iradio_line-blue.checked {
    background-color: #029696;
}
/*Page number links and color*/

#pagenavi > a.page-link.prev {
    background: black !important;
    border-color: #808080;
    border-style: double;
}
#pagenavi > span.current.prev {
    background: black !important;
    border-color: #808080;
    border-style: double;
}
#pagenavi > span:nth-child(n+1) {
    background: black !important;
    border-color: #808080;
    border-style: double;
    border-width: 1px;
}
#pagenavi > a.page-link.next {
    background: black !important;
    border-color: #808080;
    border-style: double;
}
.light-theme a,
.light-theme span {
    border: 1px solid;
    border-color: #606060;
    padding: 0 9px;
    background: rgba(0, 0, 0, 0.3);
}
.light-theme .current {
    background: rgba(0, 0, 0, 0.3);
    color: lightblue;
    cursor: default;
}
/*Search list results colors main external*/

.epl li {
    background: rgba(0, 0, 0, 0.3) !important;
    border-style: double;
    border-color: #606060;
}
/*Search list results colors first inside part*/

.epl li span.epser {
    background: #181818 !important;
    border-style: double;
    border-color: #282828;
}
/*Search list results colors second inside part*/

.epl li span.epser span {
    background: black !important;
    border-color: #282828;
}
/*Newly added, recommendations and anime of the season lists main color areas*/

.srl li {
    background: rgba(0, 0, 0, 0.3)!important;
    border-color: #505050;
}
.srl li a span.title span {
    background: black;
}
/*Images border color*/

.srl li a span.img {
    border-color: #505050;
}
.epl li span.img {
    border-color: #A8A8A8;
}
/*View count and report colors*/

#report {
    color: #404040;
}
.pviews {
    color: #787878;
}
/*Text hover colors*/

a:visited {
    text-decoration: none;
    color: #D0D0D0;
}
a:link,
a:active {
    text-decoration: none;
    color: #A8B4D4;
}
/*Old hover #86D2F5 and #E5E5E5 old visited and #B7A4B7 old purple color */

a:hover {
    text-decoration: none;
    color: #00B6FF;
}
/* Main fix starts here */

#epinfo {
    display: none!important;
}
#chat {
    display: none!important;
}
#chatango {
    width: 300px;
    height: 700px;
    display: none!important;
}
#nextprevlist {
    position: absolute;
    top: 53px;
    right: -154px;
    width: 145px;
    height: 100px;
}
header div#logo {
    position: relative;
    left: 345px;
    background: url(http://i.imgur.com/Hr9MEGu.png) no-repeat;
}
#social {
    background: none;
    position: relative;
    top: -12px;
    left: 13px;
}
#watchmode,
#srcinfo {
    display: none!important;
}
/*Some banners and social junk*/

#playinfo > span:nth-child(1),
.srcalert,
#commentinfo,
#wrapper > footer:nth-child(6),
#disclaimer,
.fb-like-box > span:nth-child(1) > iframe:nth-child(n+1),
.tweet,
.fblike,
.gplus,
.sergplus,
.serfblike,
.sertweet {
    display: none!important
}
.reportbtn {
    background: none!important;
}
/*Removing A LITTLE ads for the baka warriors that don't use ad-block when they NEED TO!! + OR USE ghostery that works great too*/

#player > div {
    background: black;
    border-style: solid;
    border-width: 1px;
    border-color: #202020;
}
#tago {
    display: none!important;
}
#wrapper > main > div.kwarta.sa.epsa {
    display: none!important;
}
#waifu > div,
#wrapper > main > div.gugol.sa.epsa,
#wrapper > main > div.gugol.la.epla,
#waifu,
.kwarta,
.wa,
.la,
.sa,
.epsa,
.epla,
a > img {
    display: none!important;
}
/*Fucking scummy stealth adverts + stalking = DEATH BY MY CODE*/

#paPontonCore,
#stats {
    display: none!important;
}
/*DELETE all the coding below if you don't like the changes it makes on smaller resolution screens when its centered.*/
/*Centers episode titles on video player*/

#playheader .title {
    height: 54px;
    width: 920px;
    float: left;
    border: 0;
    margin: 5px 0;
    padding: 0;
    text-align: center;
    line-height: 54px;
    font-size: 25px;
    text-shadow: 0 0 10px #8BB1EF;
    overflow: hidden;
}
/*Centering the damn anime lists*/

.column {
    width: 330px;
    float: left;
    padding-right: 10px;
}
.column li {
    margin-left: 14px;
    padding: 1px;
}
/*Removing the new box that creates gaps on homepage when centered*/

aside#rightside {
    display: none!important;
}
#latestlist {
    min-height: 440px;
}
/*Thanks for Hyokkuda Ryosan's input on the code below that centers content - edits from me also*/

main {
    width: 100% !important;
}
#trendinglist li,
#latestlist li {
    position: relative;
    left: 10px;
}
#serieslist li,
#newlyaddedlist li,
#recommendationslist li,
#aotslist li,
#ongoingserieslist li,
#searchresultslist li,
#seariessearchlist li {
    position: relative;
    left: 30px;
}
#wrapper > main h2 {
    text-align: center;
}
#searchtype {
    position: absolute;
    top: 7px;
    right: 16px;
}
#wrapper > footer {
    display: none!important;
}
#sitealert {
    display: none!important;
}
}

@-moz-document domain("bakavideo.tv") {
/*Removing the ten second ad timer on baka video source, thanks anagrammar*/

.trevda {
    display: none!important;
}
}

@-moz-document domain("openload.co") {
div.vjs-error-display {
    display: none!important;
}
#Videoads {
    display: none!important;
}
#mediaspace_wrapper > script:nth-child(12) {
    display: none!important;
}
#mediaspace_wrapper > script:nth-child(13) {
    display: none!important;
}
#anim-container {
    display: none!important;
}
.vjs-text-track-display div div {
    display: none!important;
}
.vjs-big-play-button:before {
    display: inline!important;
}
#vjs-tip,
#vjs-tip-arrow,
#vjs-tip-inner {
    display: none!important;
}
.logobrandOutsideplayer {
    display: none!important;
}
}

@-moz-document domain("disqus.com") {
/*These are Code DONKEYS annoying highlighted messages that appear on disqus, which nobody needs to see.*/

.highlighted>.post-content {
    display: none!important;
}
.has-highlighted-post .highlighted-comment-header {
    display: none!important;
}
}

@-moz-document domain("disqusads.com") {
/*Removing disqus ads*/

html {
    display: none!important;
}
.dark,
.text-subheading,
#grv_widget,
#grv_badge,
#grvWidgetStylesLoaded_disqus_eop_resp_6_v3_all_themes_teardrop_v2_f748720cf0790c7e1059f644bc86226c_css {
    display: none!important;
}
}

@-moz-document domain("chatango.com") {
/*Anilinkz chatango thanks to @ntaig for this code if you arent planning to use chatango at all then delete this section*/

#OM .msg {
    font-size: 14px;
    background: #1e2a34 !important;
    padding: 8px 4px 8px 4px !important;
    border: 0 !important;
}
#OM .msg:nth-child(odd) {
    background: #151d24 !important;
}
#OM .msg .user-thumb {
    display: block !important;
    width: 45px !important;
    height: 45px !important;
    border-radius: 6px !important;
    -webkit-border-radius: 6px !important;
    -moz-border-radius: 6px !important;
    margin-right: 10px !important;
}
/* Removes annoying font changes 
	Just comment this out if you for some reason want them... 
*/

#OM .msg p span,
#OM .msg p {
    font-family: ‘Lucida Sans Unicode’, ‘Lucida Grande’, sans-serif !important;
    color: #BBB !important;
}
/* --- */

#TL_URL_cntnr {
    z-index: 200 !important;
}
#OM .msg .c_username {
    color: #fff !important;
    display: block !important;
    margin: 0 4px 4px 0;
    z-index: 1 !important;
    pointer-events: none !important;
}
#OM .msg {
    position: relative !important;
}
/* Remove user backgrounds */

#OM .msg .msg-bg {
    opacity: 0 !important;
}
#OM .msg .msg-stripes {
    display: none !important;
}
#OM .msg .at-reply {
    color: #ccc !important;
    font-size: 12px !important;
    background: #47627a !important;
    padding: 2px 4px 2px 4px !important;
    border-radius: 4px !important;
    -webkit-border-radius: 4px !important;
    -moz-border-radius: 4px !important;
}
#OM .msg .msg-date {
    float: right !important;
    clear: none !important;
    color: #bbb !important;
}
#OM .msg .msg_badge {
    float: left !important;
    margin-right: 4px !important;
}
#OM .msg .at-quote {
    font-size: 12px !important;
    padding: 2px 4px 2px 4px !important;
    background: #5e9368 !important;
    border: 0 !important;
    color: #eee !important;
    border-radius: 2px !important;
    -webkit-border-radius: 2px !important;
    -moz-border-radius: 2px !important;
}
#OM .msg .at-quote .at-reply {
    background: 0 !important;
    color: #eee !important;
    padding: 0 !important;
}
#OM .msg a {
    color: #ff9e5e !important;
}
/* Blue link #59bbff */

#CGW {
    background: #2c3138 !important;
    padding: 6px 4px !important;
}
#GBG {
    border-radius: 6px !important;
    -webkit-border-radius: 6px !important;
    -moz-border-radius: 6px !important;
    border-bottom-left-radius: 0 !important;
    border-bottom-right-radius: 0 !important;
}
#OW {
    border: 0 !important;
}
input.ubdr {
    padding: 0 !important;
    margin: 0 !important;
    padding-left: 8px !important;
    color: #EEE !important;
    background: #151d24 !important;
    border: 0 !important;
    max-height: 26px !important;
}
input.ubdr::-webkit-input-placeholder {
    color: #EEE !important;
}
input.ubdr:-moz-placeholder {
    /* Firefox 18- */
    
    color: #EEE !important;
}
input.ubdr::-moz-placeholder {
    /* Firefox 19+ */
    
    color: #EEE !important;
}
input.ubdr:-ms-input-placeholder {
    color: #EEE !important;
}
#group_table tr:nth-child(1),
#group_table tr:nth-child(2),
#group_table tr:nth-child(3) {
    display: none !important;
}
#GTL,
#UTL {
    display: none !important;
}
#IWW,
#IWER2,
#input-field {
    height: 28px !important;
    max-height: 28px !important;
    padding: 0 !important;
}
.msg.ubdr {
    border: 0 !important;
}
.bottombar.has-border {
    display: none !important;
}
.msg-mod-div {
    margin-left: 6px !important;
    z-index: 9999 !important;
    opacity: 1 !important;
}
#OM .msg .msg-ip,
#OM .icon-button.delete-button {
    color: #EEE !important;
    z-index: 9999 !important;
    opacity: 1 !important;
}
path {
    fill: rgba(0, 130, 245, 0.9);
}
}
