css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  /**************
  * GENERAL CSS *
  **************/
  
  body {
    background: url("http://wallpapercave.com/wp/7Z5DQvY.jpg") !important;
  }
  
  .ps-room.ps-room-light {
    background: rgba(255, 255, 255, 0);
    border-color: #359C6C;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(158, 255, 210, 0.5);
    border-color: #359C6C;
  }
  
  .roomlist a.ilink, .roomcounters button {
    border-color: #235E6F;
    color: #235E6F;
    background: #F8FBFD;
  }
  
  .roomlist a.ilink:hover, .roomcounters button:hover {
    border-color: #CC231E;
    color: #CC231E;
    background: #F8FBFD;
  }
  
  /*******************
  * GENERAL CSS ~end *
  *******************/
  
  /*************
  * HEADER CSS *
  *************/
  
  .header {
    background: rgba(74, 204, 110, 0.85) url("http://i.imgur.com/q3jxUJC.png") no-repeat;
  }
  
  .header .logo {
    opacity: 0;
  }
  
  .maintabbarbottom {
    background: #AA0114;
    border-color: #CB0014;
  }
  
  .tabbar a.button.notifying {
    background: #BBEAE8;
    border-color: #235E6F;
    color: #235E6F;
    text-shadow: none;
    font-style: italic;
  }

  .tabbar a.button.notifying:hover {
    background: #A4E1DF;  
    border-color: #1D5262;
    color: #1D5262;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover, .maintabbar .overflow .button {
    background: #AA0114;
    border-color: #CB0014;
    color: #EEFFF4;
    text-shadow: 0px 0px 2px #77010D;
  }
  
  .tabbar .closebutton {
    color: #EEFFF4;
  }
  
  .tabbar .closebutton:hover {
    color: #BEF6D3;
  }
  
  .tabbar a.button {
    background: #F5624D;
    border-color: #CB0014;
    color: #470007;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    background: #E55C47;
  }
  
  .tabbar a.button.subtle-notifying {
    color: #255E4B;
  }
 
  .tabbar a.button.subtle-notifying:hover {
    color: #255E4B;
  } 
  
  .userbar button.icon {
    background: #AA0114;
    border: 1px solid #CB0014;
    color: #EEFFF4;
    text-shadow: 0px 0px 2px #77010D;
  }
  
  /******************
  * HEADER CSS ~end *
  ******************/
  
  /**********************
  * MAINMENUWRAPPER CSS *
  **********************/

  .menugroup {
    border: 1px solid #235E6F;
    background: rgba(50, 94, 111, 0.25);
    color: #DCF5FF;
    text-shadow: 0px 0px 2px #235E6F;
  }
  
  .menugroup .select, .menugroup .button {
    background: #BBEAE8 !important;
    border-color: #235E6F !important;
    color: #235E6F !important;
    text-shadow: none !important;
    font-style: italic !important;
  }
  
  .menugroup .select:hover, .menugroup .button:hover {
    background: #8ADCDA !important;
    border-color: #235E6F !important;
    color: #235E6F !important;
    text-shadow: none !important;
    font-style: italic !important;
  }
  
  .menugroup .select:disabled, .menugroup .select:disabled:hover {
    background: rgba(138, 220, 218, 0.7) !important;
		border-color: #235E6F !important;
		color: #235E6F !important;
		text-shadow: none !important;
		font-style: italic !important;
  }
  
  /* PM CSS */
  
  .message-pm {
    color: #7C000C;
  }
  
  .pm-window h3 {
    border-color: #185261;
    background: rgba(35, 94, 111, 0.6) url("http://i.imgur.com/3hPsjaJ.png?1") bottom right no-repeat;
    background-position: 200px;
    color: #DCF8FF;
    text-shadow: 0px -1px 0px #0A2C36;
  }
  
  .pm-window h3:hover {
    background: rgba(35, 94, 111, 0.6) url("http://i.imgur.com/3hPsjaJ.png?1") bottom right no-repeat;
    background-position: 200px;
    color: #DCF8FF;
  }
  
  .pm-window h3 small {
    color: #B2DBCD;
  }
  
  .pm-window.focused h3, .pm-window.focused h3:hover {
    border-color: #0A2C36;
    background: rgba(20, 67, 81, 0.8) url("http://i.imgur.com/6a09YBc.png?1") bottom right no-repeat;
    background-position: 200px;
    color: #DCF8FF;
    text-shadow: 0px -1px 0px #0A2C36;
  }
  
  .pm-window h3:hover .minimizebutton, .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #DCF8FF;
  }
  
  .pm-window h3 .closebutton:hover {
    color: #F7A4A4;
  }
  
  .pm-window h3 .minimizebutton:hover {
    color: #F7D0A4;
  }
  
  .pm-window h3 .closebutton:hover + .minimizebutton {
    color: #DCF8FF !important;
  }
  
  .pm-window h3.pm-notifying {
    background: #BBEAE8;
    border-color: #235E6F;
    color: #235E6F;
    text-shadow: none;
    font-style: italic;
  }
  
  .pm-window h3.pm-notifying:hover {
    background: #AEE5E3;
  }
  
  .pm-window h3.pm-notifying small {
    color: #A4578F;
  }
  
  .pm-window h3.pm-notifying .closebutton, .pm-window h3.pm-notifying .minimizebutton {
    color: #803C6F;
  }
  
  .pm-window h3.pm-notifying .closebutton:hover {
    color: #AF3B3B;
  }
  
  .pm-window h3.pm-notifying .minimizebutton:hover {
    color: #AF813B;
  }
  
  .pm-window h3.pm-notifying .closebutton:hover + .minimizebutton {
    color: #803C6F !important;
  }
  
  .pm-log {
    border-color: #185261;
  }
  
  .pm-window.focused .pm-log {
    border-color: #0A2C36;
  }
  
  .challenge {
    background: #BBEAE8;
    border-color: #235E6F;
    color: #235E6F;
    text-shadow: none;
    font-style: italic;
  }
  
  .challenge .select {
    background: #93CAC8;
    border-color: #235E6F;
    color: #235E6F;
    text-shadow: none;
  }
  
  .challenge .select:hover {
    background: #74ACAA;
    border-color: #194755;
    color: #194755;
  }
  
  .challenge .select:disabled {
    background: rgba(147, 202, 200, 0.5) !important;
    border-color: #235E6F !important;
  }
  
  .challenge .select.preselected, .challenge .select.preselected:disabled {
    color: #235E6F;
  }
  
  .challenge .buttonbar button {
    background: #93CAC8;
    border: 1px solid #235E6F;
    color: #235E6F;
    text-shadow: none;
    padding: 2px 5px;
  }
  
  .challenge .buttonbar button:hover {
    background: #74ACAA;
    border-color: #194755;
    color: #194755;
  }
  
  .pm-log-add {
    border-color: #185261;
    background: rgba(35, 94, 111, 0.6);
  }
  
  .pm-window.focused .pm-log-add {
    border-color: #0A2C36;
    background: rgba(20, 67, 81, 0.8);
  }
  
  .pm-log-add .textbox {
    border-color: #185261;
  }
  
  .pm-log-add .textbox:focus {
    border-color: #0A2C36;
  }
  
  /* PM CSS ~end */
  
  .mainmenufooter a, .mainmenufooter {
    color: #CC231E;
    text-shadow: 0px 0px 1px #DCF5FF, 0px 0px 1px #DCF5FF, 0px 0px 1px #DCF5FF;
  }
  
  .mainmenufooter a:hover {
    color: #AC120C;
  }
  
  /***************************
  * MAINMENUWRAPPER CSS ~end *
  ***************************/
  
  /***********
  * CHAT CSS *
  ***********/
  
  .chat.mine {
    background: rgba(0, 0, 0, 0);
  }

  .broadcast-blue {
    background: #CD9FC4;
    border: 1px solid #0B1361;
    color: #0B1361;
    text-shadow: 0px 0px 2px #B287A9;
    text-align: center;
    font-family: sans-serif;
    padding: 8px;
    margin: 5px;
  }

  .broadcast-blue a {
    color: #000F98;
  }

  .broadcast-blue button {
    background: #A87C9F;
    border: 1px solid #090F47;
    color: #FFF9FF;
    text-shadow: 0px 0px 2px #090F47;
    padding: 2px 8px;
  }

  .broadcast-blue button:hover {
    background: #9F7697;
  }

  .broadcast-red {
    background: #FFEBF4;
    border: 1px solid #B52C43;
    color: #B52C43;
    text-shadow: 0px 0px 2px #F2DBE5;
    text-align: center;
    font-family: sans-serif;
    padding: 8px;
    margin: 5px;
  }

  .broadcast-red a {
    color: #B3001E;
  }

  .broadcast-green {
    background: #B5D896;
    border: 1px solid #20493B;
    color: #20493B;
    text-shadow: 0px 0px 2px #A4C786;
    text-align: center;
    font-family: sans-serif;
    padding: 8px;
    margin: 5px;
  }

  .broadcast-green a {
    color: #005C3E;
  }
  
  .chat-log {
    background: rgba(255, 247, 251, 0.9) url("http://i.imgur.com/OpqaBiX.png") top right no-repeat;
  }
  
  .chat-log .chat small {
    color: #207F39;
  }
  
  .highlighted, .revealed {
    background: rgba(123, 223, 213, 0.5);
  }
  
  .infobox {
    border-color: #235E6F;
  }
  
  .chat-log-add {
    background: rgba(74, 204, 110, 0.6);
    border-color: #3DA458;
  }
  
  .chatbox label {
    text-shadow: 1px 1px 0px #F8F8F8, 1px -1px 0px #F8F8F8, -1px 1px 0px #F8F8F8, -1px -1px 0px #F8F8F8;
  }
  
  .chat-log-add .textbox {
    border-color: #3DA458;
  }
  
  .userlist {
    background: rgba(139, 245, 167, 0.8);
    border-color: #3DA458;
  }
  
  .userlist li {
    background: rgba(255, 255, 255, 0.5);
    border-color: #3DA458;
  }
  
  .userlist li button:hover, .userlist-minimized .userlist-count:hover, .userlist-maximized .userlist-count:hover {
    background: rgba(255, 255, 255, 0.5);
  }
  
  .userlist li em.group {
    color: #207F39;
  }
  
  /****************
  * CHAT CSS ~end *
  ****************/
  
  /************
  * POPUP CSS *
  ************/
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.5);
  }
  
  .ps-popup {
    background: #AA0114 url("http://i.imgur.com/yJHnhQe.png?1") no-repeat bottom right;
    border-color: #CB0014;
    color: #FFB5BC;
    text-shadow: 0px 0px 2px #77010D;
  }

  .ps-popup .roomlist a.ilink {
    border-color: #235E6F;
    color: #235E6F;
    background: #F8FBFD;
  }
  
  .ps-popup .roomlist a.ilink:hover {
    border-color: #CC231E;
    color: #CC231E;
    background: #F8FBFD;
  }
  
  .ps-popup a {
    color: #FFEBED;
  }
  
  .ps-popup a.ilink {
    color: #EEFFF4;
  }
  
  .ps-popup a.ilink:hover {
    color: #BEF6D3;
  }
  
  .ps-popup hr {
    border: none;
    border-bottom: 1px solid #7C000C;
  }
  
  .ps-popup .buttonbar {
    text-align: center;
  }
  
  .ps-popup .buttonbar button {
    color: #AA0114;
    background: #8BF5A7;
    text-shadow: 0px 0px 2px #6AE68B;
    border: 1px solid #3DA458;
    padding: 2px 5px;
  }
  
  .ps-popup .buttonbar button:hover {
    background: #6AE68B;
  }
  
  .popupmenu button {
    color: #FFB5BC;
    text-shadow: 0px 0px 2px #77010D;
  }
  
  .popupmenu button:hover {
    border-color: #7C000C;
    background: #92010F;
  }
  
  .popupmenu h3, .credits h2 {
    color: #FFEBED;
  }
  
  .popupmenu button.sel {
    border-color: #7C000C;
  }
  
  .popupmenu button.sel:hover {
    border-color: #7C000C;
    background: #7C000C;
  }
  
  .effect-volume em, .music-volume em, .credits ul {
    color: #EEFFF4;
  }
  
  .credits li small {
    color: #BEF6D3;
  }
  
  a.subtle, .credits ul li p {
    color: #FFEBED;
  }
  
  a.subtle:hover {
    color: #FFB5BC;
  }
  
  .avatarlist button {
    border-color: #7C000C;
    background-color: rgba(255, 255, 255, 0.4);
  }
  
  .avatarlist button:hover {
    border-color: #7C000C;
    background-color: rgba(255, 255, 255, 0.65);
  }
  
  .tablist .button {
    color: #AA0114;
    background: #8BF5A7;
    text-shadow: 0px 0px 2px #6AE68B;
    border: 1px solid #3DA458;
  }
  
  .tablist .button:hover {
    background: #6AE68B;
  }
  
  /*****************
  * POPUP CSS ~end *
  *****************/
  
  /******************
  * TEAMBUILDER CSS *
  ******************/
  
  .teamlist .team {
    color: #AA0114;
    background: #8BF5A7;
    border: 1px solid #3DA458;
  }
  
  .teamlist .team:hover {
    background: #6AE68B;
  }
  
  .teamchartbox {
    border-color: #359C6C;
  }
  
  .teambar button {
    border-color: #3DA458;
    color: #AA0114;
    background: #8BF5A7;
  }
  
  .teambar button:hover {
    border-color: #3DA458;
    color: #AA0114;
    background: #72D88D;
  }
  
  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    border-color: #3DA458 !important;
    color: #AA0114 !important;
    background: #8BF5A7 !important;
  }
  
  .setchart, .setchart-nickname {
    background: #AA0114;
    border-color: #CB0014;
  }
  
  .setchart label, .setchart-nickname label {
    color: #EEFFF4;
    text-shadow: 0px 0px 2px #77010D;
  }
  
  .setchart input, .setchart-nickname input, .statform input.inputform, .setdetails, .setstats {
    border-color: #CB0014;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: #93CAC8;
    border-color: #235E6F;
    color: #235E6F;
    text-shadow: none;
  }
  
  .setchart .setdetails label, .setchart .setstats label {
    color: #000;
    text-shadow: none;
  }
  
  .utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol, .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
    color: #185261;
  }
  
  .utilichart .statcol, .utilichart .bstcol, .utilichart .labelcol, .utilichart .widelabelcol, .utilichart .pplabelcol, .utilichart .abilitycol, .utilichart .twoabilitycol, .utilichart .numcol {
    color: #0A2C36;
  }
  
  .utilichart .movenamecol {
    color: #03151B;
  }
  
  .utilichart .typecol img {
    opacity: 0.8;
  }
  
  .utilichart b {
    color: #CC231E;
  }
  
  .teambuilder-results .firstresult a, .teambuilder-results .firstresult a:hover, .teambuilder-results .result a:hover {
    background: #BBEAE8;
    border-color: #235E6F;
  }
  
  .utilichart {
    background: rgba(255, 255, 255, 0.5);
    border-top: 1px solid #359C6C;
  }
  
  /***********************
  * TEAMBUILDER CSS ~end *
  ***********************/
  
  /*************
  * LADDER CSS *
  *************/
  
  .ladder th {
    background: #AA0114;
    color: #EEFFF4;
    text-shadow: 0px 0px 2px #77010D;
  }
  
  .ladder td {
    background: rgba(255, 246, 250, 0.75);
    color: #79000C;
  }
  
  .ladder td:hover {
    background: rgba(255, 246, 250, 0.95);
  }
  
  .ladder table, .ladder td, .ladder th {
    border-color: #CB0014;
  }
  
  /******************
  * LADDER CSS ~end *
  ******************/
  
  /*************
  * BATTLE CSS *
  *************/
  
  .battle {
    border-color: #325E6F !important;
  }
  
  .ps-room.ps-room-opaque {
    background: rgba(50, 94, 111, 0.5);
  }
  
  .ps-room .battle-controls {
    background: rgba(0, 0, 0, 0);
  }
  
  .battle-controls .whatdo {
    color: #DCF5FF;
    text-shadow: 0px 0px 2px #235E6F;
  }
  
  .moveselect button {
    color: #F5624D;
    text-shadow: 0px 0px 2px #235E6F;
  }
  
  .switchselect button {
    color: #34A65F;
    text-shadow: 0px 0px 2px #235E6F;
  }
  
  .battle-controls .whatdo small {
    border-color: #DCF5FF;
  }
  
  .battle-log h2 {
    background: #AA0114;
    border-color: #CB0014;
    color: #EEFFF4;
    text-shadow: 0px 0px 2px #77010D;
  }
  
  .message-throttle-notice, .message-error {
    color: #AA0114;
  } 
  
  .battle-log {
    border-color: #325E6F;
  }
  
  .battle-log-add {
    background: rgba(74, 204, 110, 0.6);
    border-color: #3DA458;
  }
  
  .battle-log-add .textbox {
    border-color: #3DA458;
  }
  
  .turnstatus-protect {
    background:url(http://i.imgur.com/RVxCoHz.png);
    opacity: 1 !important;
    height: 103px !important;
    width: 150px !important;
    border-radius: 0;
    border-width: 0;
    box-shadow: none;
    opacity: 0.9;
  }
 
  .sidecondition-safeguard {
    background: url(http://i.imgur.com/FrCoWCW.png);
    opacity: 0.6 !important;
    height: 90px !important;
    width: 130px !important;
    border-radius: 0;
    opacity: 0.9;
    border-width: 0;
    box-shadow: none;
  }
 
  .sidecondition-reflect {
    background: url(http://i.imgur.com/X1XDxVB.png);
    opacity: 0.7 !important;
    height: 90px !important;
    width: 130px !important;
    border-radius: 0;
    border-width: 0;
    opacity: 0.9;
    box-shadow: none;
  }
 
  .sidecondition-lightscreen {
    background: url(http://i.imgur.com/8eLftNA.png);
    opacity: 0.6 !important;
    height: 90px !important;
    width: 130px !important;
    opacity: 0.9;
    border-radius: 0;
    border-width: 0;
    box-shadow: none;
  }
  
  .backdrop {
    background: transparent url("http://www.eternalbuy.com/images/upfile_pl/month_1308/20138239282010710702.jpg") no-repeat scroll right bottom !important;
    opacity: 0.6;
  } 
  
  .leftbar {
    background: rgba(50, 94, 111, 0.5) url("http://i.imgur.com/YhYbeU8.png");
    border-color: #325E6F;
  }
  
  .rightbar {
    background: rgba(50, 94, 111, 0.5) url("http://i.imgur.com/OtmA4F3.png");
    border-color: #325E6F;
  }
  
  .trainer strong {
    color: #DCF5FF;
    text-shadow: 0px 0px 2px #235E6F;
    font-style: italic;
  }
  
  .statbar .hpbar {
    background: none repeat scroll 0% 0% #325E6F;
    border: 1px solid #03151B;
  }
  
  .statbar .hpbar .hptextborder {
    border-top: 1px solid #03151B;
    border-bottom: 1px solid #03151B;
  }
  
  .statbar .hpbar .hptext {
    background: #03151B;
  }
  
  .statbar .hpbar .prevhp {
    background: #03151B;
  }
  
  .statbar strong {
    display: block;
    text-align: center;
    color: #FFF;
    text-shadow: 1px 1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, -1px -1px 0px #000;
  }
  
  .switchmenu button {
    color: #AA0114;
    background: #8BF5A7;
    text-shadow: 0px 0px 2px #6AE68B;
    border: 1px solid #3DA458;
  }
  
  .switchmenu button:hover {
    color: #AA0114;
    background: #73E692;
    text-shadow: 0px 0px 2px #6AE68B;
    border: 1px solid #3DA458;
  }
  
  .switchmenu button .hpbar {
    border: 1px solid #3DA458;
  }
  
  .switchmenu button.disabled, .switchmenu button:disabled {
    border: 2px solid #AA0114 !important;
    background: #73E692 !important;
    color: #AA0114 !important;
    font-weight: bold;
  }
  
  .movemenu button {
    border: 1px solid #03151B !important;
  }
  
  .megaevo {
    background: #8BF5A7;
    border: 1px solid #BA3442;
  }
  
  /******************
  * BATTLE CSS ~end *
  ******************/
  
  /******************
  * TOURNAMENTS CSS *
  ******************/
  
  .tournament-title {
    background: rgba(139, 245, 167, 0.8);
    color: #CC231E;
  }
  
  .tournament-status, .tournament-toggle {
    background: rgba(255, 255, 255, 0.5);
    border-color: #3DA458;
    color: #3DA458;
  }
  
  .tournament-box, .tournament-tools {
    border-color: #3DA458;
  }
  
  /***********************
  * TOURNAMENTS CSS ~end *
  ***********************/
}
