css:
@-moz-document domain("reverso.net") {
/*   http://anyways.tk/   */

#content .toprightdiv .col2content, img, div#contextUp, .topGrid, .lightH, #content #rightcom, .footerLinks, #footer .footerBlue, a.mItemN.current span em, .headerDescription, #content #rightColum, .third-column, .localizediv, .frase ul, .utilizer-bg2 .text, .utilizer, .ctxbox, #content .toprightdiv, .wrapper-form-bg-3, #headerHome, .divRatingTR, #suggest_but1 ul, .fbmess, .fbbox, #content .box3, .tradcopy, div.fbPublish, .copydiv a, .number-bullet-1, .number-bullet-2, .clearbtn input, form#frmIframe, div#wrap, .banner .ad, .banner, .h10, .fdv, .softidiv {
    display: none !important;
}
.main-navigation.clearfix {
    position: absolute;
    top: 10px;
}
.wrapper-form-bg-2, .wrapper-form-bg1 {
    background: transparent;
}
a.mItemN.new span, a.mItemN.new, a.mItemN span, a.mItemN {
    background: transparent !important;
}
#content #leftColumn, #content #rightColumn {
    margin-left: 250px;
    width: 438px;
}
html {
    background: #111111;
}
#content .box1, #content .box2 {
    background: #222222;
}
textarea#txtSource {
    width: 98%;
    background: #333333;
    border: 1px solid #111111;
    color: #888888
}
.box1 textarea, .box4 textarea {
    background: #333333;
    border: 1px solid #111111;
    color: #888888
}
a.button01, a.button02 {
    background: #333333;
    border: 1px solid #111111;
    color: #111111;
}
.box1 a.translateSubmit{
    background: #333333;
    border-top: 1px solid #111111;
    border-right: 1px solid #111111;
    border-bottom: 1px solid #111111;
    height: 18px;
}
.mnuDirections {
    background: #333333;
    border: 1px solid #111111;
}
.sayBox {
    height: 20px;
    color: #888888;
}
.virtualkey {
    color: #444444;
    -webkit-filter: brightness(50%);
    filter: brightness(50%);
}
.virtualkey a {
    color: #444444;
}
.virtualkey:hover {
    -webkit-filter: brightness(100%);
    filter: brightness(100%);
}
.box1 a.translateSubmit:after {
    content: 'GO';
    color: #111;
    font-weight: bold;
    font-size: 12px;
    margin-left: 4px;
}
.box1 a.translateSubmit:hover {
    background: #222222;
}
.mnuDirections:hover {
    background: #222222;
}
ul.menu, #root-menu-div ul {
    background: #222222;
    border: 1px solid #111111;
}
#root-menu-div li a {
    color: #444444;
}
.box1 a.flag span, .box1 a.flag, div#flg {
    display: none;
}
.left {
    color: #111111;
}

::-webkit-scrollbar {
    display: none;
}
}
