css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dubtrack.fm") {

/* Match button radius */
.player_header SPAN{
  border-radius: 18px !Important;
}

/* Match other buttons to skip button */
.player_header SPAN:nth-child(n+2):nth-child(-n+4){
  display: inline !Important;
}

.player_header SPAN:not(.skip-el){
  float: left !important;
}

/* Room settings button */
.player_header .edit-room{
  padding: 0px !Important;
  padding-left: 6px !Important;
  padding-right: 6px !Important;
}
  /* Button icon */  
.player_header .edit-room:before{
  width: 36px;
  height: 36px;
  font-family: icomoon;
  content: '\e805';
  font-size: 24px;
}

.player_header .icon-chatsettings{
  display: none !Important;
}

}
