*/ 

a:link {
    color: #901212 !important;
    font-size: 15px!important;
    font-weight: 700!important;
}
a:visited{color:#760000 !important}
a:hover {color:#DC0000 !important;
	background: #000!important;
}
a:active{color:#FF0000 !important}







#content {
background: -webkit-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 4%, rgba(0,0,0,1) 8%, rgba(84,0,0,1) 18%, rgba(0,0,0,1) 32%, rgba(77,0,0,1) 45%, rgba(0,0,0,1) 54%, rgba(77,0,0,1) 64%, rgba(0,0,0,1) 78%, rgba(82,0,0,1) 87%, rgba(0,0,0,1) 95%, rgba(19,19,19,1) 100%)!important;

    background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 4%, rgba(0,0,0,1) 8%, rgba(84,0,0,1) 18%, rgba(0,0,0,1) 32%, rgba(77,0,0,1) 45%, rgba(0,0,0,1) 54%, rgba(77,0,0,1) 64%, rgba(0,0,0,1) 78%, rgba(82,0,0,1) 87%, rgba(0,0,0,1) 95%, rgba(19,19,19,1) 100%);
}

.adblock-top-text {
    margin: 9px 15px 8px;
    text-align: center;
    line-height: 24px;
    color: #E90909;
    background: black;
}

#card-ads-top {
    margin-top: -5px;
    min-height: 90px;
    background: #3D3D3D;
}


#padding {
    width: 100%;
    height: 65px;
	background: -webkit-linear-gradient(left, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 4%, rgba(0,0,0,1) 8%, rgba(84,0,0,1) 18%, rgba(0,0,0,1) 32%, rgba(77,0,0,1) 45%, rgba(0,0,0,1) 54%, rgba(77,0,0,1) 64%, rgba(0,0,0,1) 78%, rgba(82,0,0,1) 87%, rgba(0,0,0,1) 95%, rgba(19,19,19,1) 100%)!important;
background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 4%, rgba(0,0,0,1) 8%, rgba(84,0,0,1) 18%, rgba(0,0,0,1) 32%, rgba(77,0,0,1) 45%, rgba(0,0,0,1) 54%, rgba(77,0,0,1) 64%, rgba(0,0,0,1) 78%, rgba(82,0,0,1) 87%, rgba(0,0,0,1) 95%, rgba(19,19,19,1) 100%);
}


#mainarea #title {
    font-size: 1.2rem;
    height: 40px;
    font-weight: 600;
    margin: 0;
    text-align: center;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 7px;
    color: #B90101;
    border-bottom: solid 1px #dfdfdf;
    background: -webkit-linear-gradient(left, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 4%, rgba(0,0,0,1) 8%, rgba(84,0,0,1) 18%, rgba(0,0,0,1) 32%, rgba(77,0,0,1) 45%, rgba(0,0,0,1) 54%, rgba(77,0,0,1) 64%, rgba(0,0,0,1) 78%, rgba(82,0,0,1) 87%, rgba(0,0,0,1) 95%, rgba(19,19,19,1) 100%)!important;
    border: 2px solid red;
background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 4%, rgba(0,0,0,1) 8%, rgba(84,0,0,1) 18%, rgba(0,0,0,1) 32%, rgba(77,0,0,1) 45%, rgba(0,0,0,1) 54%, rgba(77,0,0,1) 64%, rgba(0,0,0,1) 78%, rgba(82,0,0,1) 87%, rgba(0,0,0,1) 95%, rgba(19,19,19,1) 100%);
}


footer.page-footer {
    margin-top: 18px;
    padding-top: 20px;
    background-color: #2E0101;
}

.card-player .card-content {
    padding-top: 10px;
    padding-bottom: 3px;
    background: black;
    border: 1.5px solid red;
}

.card .card-image {
    position: relative;
    background: black;
    /* border: 3px solid red; */
}

.card .card-title {
    color: #C80000;
    font-size: 18px;
    font-weight: 400;
    width: 100%;
    padding-left: 12px;
    margin-top: 3px;
    margin-bottom: 1px;
    height: 36px;
    background: #350000;
    border-right: 3px solid red;
    border-left: 3px solid red;
}


.box {
    position: relative;
    color: #A90000;
    /* background: #fff; */
    width: 728px;
    text-align: left;
    margin: 16px auto;
    overflow: hidden;
    background: black;
    border: 1px solid red;
}






.divider {
    height: 4px;
    overflow: hidden;
    background-color: #670000;
    margin-bottom: 5px;
    margin-top: 0;
}



.tabs .tab a.active {
    color: #7B00B5;
    background: black;
}



.tabs .tab a {
    color: #2FC8BD;
    display: block;
    width: 100%;
    height: 100%;
    -webkit-transition: all .4s ease;
    -moz-transition: all .4s ease;
    -o-transition: all .4s ease;
    -ms-transition: all .4s ease;
    transition: all .4s ease;
    background: black;
}



.tabs .indicator {
    position: absolute;
    bottom: 0;
    left: 0;
    height: 2px;
    width: 50%;
    background-color: #E300E7;
    will-change: left,right;
}


.btn, .btn-large {
    text-decoration: none;
    color: #F51515;
    background-color: #000;
    text-align: center;
    letter-spacing: .5px;
    -webkit-transition: .2s ease-out;
    -moz-transition: .2s ease-out;
    -o-transition: .2s ease-out;
    -ms-transition: .2s ease-out;
    transition: .2s ease-out;
    cursor: pointer;
    border: 1px solid red;
    border-top-color: red;
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: red;
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: red;
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: red;
    border-left-style: solid;
    border-left-width: 1px;
}


.desc-history {
    font-size: .9rem;
    color: rgba(210, 7, 68, 0.8);
}



.switch-all {
    float: left;
    margin-left: 24px;
    margin-bottom: 10px;
    margin-top: 18px;
    border: solid 1px #960000;
    padding: 10px;
    padding-right: 0;
    padding-bottom: 9px;
    border-radius: 2px;
}


.switch label input[type=checkbox]:checked+.lever {
    background-color: #4C0547;
}


.card-staff-picks .card-content .card-title {
    color: #DC06B4;
}

.board {
    font-weight: 400;
    font-size: 11pt;
    color: #f06;
    width: 30px;
    height: 30px;
    line-height: 30px;
    margin: auto;
    display: block;
    padding: 0;
    border: outset 2px #F00;
    background-color: black;
    border-radius: 2px;
    text-align: center;
    -webkit-transition: all .4s ease;
    -moz-transition: all .4s ease;
    -o-transition: all .4s ease;
    -ms-transition: all .4s ease;
    transition: all .4s ease;
}

td, th {
    padding: 15px 5px;
    display: table-cell;
    text-align: left;
    vertical-align: middle;
    border-radius: 2px;
    border: 1px solid #290000;
}

.leaderboard-entry {
    cursor: pointer;
    position: relative;
    top: 1px;
    left: 14px;
    border: 1px solid red;
}


* {
    text-shadow: none !important;
    color: #BF0303 !important;
}



#max-slider, #min-slider, #mid-slider {
    width: auto;
    font-weight: 300;
    color: red;
}


.hidden.open {
    padding-top: 34px;
    border: solid 1px #D00808;
    padding-left: 32px;
    padding-right: 32px;
    border-radius: 2px;
    margin-top: 20px;
    margin-bottom: 10px;
    overflow: hidden;
    -khtml-opacity: 1;
    opacity: 1;
    height: 60px;
    -webkit-transition: all .2s ease;
    -moz-transition: all .2s ease;
    -ms-transition: all .2s ease;
    -o-transition: all .2s ease;
    transition: all .2s ease;
}



.noUi-target, .noUi-target * {
    background: blueviolet;
    
    
}

.noUi-handle {
    position: relative;
    z-index: 1;
    background: red!important;
}

.menu-top {
    border-radius: 2px;
    background: black;
    display: inline-block;
    width: 100%;
    margin-bottom: 16px;
    border: 1px dotted red;
}


#side-navigation li {
    font-size: 14px;
    color: #777;
    padding-bottom: 9px;
    padding-top: 9px;
    padding-left: 10px;
    text-align: left;
    border-top: solid 1px #6B0303;
    cursor: pointer;
    -webkit-transition: all .3s ease;
    -moz-transition: all .3s ease;
    -o-transition: all .3s ease;
    -ms-transition: all .3s ease;
    transition: all .3s ease;
}

.genre-word-select, .genre-word:hover {
    background: #040103;
    border: solid 1px #FF0167;
    color: #fff;
}



.videorow:hover {
    background: #341313!important;
}

.row-pick:hover {
    background: #341313;
}

.genre-word {
    margin-left: 6px;
    margin-top: 6px;
    border: solid 1px #6D0202;
    color: #888;
    padding: 0 6px;
    padding-top: 1px;
    border-radius: 2px;
    font-size: 10pt;
    float: left;
    cursor: pointer;
    -webkit-transition: all .3s ease;
    -moz-transition: all .3s ease;
    -o-transition: all .3s ease;
    -ms-transition: all .3s ease;
    transition: all .3s ease;
}


#side-navigation li:hover {
    background: #341313;
}




.input-field input[type=search]:focus {
    background-color: #000;
    border: 0;
    border-radius: 2px;
    box-shadow: none;
    color: #ff0000;
}

.collection .collection-item.avatar {
    height: 100%;
    min-height: 106px;
    padding-left: 130px;
    position: relative;
    background: black;
    border: 1px solid red;
}

}
