css:
@-moz-document url-prefix("http://forums.warriorcats.com/") {
/*Global*/

/*Nudging the entire page up a little bit.*/
#pageContentContainer {
    margin-top: -15px !important;
}

/*Nav bar thing below the header image*/
.breadcrumbs>* a {
    color: #FEFD83 !important;
}

/*Title. Font size is too large and it takes up a lot of space.*/
#pageTitle {
    font-size: 24px !important;
}

/*Pagination tweaks*/
ul.pagination li.current a, ul.pagination li.current button {
    background: none !important;
}

.h-next-button, .h-previous-button {
    background-color: #ad5134 !important;
    border-color: #8a412a !important;
}
.h-next-button:hover, .h-previous-button:hover {
    background-color: #8a412a !important;
}

/*Couple of changes to 'post content' button*/
.h-post-button {
    height: 50px !important;
    border-radius: 3px !important;
    border: 1px solid #8a412a !important;
    float: right !important;
    top: 10px !important;
    color: #ad5134 !important;
    transition: all 300ms ease-out;
}
.h-post-button:hover {
    color: #8a412a !important;
    border: 1px solid #ad5134 !important;
}
.h-post-button:after {
    content: 'Post New Content' !important;
    color: #fff !important;
    visibility: visible !important;
    display: block !important;
    position: absolute !important;
    top: 13px !important;
    left: 84px !important;
}
}

@-moz-document url-prefix("http://forums.warriorcats.com/topic") {
/*Topics*/

/*Default font style*/
.post-body p {
    font-family: 'Arial', arial, serif;
    font-size: 14px;
    line-height: 21px;
    letter-spacing: 2px;
}

.post-body p {
    margin-bottom: 1em !important;
}

/*Removing reply count. Doesn't look right and we didn't need it before this.*/
.h-topic-reply-count-label {
    display: none !important;
}

/*Changes to byline area*/
.h-byline {
    background-color: #00709e !important;
    border: 1px solid #4199BF !important;
    border-bottom: none !important;
    border-radius: 6px 6px 0px 0px !important;
    padding: 6px 6px 4px 6px !important;
    margin-bottom: 0px !important;
}

.h-byline-avatar {
    border: 1px solid #fff !important;
    border-radius: 2px !important;
}

.h-member-title {
    display: block !important;
    font-size: 12px !important;
    margin-left: 0px !important;
}

.h-post-datetime {
    height: 10px !important;
    font-size: 12px !important;
    margin-top: -6px !important;
}

/*Changes to post box body*/
.post-box-body {
    border-radius: 0px 0px 3px 3px !important;
    border-top: 1px dotted #4199BF !important;
}

/*Removing massive quote trees*/
.quoted-text .quoted-text .quoted-text .quoted-text,
.quoted-text .quoted-text .quoted-text .quote-header, .quoted-text .quoted-text .quoted-text .quote, 
.quotedText .quotedText .quotedText .quotedText,
.quotedText .quotedText .quotedText .quoteHeading  {
  display: none !important;
}

/*Signature fixes*/
.signature {
    height: 100% !important;
    max-height: 110px !important;
    border-bottom: none !important;
    padding: 5px !important;
    margin-top: 20px !important;
}

/*'Take Action' sounds cheesy*/
.post-box-action-row .small-4 > a {
    visibility: hidden !important;
}
.post-box-action-row .small-12 > a {
    visibility: hidden !important;
}

.post-box-action-row .small-4 > a:after {
    content: 'Actions ▼' !important;
    visibility: visible !important;
}

.post-box-action-row .small-12 > a:after {
    content: 'Actions ▼' !important;
    visibility: visible !important;
}

/*Removed 'Original Post' note. Wasting space.*/
.h-post-note {
    display: none !important;
}

/*Settings for action boxes. It just works and I don't know why*/
#actionBoxes {
    height: 100px !important;
    position: absolute !important;
    top: 56px !important;
    left: 14px !important;
}

.action-box > a {
    color: #fff !important;
    font-size: 20px !important;
}

#editContentActionBox {
    margin-top: 4px !important;
    margin-bottom: -10px !important;
    width: 307px !important;
    height: 50px !important;
    text-align: center !important;
}
#manageConsumerActionBox {
    width: 307px !important;
    height: 50px !important;
    text-align: center !important;
}
#actionBoxes > .f-dropdown::after {
    left: 72px !important;
}
#actionBoxes > .f-dropdown::before {
    left: 72.5px !important;
}

/*Moving the Recent Topics widget down a little*/
.h-forum-topics-widget {
    margin-top: 187px !important;
}

/*And sliding the 'Like This Topic' and 'Follow This Topic' buttons into the space it made*/
.h-content-ancillary .interest-row {
    position: absolute !important;
    right: -319.5px !important;
    top: 143px !important;
    z-index: 2 !important;
}

/*The Like/Follow buttons resize themselves which I don't like*/
.h-content-ancillary .interest-row .button {
    width: 150px !important;
    padding: 10px 5px 10px 5px !important;
    background: none !important;
    font-size: 12px !important;
}

/*Remove button color & change font color for heart & check buttons*/
a.button i.fa.icon-on {
    color: #AD5134 !important;
}
}

@-moz-document url-prefix("http://forums.warriorcats.com/forum/") {
/*Moving the Recent Topics widget down a little*/
.h-forum-topics-widget {
    margin-top: 55px !important;
}

/*Resize & move Follow button*/
#watchForumButton {
    position: absolute !important;
    top: 150px !important;
    left: 681px !important;
    width: 304px !important;
    background: none !important;
    z-index: 2 !important;
}
#watchForumButtonText {
    font-family: Open Sans,Arial,sans-serif !important;
    font-size: 16px !important;
    color: #FEFD83 !important;
}

/*Action boxes*/
.action-boxes {
    width: 300px !important;
    text-align: center !important;
    margin-top: 69px !important;
}
.action-boxes .action-box {
    font-family: Open Sans,Arial,sans-serif !important;
    font-size: 16px !important;
}

/*Remove button color & change font color for heart & check buttons*/
a.button i.fa.icon-on {
    color: #fff !important;
}

/*Left content*/

/*Rounded section titles & no spacing*/
.section-box-header { 
    border-radius: 6px 6px 0px 0px !important;
    margin-bottom: -1px !important;
}

/*Change color on hover & remove spacing*/

.h-ubb-topic-list-box {
    margin-bottom: -1px !important;
    transition: all 200ms ease-out !important;
}
.h-ubb-topic-list-box:hover {
    background-color: #00709e !important;
    border: 1px solid #4199BF !important;
}

/*Move profile picture to left of topic info*/
.h-ubb-topic-list-box .medium-10 {
    margin-left: 95px !important;
}
.h-ubb-topic-list-box .medium-12 {
    margin-left: 95px !important;
}
.h-ubb-topic-list-box .medium-2 {
	position: absolute !important;
}
.h-ubb-topic-list-box .medium-2 img {
    border: 1px solid #fff !important;
    height: 75px !important;
    width: 75px !important;
}

/*Remove cross-link note*/
.h-topic-cross-link-label {
    display: none !important;
}
/*remove ugly colloring on 'new'/'replies' boxes*/
.h-new-label, .h-topic-reply-count-label {
    background: none !important;
}
/*recolor new label*/
.h-new-label {
    color: #FEFD83 !important;
}
/*resize content label section*/
.content-label-section {
    height: 14px !important;
}

/*remove caps on replies counter & other changes*/
.h-topic-reply-count-label {
    margin-left: 10px !important;
    text-transform: none !important;
}
/*add speech bubble before reply count*/
.h-topic-reply-count-label:before {
    content: '\1F4AC' !important;
    position: absolute !important;
    left: -11px !important;
}

/*pagination recolor*/
ul.pagination.inline.tiny-font li a, ul.pagination li a  {
    color: #fff !important;
}
ul.pagination li a:hover {
    background-color: #4199BF !important;
}
.pagination .current a {
    font-style: italic !important;
    color: #a94f4f !important;
}
}

@-moz-document url("http://forums.warriorcats.com/forums"), url-prefix("http://forums.warriorcats.com/category") {
.section-box-header {
    border-radius: 6px 6px 0px 0px !important;
    margin-bottom: -1px !important;
}

/*Moving the Recent Topics widget down a little*/
.h-forum-topics-widget {
    margin-top: 55px !important;
}

/*Resize & move Follow button*/
#watchForumButton {
    position: absolute !important;
    top: 150px !important;
    left: 681px !important;
    width: 304px !important;
    background: none !important;
    z-index: 2 !important;
}
#watchForumButtonText {
    font-family: Open Sans,Arial,sans-serif !important;
    font-size: 16px !important;
    color: #FEFD83 !important;
}
/*Action boxes*/
.action-boxes {
    width: 300px !important;
