css:
@-moz-document domain('reddit.com') {
  .thing:not(.comment) .midcol .arrow, .midcol.unvoted .arrow { visibility: hidden }
  .thing:not(.comment) .midcol:hover .arrow, .arrow, .midcol.unvoted:hover .arrow { visibility: visible }

  .thing .rank,
  .commentarea .entry .buttons > .viewSource, .commentarea .entry .buttons > .save-button, .commentarea .entry .buttons > .saveComments, .commentarea .entry .buttons > .report-button, .commentarea .entry .buttons > .give-gold-button, .commentarea .entry .buttons > li .bylink, .commentarea .entry .buttons > li .sendreplies-button, .commentarea .entry .buttons > li .toggleChildren,
  .linklisting .entry .buttons > :not(.first), #sr-header-area, .organic-listing, #header-img { display: none !important }

  .res-nightmode .listing-page .sitetable > div:nth-of-type(4n+1), .res-nightmode .RES-keyNav-activeElement { background-color: inherit !important }
  .entry .tagline { color: transparent !important }

  .commentarea .entry .buttons > *, .entry .tagline > :not(.author), .linklisting .entry .buttons .comments, .thumbnail.default, .thumbnail.self, .domain a
  { opacity: 0.07 !important }
  .commentarea .entry .buttons > :hover, .entry .tagline > :hover, .linklisting .entry .buttons .comments:hover, .domain a:hover { opacity: 1 !important }
  .res-nightmode.res-commentBoxes .comment { border: none !important; }
  .res-nightmode .RES-keyNav-activeElement .usertext-body .md p, .res-nightmode .RES-keyNav-activeElement .usertext-body .md, .res-nightmode .RES-keyNav-activeElement .usertext-body { background-color: transparent !important; }

  .domain { color: rgba(255, 255, 255, 0) }

  .commentarea > .usertext { width: 80px !important }
  .commentarea > .usertext .usertext-edit { display: none !important }
  .commentarea > .usertext:before {
    content: "comment";
    display: block;
    background-color: rgba(255, 255, 255, 0.3);
    border-radius: 3px;
    /*padding: 3px;*/
    text-align: center;
  }

  .commentarea > .usertext:hover .usertext-edit { display: block !important }
  .commentarea > .usertext:hover { width: 100% !important }
  .commentarea > .usertext:hover:before { display: none !important }

  .thing:not(.comment) {
    padding-top: 14px !important;
  }

  .thing:not(.comment) > .midcol {
    position: relative !important;
    top: -12px !important;
  }

  #header, .res-nightmode #header, .res-nightmode #header-bottom-left {
    border: none !important;
    background-color: transparent !important;
    margin-top: 10px !important;
  }

  .pagename a, .res-nightmode .tabmenu li a {
    color: #737373 !important;
  }

  .res-nightmode .tabmenu li a, .md code, .md pre {
    background-color: transparent !important;
  }

  .res-nightmode .tabmenu li.selected a, .res-nightmode .tabmenu li:hover a {
    color: #b3b3b3 !important;
  }

  .res-nightmode .tabmenu li.selected {
    border-bottom: 2px solid #b3b3b3 !important;
  }

  .res-nightmode .tabmenu li {
    padding: 6px !important;
  }

  .commentarea .entry {
    display: flex;
    flex-wrap: wrap;
  }

  .commentarea .entry .tagline {
    order: 1;
  }

  .commentarea .entry .tagline {
    order: 1;
  }

  .commentarea .entry .usertext {
    order: 3;
    min-width: 100%;
  }

  .commentarea .entry .buttons {
    order: 2;
    margin-top: -2px;
  }
}
