css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {
  
div.ProfileWTFAndTrends,
div.ProfileCanopy-card,
li.ProfileNav-item--media,
li.ProfileNav-item--more,
div.DashboardProfileCard-content > *:nth-child(1),
div.ProfileCard-content > *:nth-child(2),
div.DashboardProfileCard-content > *:nth-child(3),
a.DashboardProfileCard-bg,
a.ProfileCard-bg,
div.SidebarCommonModules,
div.siderail-related-tweets-container,
div#profile-hover-container,
li.DismissibleModule,
li.has-profile-suggested-tweet {
    display: none !important;
}
/* Simplify Follower-Followee List */

a.ProfileCard-avatarLink {
    margin: 3px !important; float: left;
}

div.Grid-cell.u-size1of3.u-mb10 {
    margin-bottom: 0px !important;
}

div.ProfileCard {
    border-radius: initial !important;
}

div.ProfileCard-actions {
    background-color: white;
}

span.FollowStatus {
    color: blueviolet;
}

a.ProfileNameTruncated-link {
    font-size: 14px;
}

p.ProfileCard-bio {
    font-size: 12px;
}

div.ProfileCard-content {
    margin: 8px;
}
/* force one column style */

.u-size1of2,
.u-size1of3,
.u-size2of3,
.u-size1of4,
.u-size3of4,
div.u-size2of6,
div.u-size4of12,
.u-lg-size1of3,
.u-lg-size2of3,
.u-lg-size4of6,
.u-lg-size8of12 {
    width: 100% !important;
}

div.content-main {
    margin: 0px !important;
}

div.ProfileCanopy-avatar {
    bottom: 10px;
}

div.ProfileCanopy-nav,
.ProfileCanopy.is-locked .ProfileCanopy-inner,
div.AdaptiveSearchTitle.u-bgUserColor,
div.AdaptiveFiltersBar,
.AdaptiveSearchPage .AppContent-main,
.AdaptiveSearchPage .AppContent {
    width: 520px !important; min-width: 520px !important; margin: 0 auto;
}

.AdaptiveSearchTimeline--mediaGrid .stream-container {
    width: inherit;
}

.ProfilePage {
    min-width: 520px !important;
}

.permalink-container {
    width: 521px !important;
}

.multi-photo {
    width: 212px !important;
}

.OldMedia {
    max-width: 435px !important;
}

.player-container {
    max-width: 435px !important;
}

div.home-tweet-box {
    width: 494px !important; margin: -23px auto auto auto;
}

li.stream-item,
.stream-item.separated-module + .stream-item {
    border-radius: unset;
}
/* Integrate "Compose new Tweet" area smoothly */

.tweet-user {
    border-radius: 0 !important;
}

.top-timeline-tweetbox .timeline-tweet-box .tweet-form {
    margin-left: 0px;
}
/* Adjust and make the tweet box look right.
*/

.home-tweet-box {
    margin: 0px !important;
}
/* More things that bug me: .dashboard-left and .dashboard-right need to die. */

.dashboard-left,
.dashboard-right {
    display: none !important;
}

/* MORE things that need to go die in a fire */

li.moments { display:none !important; }

.top-timeline-tweetbox .timeline-tweet-box {
    border-radius: 0 !important; margin: auto auto auto auto;
}

.form-search {
    width: 200px !important;
}

.top-timeline-tweet-box-user-image {
    display: none;
}
/* Profile */

div.ProfileSidebar {
    padding: 1px 0px; background-color: white;
}

.ProfileCanopy,
.ProfileCanopy-header {
    height: 240px !important;
}

.ProfileCanopy--large {
    height: 220px !important;
}

.ProfileCard-bio {
    height: 2.6em;
}

#empty-timeline-recommendations,
.trends,
.content-header,
.site-footer,
.bird-topbar-etched,
.component,
.wtf-module,
.topics,
.topbar-tweet-btn,
.topbar-divider,
.search-icon,
.js-mini-profile-stats-container,
.js-mini-profile-stats-container,
div.profile-summary small.metadata,
div.promptbird,
span.text,
.flex-module.media-thumbnails.large.recent_photos,
#signin-link>small,
.nav-breaker,
.wrapper-mobile_gallery > div.page-canvas,
div.Footer.module,
.enhanced-mini-profile .mini-profile .profile-summary {
    display: none !important;
}

div.profile-card,
.profile-card.profile-header,
div.profile-header-inner-overlay,
div.profile-banner-footer,
#profile_popup .modal-content.clearfix {
    display: block !important; z-index: 1; width: 520px !important;
}

.home-tweet-box {
    display: block !important;
}

#page-container,
.global-nav,
.container,
.content-main,
.dashboard,
#timeline {
    width: 520px !important; margin: 0 auto;
}

.global-nav .container {
    min-width: 520px;
}

.js-hover.js-nav {
    visibility: hidden; width: 25px !important;
}

.js-hover.js-nav > .new-wrapper {
    visibility: visible !important;
}

.js-hover.js-nav > .new-wrapper > .nav-new {
    margin-left: -16px !important;
}

div.dm-nav {
    margin: 3px 18px;
}

.profile-header .profile-card-inner {
    /* 人のプロファイル。 */
    
    background-color: rgba(0, 0, 0, .7);
    /* 文字が見難いような背景画像にする人がいるので、その対策 */
    
    width: 480px;
    /* それに伴ってスタイルの改善 3点 */
    
    padding: 20px; text-align: left;
}

.profile-header-inner {
    height: auto !important;
    /* 上で padding を指定した副作用の相殺 */
}

.module .flex-module {
    /* 人のツイート数等 */
    
    border: none;
    /* はみ出さないようにする */
    
    background-color: #F2F2F2;
    /* そのため境界が分かりにくくなるのを背景色で補完 */
}

.mini-profile img.avatar {
    z-index: 500;
}

.profile-summary {
    padding: 5px !important;
}

.mini-profile {
    margin-top: -12px; margin-bottom: -3px;
}

.topbar,
.nav .active a {
    background-color: transparent !important; -webkit-box-shadow: 0 0px 0px rgba(0, 0, 0, 0) !important; -moz-box-shadow: 0 0px 0px rgba(0, 0, 0, 0) !important; box-shadow: 0 0px 0px rgba(0, 0, 0, 0) !important; border: none;
}

.global-nav,
.global-nav .well,
.nav .active > a,
.btn-tweet,
.btn-tweet:hover,
.btn-tweet:active,
.nav .active > a,
.card-soccer .cover {
    background-image: none !important; background-color: transparent !important;
}

.profile-card.profile-header .stats a {
    padding: 3px !important;
}

.profile-card.profile-header .stats a strong {
    margin-left: 3px; display: inline;
}

.profile-card.profile-header .stats li {
    display: block;
}

.profile-header-inner-overlay {
    height: auto !important;
}

.stream-user-gallery * {
    margin: auto !important;
}
/* Show Favorites, Lists. Shift down User Action button. */

.ProfileNav-item--userActions {
    margin-top: 58px; margin-right: 10px; z-index: 10; background-color: transparent;
}

.ProfileNav-item--favorites {
    display: inline-block !important;
}

.ProfileNav-item--lists {
    display: inline-block !important;
}
/* Fix "View all Tweets" link on a search page.
   This element escapes by the effect of auto-pager. */

div.stream-end a.js-nav {
    position: fixed; padding: 0.5em; bottom: 3em; background-color: rgb(245, 245, 245);
}
/* disable decoration of Fav & Retweet people's icon */

.stream-item .stats .avatar-row a:first-child {
    border: none !important; padding-left: inherit !important;
}
}
