css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("itsjerryandharry.com") {
#header {
    background-color: rgb(255, 62, 62); /* Original: rgb(177, 139, 139) */
}
.navTabs .navTab.selected .tabLinks {
    background-color: rgb(255, 128, 121); /* Original: rgb(210, 166, 166) */
}
.navTabs .navTab.selected .navLink {
background-color: rgb(255, 128, 121); /* Original: rgb(210, 166, 166) */
}
.navTabs .navTab.PopupClosed .navLink, .navTabs .navTab.PopupClosed .navLink:hover {
    color: rgb(255, 168, 168); /* Original: rgb(238,186,185) */
}
.subHeading {
    background: rgb(255, 159, 159) !important; /* Original: rgb(252, 217, 215) */
}
.primaryContent a {
    color: rgb(230, 0, 0); /* Original: rgb(179,32,16) */
}
a:link, a:visited {
    color: rgb(230, 0, 0);
}
.heading, .xenForm .formHeader {
background-color: rgb(255, 128, 121); /* Original: rgb(210, 166, 166) */
}
.sectionFooter {
background-color: rgb(255, 159, 159); /* Original: rgb(252, 217, 215) */
}
.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading {
    color: rgb(255, 81, 81); /* Original: rgb(228, 110, 108) */
    margin-top: 5px;
}
.avatar img, .avatar .img, .avatarCropper {
    border: 1px solid rgb(255, 121, 121); /* Original: rgb(238, 186, 185) */
}
.formPopup .controlsWrapper {
    background: rgb(255, 159, 159); /* Original: rgb(252, 217, 215) */
}
.Menu .sectionFooter {
    background-color: rgba(255, 181, 179, 0.9); /* Original: rgba(252, 217, 215, 0.9) */
}
.sectionHeaders {
    background: rgb(255, 159, 159) !important; /* Original: rgb(252, 217, 215) */
}
.tabs li a, .tabs.noLinks li {
border: 1px solid rgb(255, 121, 121);
background: rgb(255, 159, 159);
}
.tabs {
border-bottom: 1px solid rgb(255, 121, 121);
}
.discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: rgb(255, 183, 183); /* Original: rgb(252, 241, 240) */
}
.discussionListItem {
    border-bottom: 1px solid rgb(255, 159, 159);
}
.messageUserBlock div.avatarHolder {
    background-color: rgb(255, 183, 183);
}
.messageUserBlock .arrow span {
border-left-color: rgb(255, 183, 183);
-moz-border-left-colors: rgb(255, 183, 183);
}
.messageUserBlock {
    background-color: rgb(255, 159, 159);
    border: 1px solid rgb(255, 159, 159);
}
.messageUserBlock .extraUserInfo {
    background-color: rgb(255, 183, 183);
}
.redactor_toolbar {
    background-color: rgb(255, 183, 183);
    border-bottom: 1px solid rgb(255, 159, 159);
}
.nodeList .categoryStrip {
    color: #710000; /* Original: #6d3f03 */
}
.nodeList .categoryStrip .nodeTitle a {
    color: #710000;
}
.nodeList .categoryStrip {
    background: rgb(255, 159, 159) !important;
}
.node .nodeLastPost {
    background-color: rgb(255, 183, 183);
border: 1px solid rgb(255, 159, 159);
}
.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
    background: rgb(255, 128, 121) url('http://itsjerryandharry.com/styles/flexile/flexile/node-sprite.png') no-repeat 0 0;
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background: rgb(225, 0, 0) url('http://itsjerryandharry.com/styles/flexile/flexile/node-sprite.png') no-repeat -44px 0; /* Original: rgb(179, 32, 16) */
}
#taigachat_box {
    border: 1px solid rgb(255, 121, 121);
    background: rgb(255, 183, 183);
}
.userBanner.bannerStaff {
    color: rgb(225, 0, 0);
    border-color: rgb(255, 121, 121);
}
.message .likesSummary {
    border: 1px solid rgb(255, 159, 159);
}
.secondaryContent {
    background-color: rgb(255, 183, 183);
}
}
