css:
@-moz-document domain("chatango.com") {
#OM .msg {
  font-size: 12px;
  background: #fff !important;
  padding: 8px 4px 8px 4px !important;
  border: 0 !important;
}
#OM .msg:nth-child(odd) {
  background: #FBFBFB !important;
}
#OM .msg .user-thumb {
  display: block !important;
  width: 35px !important;
  height: 35px !important;
  margin-right: 10px !important;
}
/* Removes annoying font changes 
	Just comment this out if you for some reason want them... 
*/

#OM .msg p span,
#OM .msg p {
  font-family: ‘Lucida Sans Unicode’, ‘Lucida Grande’, sans-serif !important;
  color: #000 !important;
}
/* --- */

#TL_URL_cntnr {
  z-index: 200 !important;
}
#OM .msg .c_username {
  color: #000 !important;
  display: block !important;
  margin: 0 4px 4px 0;
  z-index: 1 !important;
  pointer-events: none !important;
}
#OM .msg {
  position: relative !important;
}
/* Remove user backgrounds */

#OM .msg .msg-bg {
  opacity: 0 !important;
}
#OM .msg .msg-stripes {
  display: none !important;
}
#OM .msg .at-reply {
  color: #000 !important;
  font-size: 12px !important;
  background: #47627a !important;
  padding: 2px 4px 2px 4px !important;
  border-radius: 4px !important;
  -webkit-border-radius: 4px !important;
  -moz-border-radius: 4px !important;
}
#OM .msg .msg-date {
  float: right !important;
  clear: none !important;
  color: #000 !important;
}
#OM .msg .msg_badge {
  float: left !important;
  margin-right: 4px !important;
}
#OM .msg .at-quote {
  font-size: 12px !important;
  padding: 2px 4px 2px 4px !important;
  background: #5e9368 !important;
  border: 0 !important;
  color: #000 !important;
  border-radius: 2px !important;
  -webkit-border-radius: 2px !important;
  -moz-border-radius: 2px !important;
}
#OM .msg .at-quote .at-reply {
  background: 0 !important;
  color: #eee !important;
  padding: 0 !important;
}
#OM .msg a {
  color: #ff9e5e !important;
}
/* Blue link #59bbff */

#CGW {
  background: #000 !important;
  padding: 6px 4px !important;
}
#OW {
  border: 0 !important;
}
input.ubdr {
  padding: 0 !important;
  margin: 0 !important;
  padding-left: 8px !important;
  color: #EEE !important;
  background: #151d24 !important;
  border: 0 !important;
  max-height: 26px !important;
}
input.ubdr::-webkit-input-placeholder {
  color: #000 !important;
}
input.ubdr:-moz-placeholder {
  /* Firefox 18- */
  
  color: #000 !important;
}
input.ubdr::-moz-placeholder {
  /* Firefox 19+ */
  
  color: #000 !important;
}
input.ubdr:-ms-input-placeholder {
  color: #000 !important;
}
#group_table tr:nth-child(1),
#group_table tr:nth-child(2),
#group_table tr:nth-child(3) {
  display: none !important;
}
#GTL,
#UTL {
  display: none !important;
}
#IWW .msg .msg-fg .input-field {
  background: white !important;
  color: black !important;
}
#IWW .msg .msg-fg .input-field::before {
  color: black !important;
}
#IWW,
#IWER2,
#input-field {
  height: 28px !important;
  max-height: 28px !important;
  padding: 0 !important;
  color: #000;
  background: #fff;
}
#input-field {
  box-sizing: border-box !important;
  padding: 6px 6px 0px 6px !important;
  background: #000 !important;
  color: #EEE !important;
}
.msg.ubdr {
  border: 0 !important;
}
.bottombar.has-border {
  display: none !important;
}
.msg-mod-div {
  margin-left: 6px !important;
  z-index: 9999 !important;
  opacity: 1 !important;
}
#OM .msg .msg-ip,
#OM .icon-button.delete-button {
  color: #EEE !important;
  z-index: 9999 !important;
  opacity: 1 !important;
}
}

@-moz-document domain("infrequent.co") {
.navbar.navbar-default.navbar-fixed-top.navbar-inverse {
  background: red !important;
  border: 0 !important;
  padding: 0 8px 0 8px !important;
}
.chat {
  padding-top: 50px !important;
}
.navbar.navbar-default.navbar-fixed-top.navbar-inverse .container {
  margin: 0 !important;
  width: 100% !important;
  padding: 0 !important;
  padding-left: 20px !important;
}
/* No point in showing these options right now... */

ul.nav.navbar-nav.navbar-right {
  display: none !important;
}
ul.nav.navbar-nav li a {
  font-size: 14px !important;
}
ul.nav.navbar-nav li a:hover,
ul.nav.navbar-nav li a.dropdown-toggle,
ul.nav.navbar-nav ul.dropdown-menu li a:hover {
  color: #ff9e5e !important;
  background: 0 !important;
}
ul.nav.navbar-nav li:first-child a {
  padding-left: 8px !important;
}
ul.nav.navbar-nav {
  padding: 0 !important;
  margin: 0 !important;
}
ul.nav.navbar-nav ul.dropdown-menu {
  background: #000 !important;
  opacity: 0.8 !important;
}
ul.nav.navbar-nav ul.dropdown-menu li a {
  color: #fff !important;
  padding: 8px 12px 8px 12px !important;
}
ul.nav.navbar-nav ul.dropdown-menu li.divider {
  display: none !important;
}
/* Logo thingy */

a.navbar-brand {
  background: url("http://i.imgur.com/JmLAWLs.png)") no-repeat 0px !important;
}
a.navbar-brand img {
  display: none !important;
}
.msg-mod-div {
  margin-left: 6px !important;
  z-index: 9999 !important;
  opacity: 1 !important;
}
#OM .msg .msg-ip,
#OM .icon-button.delete-button {
  color: #EEE !important;
  z-index: 9999 !important;
  opacity: 1 !important;
}
}
