css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://www.web.roblox.com"), url-prefix("https://www.web.roblox.com"), url-prefix("http://forum.roblox.com/"), url-prefix("https://forum.roblox.com/") {
.content {
    max-width: 1338px;
    background-color:rgba(0, 0, 0, 0) !important;
    padding-top: 12px;
    margin: 0 auto;
}

#header {
    background-color: #d50000;
}

#header .btr-nav-notif {
    border: 2px solid #ff5252;
}

.status-confirm {
    border: 1px solid #d50000;
    background-color: #FFC0CB;
}

.open .input-dropdown-btn {
    background-color: #ff5252 !important;
}

.input-group-btn .dropdown-menu li a:hover, .input-group-btn .dropdown-menu li a:focus, .input-group-btn .dropdown-menu li a.active {
    box-shadow: 4px 0 0 0 #ff5252 inset;
}

.menu-vertical .menu-option .menu-secondary-option:focus, .menu-vertical .menu-option .menu-secondary-option.active {
    box-shadow: 4px 0 0 0 #ff5252 inset;
}

.nav-pills>li.active>a,.nav-pills>li.active>a:hover,.nav-pills>li.active>a:focus {
    background-color: #ff5252;
}

.menu-vertical .menu-option:hover, .menu-vertical .menu-option:focus, .menu-vertical .menu-option.active {
    box-shadow: 4px 0 0 0 #ff5252 inset;
}

.rbx-tab-horizontal-submenu li:hover {
    background-color: #ff5252 !important;
}

.pill-toggle input:checked+label {
    background-color: #ff5252 !important;
}

.navigation .notification-icon {
    background: #b71c1c;
}
.rbx-btn-secondary-sm {
    background-color: #ff5252 !important;
    border-color: #ff5252;
}
.rbx-btn-secondary-sm:hover {
    background-color: #ff1744 !important;
    border-color: #ff1744;
}
.chat-header {
    background-color: #d50000 !important;
}
.rbx-btn-primary-lg {
    background-color: #ff5252 !important;
    border-color: #ff5252;
}
.rbx-btn-primary-lg:hover {
    background: #ff1744 !important;
    border-color: #ff1744;
}
.rbx-highlight {
    background-color: #ff5252 !important;
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    box-shadow: 0 -4px 0 0 #d50000 inset;
}
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active {
    box-shadow: 0 -4px 0 0 #d50000 inset;
}
.rbx-dropdown-menu li:hover {
    box-shadow: 4px 0 0 0 #d50000 inset;
    cursor: pointer;
}
.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0 0 0 #d50000 inset;
    cursor: pointer;
}
.rbx-input-group-btn .rbx-input-dropdown-btn:hover,
.rbx-input-group-btn .rbx-input-dropdown-btn:focus {
    background-color: #d50000;
    box-shadow: 0 1px 3px rgba(150, 150, 150, 0.74);
    border-color: #d45000;
    color: white;
    cursor: pointer;
}
.open .rbx-input-dropdown-btn {
    background-color: #d50000;
    color: white;
}
.rbx-input-group-btn .rbx-dropdown-menu li a:hover,
.rbx-input-group-btn .rbx-dropdown-menu li a:focus,
.rbx-input-group-btn .rbx-dropdown-menu li a.active {
    box-shadow: 4px 0 0 0 #d50000 inset;
    cursor: pointer;
    outline: 0 none;
    border: 0 none;
}
.dialog-container .dialog-header .dialog-header-title {
    position: relative;
    overflow: hidden;
    background-color: #d50000;
    white-space: nowrap;
}
.dialog-container .dialog-header .dialog-header-title:after {
    background-image: linear-gradient(to right, rgba(0, 162, 255, 0) 0, #d50000 90%);
    content: "";
    height: 100%;
    position: absolute;
    right: 0;
    bottom: 0;
    width: 10%;
}
.dialog-container .dialog-header {
    background-color: #d50000;
}
.dialog-container .dialog-message-container .dialog-message {
    max-width: 90%;
    max-width: calc(100% - 24px);
    background-color: #EA6E6E;
    border-radius: 3px;
    padding: 9px;
    margin: 0 12px;
    float: right;
    word-wrap: break-word;
}
.dialog-container .dialog-message-container .dialog-triangle:before {
    border-color: transparent transparent transparent #EA6E6E;
    border-style: solid;
    border-width: 6px;
    content: "";
    width: 0;
    height: 0;
    left: 100%;
    position: absolute;
    top: 12px;
}
@keyframes blink-dialog {
    from {
        background-color: #EA6E6E;
    }
    to {
        background-color: #d50000;
    }
}

.footer-link a:hover {
    color: #d50000;
}
.rbx-btn-primary-xs:hover, .rbx-btn-primary-xs:focus {
    background-color: #d50000;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #d50000;
    color: white;
    cursor: pointer;
}
.rbx-btn-primary-xs:active, .rbx-btn-primary-xs:visited, .rbx-btn-primary-xs.active {
    box-shadow: none;
    background-color: #d50000;
    color: white;
}
.rbx-btn-primary-xs {
    background-color: #D02424;
    border-color: #D02424;
    color: white;
    height: auto;
    transition: all,0.2s,ease-in-out;
    padding: 7px 16px;
    font-size: 1em;
    line-height: 100%;
    border-radius: 3px;
}
.dialog-container .dialog-member-header .group-member .group-leader {
    background-color: #F95E5E;
}

.btn-secondary-xs {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    border: 1px solid transparent;
    background-color: #d50000 !important;
    border-color: #d50000 !important;
    color: #fff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    transition: all,0.2s,ease-in-out !important;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 4px 4px;
    font-size: 14px;
    line-height: 100%;
    border-radius: 3px;
}

.btn-secondary-xs:hover, .btn-secondary-xs:focus {
    background-color: #EA6E6E !important;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #EA6E6E !important;
    color: #fff;
    cursor: pointer;
}

.btn-secondary-md {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow .2 ease-in-out;
    -o-transition: box-shadow .2 ease-in-out;
    transition: box-shadow .2 ease-in-out;
    border: 1px solid transparent;
    background-color: #d50000 !important;
    border-color: #d50000 !important;
    color: #fff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px 9px;
    font-size: 18px;
    line-height: 100%;
    border-radius: 3px;
}

.btn-secondary-md:hover, .btn-secondary-md:focus {
    background-color: #ff5252 !important;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #ff5252 !important;
    color: #fff;
    cursor: pointer;
}

.notification-blue {
    background-color: #d50000;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 400;
    color: #fff;
    text-align: center;
    padding: 5px 6px;
    min-width: 24px;
}

.dropdown-menu li:hover {
    background-color: #F2F2F2;
    box-shadow: 4px 0 0 0 #d50000 inset;
    cursor: pointer;
}

.SetListDropDownList .menu a:hover,
.ui-autocomplete .ui-menu-item a:hover {
    background-color: #d50000 !important;
    color: #fff !important;
    cursor: pointer !important;
}
.btn-neutral,
.btn-neutral:link,
.btn-neutral:active,
.btn-neutral:visited {
    border-color: #d40000 !important;
    background-color: #d40000 !important;
    background-image: url(http://i.imgur.com/ZvnvdGv.png) !important;
    color: white !important;
}

.abp {
    display: none !important;
}

.rbx-tabs-vertical .rbx-tab.active .rbx-tab-heading {
    box-shadow: -4px 0 0 0 #d50000 inset !important;
}
.rbx-tabs-vertical .rbx-tab-heading:hover,
.rbx-tabs-vertical .rbx-tab-heading:focus,
.rbx-tabs-vertical .rbx-tab-heading:active {
    box-shadow: -4px 0 0 0 #d50000 inset;
}
.active .rbx-lead {
    color: #d50000 !important;
    font-weight: 400;
}
div.MediaPlayerIcon {
    background-image: url(http://puu.sh/lK4Th/0f51f6731e.png) !important;
    background-repeat: no-repeat !important;
    width: 25px !important;
    height: 25px !important;
    border: 0 !important;
}
.rbx-btn-primary-sm {
    background-color: #d50000;
    border-color: #d50000;
    color: white;
    height: auto;
    transition: all,0.2s,ease-in-out;
    padding: 9px 24px;
    font-size: 1.125em;
    line-height: 100%;
    border-radius: 3px;
}
.rbx-btn-primary-sm:hover, .rbx-btn-primary-sm:focus {
    background-color: #EA6E6E;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #EA6E6E;
    color: white;
    cursor: pointer;
}
.forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
    background: #b71c1c !important;
}
.rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button {
    background-color: #d50000 !important;
    border-color: #d50000;
}
.rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover {
    background: #ff5252 !important;
    border-color: #ff5252 !important;
}
#TopAbpContainer {
    display: none;
}
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left {
    display: none;
}
#MasterContainer,
.nav-content-inner,
.container-main,
.gutter-ads {
    background: url(http://i.imgur.com/2zfLaSK.png) top center repeat-x #DDDDDB;
}
#party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off {
    background: linear-gradient(to bottom, #e53935, #b71c1c);
}
#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4) {
    display: none;
}
body {
    font-family: Segoe UI;
}
#GamesPageLeftColumn {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
}
#GamesListsContainer > div {
    background-color: transparent !important;
    width: 100% !important;
}
.checkbox label:hover::before {
    border: 1px solid #ff5252;
}
.checkbox input[type="checkbox"]:checked+label::before{
    background-color: #ff5252;
    border: 1px solid #ff5252;
}
.rbx-select-group .rbx-select:hover{
    background-color: #ff5252;
}
.notification-stream-header .text-link{
    color: #EA6E6E
}
.text-name.small, .text-name.small:link, .text-name.small:visited, .text-name.small:active, .text-name.xsmall, .text-name.xsmall:link, .text-name.xsmall:visited, .text-name.xsmall:active, .text-name.xxsmall, .text-name.xxsmall:link, .text-name.xxsmall:visited, .text-name.xxsmall:active {
    color: #EA6E6E;
}
.notification-stream-list .notification-stream-item:hover {
    box-shadow: 4px 0 0 0 #ff5252 inset;
}
.input-group-btn .input-dropdown-btn:hover, .input-group-btn .input-dropdown-btn:focus {
    background-color: #ff5252;
    border-color: #ff5252;
}
.input-field:focus {
    box-shadow: none;
    border-color: #ff5252 !important;
}
}
