    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:hover {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:active {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
/* MASTER HEAD - Sidebar Button Arrow */
.not-yt-legacy-css #masthead-positioner #appbar-guide-button:hover .yt-uix-button-arrow, .yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
  border-top-color: #FFF;
}
.not-yt-legacy-css #masthead-positioner #appbar-guide-button .yt-uix-button-arrow, .yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
  border-top-color: #FFF;
}
/* MASTER HEAD - Region Abreviation Text Color */
#yt-masthead #logo-container span.content-region {
    color: #FFFFFF;
}
/* Full Page Alert (Added 12-8-15) */
.yt-alert-default.yt-alert-info, .yt-alert-actionable.yt-alert-info, .yt-alert-naked.yt-alert-info .yt-alert-icon, .yt-alert-small.yt-alert-info {
    background: #7500AB;
    border-bottom: 1px solid #e8e8e8;
}
/* MASTER SIDEBAR - Active Page */
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover{
  background: #7500AB;
}

/* MASTER SIDEBAR - Section Headings */
li.guide-section h3 a {
  color: #7500AB;
}
/* Page Navagation Tabs */
a.yt-uix-button-epic-nav-item.partially-selected:hover, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading{
  border-color: #7500AB;
}
.feed-header-feed-filter .yt-uix-button-epic-nav-item.selected, .feed-header-feed-filter .yt-uix-button-epic-nav-item:hover{
    border-color: #7500AB;
}
a.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled{
    border-color: #7500AB;
}
/* PLAYER - Progress Bar */
.ytp-play-progress {
  background: #7500AB;
}
.ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
    background-color: #7500AB;
}
/* TRANSPERENT PLAYER - Anotations & Auto Play Slider Buttons */
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
  background: #7500AB;
}
/* TRANSPERENT PLAYER - Volume Slider */
.ytp-volume-slider-track {
    background: #7500AB;
}
/* TRANSPERENT PLAYER - Other Stuff */
.ytp-settings-button.ytp-hd-quality-badge:after, .ytp-settings-button.ytp-4k-quality-badge:after, .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-settings-button.ytp-8k-quality-badge:after{
  background-color: #7500AB;
}
.ytp-chrome-controls .ytp-button-with-box:after{
  background-color: #7500AB;
}
.ytp-menuitem sup {
  color: #7500AB;
}
/* OLD PLAYER - Scrubing Button*/
.html5-progress-bar:focus .html5-scrubber-button, .html5-scrubber-button:active, .html5-scrubber-button:hover {
  background: #7500AB;
}
/* OLD PLAYER - Volume Slider*/
.ytp-volume-slider-foreground:before {
  background: #7500AB;
}

/* WATCH PAGE - AutoPlay Slider Button */
.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
  background-color: #7500AB;
}
/* WATCH PAGE - Like Scale */
.video-extras-sparkbar-likes {
  background: #7500AB;
}
/* CREATOR PAGES - Tabs */
.creator-editor-nav-tabs li.selected, .creator-editor-nav-tabs li:hover,.epic-nav-item.selected,.epic-nav-item:hover,.menu-tab:hover, .menu-tab.selected {
  border-bottom: 4px solid #7500AB;
}
/* CREATOR PAGES - Sidebar Sub Menu (Also used in user settings) */
#creator-sidebar .creator-sidebar-item.selected, #creator-sidebar .creator-sidebar-submenu .creator-sidebar-item.selected a:hover{
background-color: #7500AB;
}
/* SITEWIDE - Blue Buttons */
.yt-uix-button-primary, .yt-uix-button-primary[disabled], .yt-uix-button-primary[disabled]:hover, .yt-uix-button-primary[disabled]:active, .yt-uix-button-primary[disabled]:focus {
  border-color: #7500AB;
  background: #7500AB;
}
/* WATCHPAGE Loading Progress Bar (Thanks to nalljya) */
#progress {
    background-color:white!important;
    box-shadow: 0 0 0 0;
}


}
