css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.inoreader.com") {
  .article_tile_picture {
    height: 150px;
  }
  
  .article_tile {
    margin: 30px 2%;
  }
  
  div.article_tile {
    width: 25%;
    max-width: 280px;
  }
  
  .tiles_wrapper {
    padding-top: 50px !important;
    padding-left: 6%;
    text-align: left;
  }
  
  .article_tile_picture {
    border-radius: 5px 5px 0px 0px;
  }
  
  .article_tile .article_title_link {
    font-size: 14px;
  }
  
  div.article_tile {
    border-radius: 5px;
  }
  
  div.article_tile.current {
    box-shadow: unset;
  }
  
  .article_unreaded .article_tile_content {
    color: #8F8D8D;
  }
  
  .article_tile_content {
    margin: 20px 0px 15px;
  }
  
  .article_tile_footer {
    padding: 3px 0px 0px 6px;
  }
  
  .article_subscribed {
    background-color: #FBFBFB;
  }
  .article_unreaded {
    background-color: white; 
  }
  
  #subscriptions_buttons, #sb_rp_tools {
    background-color: #F9F9F9;
    background-image:none;
  }
  #sb_tp_search_overlay {
    background-color: #F9F9F9;
    background-image:none;
  }
  .parent_div {
    line-height: 30px;
  }
  #subscriptions_nav {
    padding-bottom: 10px;
  }
  .dashboard_gadgets {
    padding: 12px 0px;
  }
  .gadget_overview_see_more, .gadget_overview_feed_title, .gadget_overview_article_content_wrapper {
    padding-left: 205px;
  }
  .gadget_overview_article_content_wrapper {
   padding-right: 20px; 
  }
  .dashboard_gadgets .header_name {
    text-transform: uppercase;
    font-weight: 400;
    font-size: 14px;
    letter-spacing: .5px;
  }
  A.bluelink:link {
    color: #7099D5;
  }
  .gadget_overview_feed_title {
    font-size: 18px;
    margin: 10px 0px 3px;
    line-height: 30px;
  }
  .gadget_overview_feed {
    margin: 5px 0px 30px 9px;
  }
  .gadget_overview_article_content_picture {
    top: 4px;
    width: 190px;
    height: 120px;
    border-radius: 5px;
    border: 1px #EAE7E7 solid;
  }
  .gadget_overview_article_title {
    margin: 3px 0px;
  }
  .gadget_overview_article_content {
    max-height: 60px;
  }
  .pin_container {
    display: block;
  }
}
