css:
@-moz-document domain("vara.nl") {
header .top-image {height:0px;}
header .logo {position: inherit !important;visibility:hidden;height:0px!important;}
.container-header {height:0px !important;}

h1, h2, h3, h4, h5, p, body {
    font: normal 14px/1.5 '', tahoma;
    color: #222 !important;
}

.ask-question, .user-search {height:150px !important;}

.row {
    width: 90%;
    margin-left: auto;
    margin-right: auto;
    margin-top: 0;
    margin-bottom: 0;
    max-width: 75rem;
}

.scrolltoTop {
    background: #535353 !important;
}

.row.row-filters select {
    background: #333 !important; }
.item-thumb .type-fragment,
.item-thumb .type-aflevering {
    background-color: yellow !important;
    color: black !important; }

.item-thumb .type:after { border-color: #a3175c transparent transparent;}

.main-content .answer-wanted,
.answer-wanted:before,
.answer-wanted:after {background:#e6e6e6 !important;}

.main-content .answer-question-field,
.main-content .answer-question-field.question-open:before,
.main-content .answer-question-field.question-open:after  {background:#e2e2e2 !important;}

.main-content .answer-question .answer-question-wrapper { border-bottom: 1px solid black; }

.main-content .new {background: #f5f5f5; }

.main-content .answer-top-rated { background: #FFFCE3;}
}
