    width: 85%;
    margin-left: auto;
    margin-right: auto
}
.forabg {
    align-content: center;
    background: #909090;
    border-left: 1px solid rgb(0, 0, 0);
    border-right: 1px solid rgb(0, 0, 0);
    border-bottom: 1px solid rgb(0, 0, 0);
    border-top: 1px solid rgb(0, 0, 0);
}
.forumbg,
.bg1,
.bg2,
ul.forums,
#tabs a {
    background: #E3E3E3;
}
.li.row:hover {
    background: #FFFFFF;
}
.li.row.dl {
    color: black;
}
}

