css:
/**
* JVNoël par LordArryn !
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.jeuxvideo.com") {
  
  /* Supprime les pubs */
  #prospect,
  .outbrain,
  #jv-feedback,
  #forum-right-col > div:not(.panel-jv-forum) {
    display: none !important;
  }
  
  /* Suppression du titre des forums */
  #bloc-meta-titre-jeu {
    display: none !important;
  }
  
  /**
  * Fond
  */
  #content {
    background-image: url("http://image.noelshack.com/fichiers/2015/49/1449348975-jvnoel.png") !important;
    background-color: #81101f !important;
  }
  
  /**
  * En-tête
  */
  /* Couleurs : #F0FFFF texte, #ba2c2c header sticky, #dfdfdf texte header sticky, #81101f header & content, #4b1614 divers */
    #jv-header::before {
    content: "" !important;
    background: repeat scroll 0 -295px url("http://image.noelshack.com/fichiers/2015/49/1449357218-large-horiz-long.png") !important;
    background-size: 10px 50px;
    z-index: 50 !important;
    position: fixed !important;
    width: 950px !important;
    height: 75px !important;
    top: 0;
    left: 0;
  }
  
  #header-links,
  #header-search-jv,
  #header-store-jv {
    display: none !important;
  }
  
  #header-top {
    background: none repeat scroll 0 0 #81101f !important;
    color: #F0FFFF !important;
  }
  
  #header-logo::after {
    content: "Joyeux Noël !" !important;
    position: absolute !important;
    left: 255px !important;
    top: 18px !important;
    width: 200px !important;
    font-size: 1.3em !important;
    font-style: italic !important;
  }
  
  #header-user .menu-compte-head .nom-alias,
  #header-user .menu-compte-head .lib-txt {
    color: #4b1614 !important;
  }
  
  #header-user .menu-compte-head li.border-b,
  #header-user .menu-compte-head li {
    border-bottom: 1px solid #dfdfdf !important;
  }
  
  #header-user .menu-compte-head {
    background: none repeat scroll 0 0 #ba2c2c !important;
  }
  
  #header-user,
  .nom-head-avatar {
    color: #F0FFFF !important
  }
  
  #header-user > *,
  .pull-left > * {
    padding-right: 5px;
  }
  
  #header-user::before {
    z-index: 10 !important;
    content: "" !important;
    background: url("http://image.noelshack.com/fichiers/2015/49/1449349481-boule-de-noel2.png") no-repeat scroll center !important;
    background-size: 50px 50px !important;
    position: absolute !important;
    left: -75px !important;
    width: 50px !important;
    height: 50px !important;
  }
  
  /************** Header sticky ****************/
  /*Suppression du houx
  .bloc-sticker::before {
    content: "" !important;
    background: repeat scroll 0 0 url("http://rosatubes.r.o.pic.centerblog.net/07a65ff8.png") !important;
    background-size: 135px 85px !important;
    z-index: 100 !important;
    position: absolute !important;
    top: -10px !important;
    left: 30px !important;
    width: 135px !important;
    height: 85px !important;
  }*/
  
  #header-bottom,
  .bloc-sticker {
    background: none repeat scroll 0 0 #ba2c2c !important;
  }
  
  .hit-parade-menu-item {
    background: none repeat scroll 0 0 #4b1614 !important;
  }
  
  #navbar-jv .liste-rubrique > li.open {
    background: none repeat scroll 0 0 #4b1614 !important;
  }
  
  #navbar-jv .content-menu-jv {
    border-color: #4b1614 !important;
  }
  
  .menu-liste-machine * {
    color: #dfdfdf !important;
  }
  
  .bloc-menu-navbar {
    color: #F0FFFF !important;
  }
  
  .content-menu-jv,
  .dropdown-menu {
    background:  none repeat scroll 0 0 #81101f !important;
  }
  
  .mdd-titre {
    color: #dfdfdf !important;
  }
  
  .mdd-liste-one li .mdd-a:not(.big-one) {
    color: #dfdfdf !important;
    border-bottom: 1px dotted #dfdfdf !important;
  }
  
  .mdd-liste-one li .mdd-a:not(.big-one):hover {
    color: #F0FFFF !important;
  }
  
  .mdd-liste-one li .big-one {
    color: #dfdfdf !important;
    font-weight: bold !important;
  }
  
  .mdd-liste-one li .big-one:hover {
    color: #ba2c2c !important;
    font-weight: none !important;
  }
  
  .mdd-liste-one li .big-one,
  .mdd-titre.mit-border {
    border-bottom: 1px solid #a5a5a5;
  }
  
  .mdd-liste-card-l .wrapper-titre-item {
    background: #4b1614 none repeat scroll 0% 0% !important;
    opacity: 0.9 !important;
  }
  
  .mdd-liste-card-l .mtag,
  .mdd-liste-card-l .mtag, .mdd-card-xl .mtag {
    background: #248145 none repeat scroll 0% 0% !important;
  }
  
  .play-on-big {
    display: none !important;
  }
  
  .mdd-recherche input[type="text"],
  .mdd-recherche button {
    background: #ba2c2c none repeat scroll 0 0 !important;
    border: none !important;
    color: #dfdfdf !important;
  }
  /*********************************************/
  /**
  * Forums
  */
  /* Couleurs : #81101f rouge fond ; #ba2c2c rouge clair ; #4b1614 rouge foncé ; #248145 vert ; #dfdfdf gris clair */
  .container-content {
    background: url("http://chezkarine.com/images/background-snow.png") !important;
    background-size: 100% !important;
  }
  
  .fil-ariane-crumb::after {
    content: "/ JVNoël par LordArryn" !important;
    color: #ba2c2c !important;
  }
  
  .titre-head-bloc .titre-bloc {
    padding-left: 50px !important;
  }
  
  .titre-head-bloc .titre-bloc::before,
  .titre-head-bloc-forum .titre-bloc-forum::before {
    content: "" !important;
    position: absolute !important;
    left: -10px;
    top: -5px;
    background: url("http://ageheureux.a.g.pic.centerblog.net/tube-cadeaux-de-noel.png") !important;
    background-size: 70px 62px !important;
    width:  70px !important;
    height: 62px !important;
  }
  
  .titre-bloc,
  .bloc-header-form,
  .titre-bloc-forum {
    color: #248145 !important;
    background: none !important;
    padding: 0 !important;
  }
  
  .titre-head-bloc-forum,
  .titre-head-bloc {
    border-top: 2px solid #dfdfdf !important;
  }
  
  .panel-jv-forum > .panel-heading {
    background-color: #ba2c2c !important;
  }
  
  .panel-jv-forum > .panel-heading + .panel-body {
    background-color: #4b1614 !important;
  }
  
  .titre-info-fofo {
    border-bottom: 1px solid #dfdfdf !important;
  }
  
  .nicescroll-rails {
    background-color: #81101f !important;
  }
  
  .nicescroll-rails div {
    background-color: #ba2c2c !important;
  }
  
  .bloc-sondage {
    background-color: #ba2c2c !important;
  }
  
  .back-barre {
    background-color: #81101f !important;
  }
  
  .bloc-sondage .tab-choix .back-barre span {
    background-color: #4b1614 !important;
  }
  
  .bloc-sondage .tab-choix .pourcent {
    background-color: #81101f !important;
  }
  
  .titre-head-bloc-sondage .titre-bloc {
    color: #FFBF00 !important;
  }
  
  .bloc-sondage .pied-result {
    color: #FFBF00 !important;
    border-top: 1px solid #dfdfdf !important;
  }
  
  .titre-head-bloc-sondage {
    border-bottom: 1px solid #dfdfdf !important;
  }
  
  *[src="/img/forums/topic-dossier2.png"] {
    background: url("http://image.noelshack.com/fichiers/2015/49/1449349481-boule-de-noel2.png") no-repeat scroll center !important;
    background-size: 17px 15px !important;
    width: 17px !important;
    height: 15px !important;
    padding-bottom: 15px !important;
  }
  
  *[src="/img/forums/topic-dossier1.png"] {
    background: url("http://chezkaradeg22.c.h.pic.centerblog.net/8e7ce553.png") no-repeat scroll center !important;
    background-size: 17px 15px !important;
    width: 17px !important;
    height: 15px !important;
    padding-bottom: 15px !important;
  }
  
  .bloc-liste-num-page .page-active {
    background-color: #ba2c2c !important;
  }
  
  .titre-topic a {
    color: #41303f !important;
  }
  
  .titre-topic a:hover {
    color: #ba2c2c !important;
  }
  
  .titre-topic a:visited {
    color: #aba0a0 !important;
  }
  
  /* #81101f rouge fond
     #ba2c2c rouge clair
     #4b1614 rouge foncé
     Boutons */
  .btn-actu-new-list-forum,
  .btn-poster-msg,
  #diff {
    background-color: #ba2c2c !important;
    color: #dfdfdf !important;
    border: 1px solid #4b1614 !important;
    transition: .2s !important;
  }
  
  .btn-actu-new-list-forum:hover,
  .btn-poster-msg:hover,
  #diff:hover {
    background-color: #81101f !important;
    color: #FFF !important;
  }
  
  .btn-repondre-msg,
  .btn-ajouter-sondage,
  .btn-lock-topic,
  .btn-topic-resolu {
    background-color: #FFC926 !important;
    border: 1px solid #248145 !important;
    color: #dfdfdf !important;
    transition: .2s !important;
  }
  
  .btn-repondre-msg:hover,
  .btn-ajouter-sondage:hover,
  .btn-lock-topic:hover,
  .btn-topic-resolu:hover {
    background-color: #248145 !important;
    color: #FFF !important;
  }
  
  .btn-sondage-forum {
    background-color: #81101f !important;
    border: none !important;
  }
  
  /*fond forum / posts / MP
  #ffebf2 rouge clair inutilisé
  #ebfff2 vert clair*/
  #table-liste-topic-forum tbody tr:nth-child(2n),
  .list-msg tbody tr:nth-child(2n),
  .bloc-message-forum:nth-child(2n){
    background: #fffeff none repeat scroll 0% 0% !important;
  }
  
  #table-liste-topic-forum tbody tr:nth-child(2n+1),
  .list-msg tbody tr:nth-child(2n+1),
  .bloc-message-forum:nth-child(2n+1) {
    background: #ebfff2 none repeat scroll 0% 0% !important;
  }
  
  /**
  * MP
  */
  #mp-menus .nouveau-msg a,
  .list-msg th,
  #discussion h1, #discussion .titre-discut {
    background: #248145 none repeat scroll 0 0 !important;
  }
  
  /**
  * Footer
  */
  .go-haut-page {
    background: url("http://www.wohlfahrt.com/bestofchristmas/out/pictures/generated/product/1/1000_800_75/185802.png") !important;
    background-size: 50px 50px !important;
    width: 50px !important;
    height: 50px !important;
  }
  
  #jv-footer {
    position: relative !important;
  }
  
  #jv-footer .liens-pied {
    background-color: #7E5835 !important;
    border-top: 5px solid #ba2c2c !important;
  }
  
  #jv-footer .liens-pied ul {
    border-bottom: 1px solid #842E1B !important;
  }
  
  #jv-footer .liens-pied .a-footer {
    color: #44a165 !important;
  }
  
  #jv-footer .liens-pied .a-footer:hover {
    color: #F0FFFF !important;
  }
  
  #jv-footer .liens-pied p {
    border-top: 1px solid #7E3300 !important;
    color: #F0FFFF !important;
  }
  
  #jv-footer .liens-pied::before {
    content: "" !important;
    position: absolute !important;
    bottom: 0 !important;
    left: 75px !important;
    background: url("http://image.noelshack.com/fichiers/2015/49/1449403478-mod-article810022-362.png") !important;
    background-size: 123px 192px !important;
    width: 123px !important;
    height: 192px !important;
  }
  
  #jv-footer .liens-pied::after {
    content: "" !important;
    position: absolute !important;
    bottom: 0 !important;
    right: 75px !important;
    background: url("http://img-fotki.yandex.ru/get/4713/112424586.209/0_7f78f_b9ab6b4d_L.jpg") !important;
    background-size: 150px 147px !important;
    width: 150px !important;
    height: 147px !important;
  }
  
}
