css:
@-moz-document domain("jvforum.fr") {
div.topic__label.topic__label--marque-off {
background: url('http://image.noelshack.com/fichiers/2015/49/1449092764-btn-epingl-lock.png') no-repeat !important;
}

div.topic__label.topic__label--marque-on {
background: url('http://image.noelshack.com/fichiers/2015/49/1449409264-btn-epingl.png') no-repeat !important;
}

div.topic__label.topic__label--dossier2 {
background: url('http://image.noelshack.com/fichiers/2015/49/1449409264-btn-topic-rouge.png') no-repeat !important;
}

div.topic__label.topic__label--dossier1 {
background: url('http://image.noelshack.com/fichiers/2015/49/1449409264-btn-topic.png') no-repeat !important;
}

div.topic__label.topic__label--resolu {
background: url('http://image.noelshack.com/fichiers/2015/49/1449409264-btn-resolu.png') no-repeat !important;
}

div.topic__label.topic__label--lock {
background: url('http://image.noelshack.com/fichiers/2015/49/1449409264-btn-lock.png') no-repeat !important;
}

.topic__label {
width: 40px !important;
height: 40px !important;
border-radius: 50% !important;
-moz-border-radius: 50% !important;
-webkit-border-radius: 50% !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
margin-right: 10px !important;
font-family: 'Roboto', sans-serif !important;
}

.topic--small, .topic {
height: 60px !important;
display: block !important;
margin-bottom: 16px !important;
padding-top: 10px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
background-color: #424242 !important;
color: #EEE !important;
}

.liste-topics {
background: none !important;
border: none !important;
box-shadow: 0 0px 0px rgba(0, 0, 0, 0) !important;
}

.sheet {
background: #303030 !important;
}

.page-title__link, .page-title--topic {
color: #EEE !important;
font-family: 'Roboto', sans-serif !important;
}

.page-title__link::before {
content: "Forum: " !important;
}

.page-title {
text-shadow: none;
}

.header {
background: #263238 !important;
color: #EEE !important;
}

.header__home-link, .header__forum-link {
color: #EEE !important;
border: none !important;
}

.header__forum-cell {
background: #263238 !important;
color: #EEE !important;
}

.aside__top-button-label {
color: #EEE !important;
}

.menu {
background-color: #424242 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
}

.menu__item {
color: #EEE !important;
}

.menu__item + .menu__item {
border-top: 1px solid #484848 !important;
}

.button--large, .button--raised, .button, .button--scale {
background-color: #424242 !important;
color: #EEE !important;
}

.topic__text-container {
width: 100% !important;
}

.topic__nb-answers::before {
content: "Messages: " !important;
}

.topic__date::before {
content: "Dernier: " !important;
}

.form {
background: none !important;
border: none !important;
padding: 0px !important;
}

.form__topic, .form__textarea {
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
background-color: #424242 !important;
color: #B8B8B8 !important;
border: none !important;
}

.pagination-forum .suivant a, .pagination-forum .precedent a {
color: #B8B8B8 !important;
}

.pagination-forum .page span {
background-color: #263238 !important;
color: #EEE !important;
}

.message__content {
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
background-color: #424242 !important;
color: #FFF !important;
border: none !important;
padding-top: 0.1px !important;
padding-bottom: 0.1px !important;
}

.message__byline-date-inline {
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
background-color: #263238 !important;
color: #B8B8B8 !important;
border: none !important;
}

.message__byline-author-pseudo {
color: #B8B8B8 !important;
font-family: 'Roboto', sans-serif !important;
}

.message__byline-author-avatar-image {
border-radius: 50% !important;
-moz-border-radius: 50% !important;
-webkit-border-radius: 50% !important;
}

.pagination-topic__page-link--active {
background-color: #263238 !important;
color: #EEE !important;
font-family: 'Roboto', sans-serif !important;
}

.pagination-topic__page-link {
color: #B8B8B8 !important;
}

.pagination-topic__page-link:hover {
color: #B8B8B8 !important;
background: none !important;
}

.message__content-text > .quote {
background: rgba(38, 50, 56, 0.5) !important;
}

.message__content-edit-mention {
color: rgba(184, 184, 184, 0.5) !important;
}

img.js-profile-avatar.profile-avatar.profile-avatar--loaded {
height: 100px !important;
width: 100px !important;
border-radius: 50% !important;
-moz-border-radius: 50% !important;
-webkit-border-radius: 50% !important;
}

body.body--no-bottom.body--sheet {
background: #303030 !important;
}

.profile-card--transparent, .profile-card {
background-color: #424242 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
border: none !important;
}

.button-link {
color: #B8B8B8 !important;
}

.card__header {
color: #B8B8B8 !important;
}

.profile-card--transparent a, .message__content a {
color: #B8B8B8 !important;
}

.topic__text-container {
display: table-cell !important;
}

.topic__title--desktop, .topic__author--desktop, .topic__nb-answers--desktop, .topic__date--desktop {
display: none !important;
}

body {
background: #303030 !important;
}

.connexion-bloc {
background-color: #424242 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
border: none !important;
}

.connect-form__input {
background-color: #263238 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
border: none !important;
}

.connect-form__captcha-alternative {
font-family: 'Roboto', sans-serif !important;
color: #B8B8B8 !important;
}

.connexion-disclaimer {
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
}

.connexion-disclaimer a {
font-family: 'Roboto', sans-serif !important;
color: #B8B8B8 !important;
}

.connect-form__submit {
background-color: #263238 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
border: none !important;
}

.article {
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
}

.article a {
font-family: 'Roboto', sans-serif !important;
color: #B8B8B8 !important;
}

.article__cta {
background-color: #263238 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
border: none !important;
}

.article__subheadline-number {
background: #263238 !important;
}

.rechercheforum-q {
background-color: #424242 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
border: none !important;
}

.validate {
background-color: #424242 !important;
margin-bottom: 16px !important;
box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37) !important;
border-radius: 2px !important;
font-family: 'Roboto', sans-serif !important;
color: #FFF !important;
border: none !important;
border-radius: 50% !important;
-moz-border-radius: 50% !important;
-webkit-border-radius: 50% !important;
height: 32px !important;
width: 32px !important;
}

.homepage-links a, .logout-container a {
color: #B8B8B8 !important;
}
}
