css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://gzgreg.github.io/DerivativeClicker/") {
  body {
    background-color: #201F1D;
  }
  
  #navbar {
    color: #DCDCDC;
  }
  
  #navbar a {
    color: #868685;
    text-decoration: underline;
  }
  
  #container {
    color: #DCDCDC;
  }
  
  #moneyButton {
    background-color: #201F1D;
    border-color: #cc8400;
    color: #DCDCDC;
  }
  
  #tabs .ui-tabs-nav .ui-state-default a {
    color: #868685;
  } 
  
  #tabs .ui-tabs-nav .ui-state-active a {
    color: #DCDCDC;
  }
  
  #inventory {
    color: #DCDCDC;
  }
  
  .button {
    background-color: #201F1D;
    border-color: #7a4f00;
    color: #585858;
  }
  
  .buttonLit {
	  background-color: #201F1D;
    border-color: #cc8400;
  }
  
  .buttonLit:hover {
    background-color: #201F1D;
    border-color: #FFB732;
  }
  
  #bottom {
    color: #DCDCDC;
  }
  
  #numToBuy, #numToBuy2 {
    background-color: #201F1D;
    color: #DCDCDC;
  }
  
  .strong {
    text-decoration: none;
  }
  
  .ui-widget input, .ui-widget select{
    border-width: 1px;
    border-color: #CC8400;
    border-style: solid;
    background-color: #201F1D;
    color: #DCDCDC;
    border-radius: 5px;
  }
  
  .ui-widget-content {
    color: #DCDCDC;
  }  
  
  #achievementContainer .achievement {
    border-color: #a36900;
    padding: 1px 2px;
  }
}
