css:
/* CandyUI Tinyboard Dark Theme v4.1 by Leopardnell*/
/* Please rate this theme if liked*/
/* If you want to past this code directly, please remove the line above*/
@-moz-document domain("8ch.net"), domain("55chan.org"), domain("dogolachan.org") {
/*Global Styles*/
    body {
        background: #263238;
        color: #FAFAFA;
        font-family: Source Sans Pro, sans-serif;
        font-size: 9.5pt;
        margin: 0 5px;
        padding-left: 5px;
        padding-right: 5px;
    }
    div#wrap {
        width: 900px;
        margin: 0 auto;
    }
    hr{
        border: none;
    }
    form hr{
        border: 1px dashed #455A64 !important;
        margin-bottom: 15px;
    }
    /* Scrollbar */
    ::-webkit-scrollbar {
        width: 10px;
}
    ::-webkit-scrollbar-track {
            background: #455A64;
}
    ::-webkit-scrollbar-thumb {
        background: #607D8B; 
}
    
/*Links and Texts*/
    a, a:visited {
        text-decoration: none !important;
        color: #00BCD4;
    }
    a:hover,.intro a.post_no:hover {
        color: #26C6DA;
    }
    a.post_no {
        text-decoration: none;
        margin: 0;
        padding: 0;
    }
    a.hide-thread-link {
        color: #ec407a;
    }
    p.intro a.post_no {
        color: inherit;
    }
    p.intro a.post_no, p.intro a.email, p.intro a.post_anchor {
        margin: 0;
    }
    .intro a.email span.name {
        color: #FFC107;
    }
    p.intro a.email span.name{
        color: #EF5350;
    }
    .intro a.email:hover span.name {
        color: #ff0000;
    }
    p.intro label {
        display: inline;
    }
    h1 {
        font-family: Source Sans Pro, sans-serif;
        letter-spacing: -2px;
        font-size: 20pt;
        margin: 0;
    }
    h2 {
        color: #AF0A0F;
        font-size: 11pt;
        margin: 0;
        padding: 0;
    }
    header {
        margin: 1em 0;
    }
    header div.subtitle {
        font-size: 8pt;
        color: #fafafa;
    }
    header h1 {
        color: #BA68C8;
        text-align: center;
    }
    span.trip {
        color: #228854;
    }
    span.quote, .quote {
        color: #3EA961;
    }
    span.omitted {
        display: block;
        margin-top: 1em;
        color: #ddd;
    }
    br.clear {
        clear: left;
        display: block;
    }
    span.controls {
        float: right;
        margin: 0;
        padding: 0;
        font-size: 80%;
    }
    span.controls.op {
        float: none;
        margin-left: 10px;
    }
    span.controls a {
        margin: 0;
    }
    span.heading {
        color: #c83838;
        font-size: 12pt;
        font-weight: 600;
            text-transform: uppercase;
    }
    span.spoiler {
        background: #ddd;
        color: #ddd;
        padding: 0 1px;
    }
    span.spoiler:hover,div.post.reply div.body span.spoiler:hover a {
        color: #212121;
    }
    span.public_ban {
        display: block;
        color: red;
        font-weight: bold;
        margin-top: 15px;
    }
    span.toolong {
        display: block;
        margin-top: 15px;
        color: #B0BEC5;
    }
    span.ommited{
      color: #d1d1d1;
    }
    
/*Navbar*/
    .desktop-style div.boardlist:nth-child(1) {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        margin-top: 0;
        padding: 6px;
        z-index: 30;
        box-shadow: 0 1px 3px rgba(38, 50, 56, 0.9);
        border-bottom: none;
        font-weight: 600;
        background-color: #37474F;
        text-align: center;
    }
    .desktop-style div.boardlist:nth-child(1).cb-menu {
        margin-top: 0px;
        margin-left: -5px;
        width: auto;
        border-radius: 2px;
        box-shadow: 0 1px 2px rgba(0, 0, 0, .15);
        border: none;
    }
    .cb-menuitem span {
        padding: 10px;
        display: table-cell;
        text-align: left;
        border: none;
    }
    .desktop-style body {
        padding-top: 20px;
    }
    .desktop-style .sub {
        background: inherit;
        margin: 0px;
    }
    .desktop-style .sub .sub {
        display: inline-block;
        text-indent: -9000px;
        width: 7px;
        background: none right center no-repeat;
    }
    .desktop-style .sub .sub:hover,.desktop-style .sub .sub.hover {
        display: inline;
        text-indent: 0;
        background: inherit;
    }

/* Boardlist */
    div.boardlist {
        color: #F5F5F5;
        font-size: 9.5pt;
        margin-top: 3px;
        font-weight: bold;
    }
    div.boardlist a {
        text-decoration: none;
        color: #fafafa;
    }
    div.boardlist a:hover {
        color: #26C6DA;
    }
    a.ytfile:hover {
      color: #26afda;
      text-decoration: none !important;
    }
    div.boardlist a:visited {
        color: #FFEB3B;
    }
    .compact-boardlist {
        padding: 3px 3px 0;
    }
    .compact-boardlist .cb-item {
        display: inline-block;
        vertical-align: middle;
    }

    .compact-boardlist .cb-icon {
        padding-bottom: 1px;
    }

    .compact-boardlist .cb-fa {
        font-size: 21px;
        padding: 2px;
        padding-top: 0;
    }

    .compact-boardlist .cb-cat {
        padding: 5px 6px 8px 6px;
    }
    .cb-menuitem {
        display: table-row;
    }

    .cb-menuitem span {
        padding: 5px;
        display: table-cell;
        text-align: left;
        border-top: 1px solid rgba(0,0,0,0.5);
    }

    .cb-menuitem span.cb-uri {
        text-align: right;
    }
    .boardlist:not(.compact-boardlist) #watch-pinned:before {
        content: " [ ";
    }
    .boardlist:not(.compact-boardlist) #watch-pinned:after {
        content: " ] ";
    }
    .boardlist:not(.compact-boardlist) #watch-pinned {
        display: inline;
    }
    .boardlist:not(.compact-boardlist) #watch-pinned a {
        margin-left: 3pt;
    }
    .boardlist:not(.compact-boardlist) #watch-pinned a:first-child {
        margin-left: 0;
    }
    .compact-boardlist #watch-pinned {
        display: inline-block;
        vertical-align: middle;
    }
    
/*PostForm*/
    form table tr th {
        text-align: left;
        font-size: 10pt;
        border: none;
        padding: 2px 10px;
        background-color: #AB47BC;
    }
    
/*Mediauploader*/
    .dropzone {
        color: #fff;
        cursor: default;
        margin: auto;
        padding: 0px 4px;
        text-align: center;
        min-height: 50px;
        max-height: 140px;
        transition: 0.2s;
        background-color: #B0BEC5;
        overflow-y: auto;
    }
    .dropzone-wrap {
        width: 100%;
    }
    .dropzone .file-hint {
        color: #607D8B;
        transition: 0.2s;
        border: 2px dashed #90A4AE;
    }
    .file-hint:hover, .dropzone.dragover .file-hint {
        color: #FAFAFA;
        border: 2px dashed #607D8B;
    }
    .dropzone.dragover {
        background-color: rgba(200, 200, 200, 1);
    }
    .dropzone .file-thumbs {
        text-align: left;
        width: 100%;
    }
    .dropzone .tmb-container {
        padding: 3px;
        overflow-x: hidden;
        white-space: nowrap;
    }
    .dropzone .file-tmb {
        height: 40px;
        width: 70px;
        cursor: pointer;
        display: inline-block;
        text-align: center;
        background-color: rgba(187, 187, 187, 0.5);
        background-size: cover;
        background-position: center;
    }
    .dropzone .file-tmb span {
        font-weight: 600;
        position: relative;
        top: 13px;
    }
    .dropzone .tmb-filename {
        display: inline-block;
        vertical-align: bottom;
        bottom: 12px;
        position: relative;
        margin-left: 5px;
    }
    .dropzone .remove-btn {
        cursor: pointer;
        color: rgba(125, 125, 125, 0.5);
        display: inline-block;
        vertical-align: bottom;
        bottom: 10px;
        position: relative;
        margin-right: 5px;
        font-size: 20px
    }
    .dropzone .remove-btn:hover {
        color: rgba(125, 125, 125, 1);
    }
/*Blotter*/
    div.blotter{
        color: #ba68c8;
        border: 1px solid #ab47bc;
        padding: 5px;
        margin-bottom: 15px;
        border-radius: 1px;
        font-weight: 500;
    }

/*Shitposts*/
    .post-menu ul {
        background-color: #546E7A;
        border: 1px solid #607D8B;
    }
    .post-menu li:hover {
        background-color: #607D8B;
    }
    div#watchlist {
        background: #607D8B !important;
        border: 1px solid #F5F5F5 !important;
    }
    .optionsContainer {
        border-bottom: 1px dashed #607D8B;
        margin-bottom: 15px;
    }
    .intro span.subject {
        color: #7E57C2;
        font-weight: bold;
    }
    .intro span.name {
        color: #4DB6AC;
        font-weight: 600;
    }
    .intro time,p.intro a.ip-link,p.intro a.capcode {
        direction: ltr;
        unicode-bidi: embed;
        color: #E0E0E0;
    }
    .intro span.capcode,p.intro a.capcode,p.intro a.nametag {
        color: #F00000;
        margin-left: 0;
    }
    p.intro a.post_no {
        color: #ddd;
    }
    .mentioned, .unimportant{
      font-size: 12px !important;
    }
    .intro a {
        margin-left: 8px;
    }
    .intro.thread-hidden {
        margin: 0;
        padding: 0;
    }
    .unimportant, .unimportant * {
        font-size: 11px;
    }
    a.unimportant{
        text-decoration: none;
    }
    .file {
        float: left;
        margin-right: 2px;
    }
    .file:not(.multifile) .post-image {
        float: left;
    }
    .file:not(.multifile) {
        float: none;
    }
    p.fileinfo {
        display: block;
        margin: 0 0 0 20px;
    }
    div.post p.fileinfo {
        padding-left: 5px;
    }
    div.post i.fa{
        color: #EF5350;
    }
    .post-image {
        box-shadow: 0 1px 5px rgba(0, 0, 0, .45);
        background: #F5F5F5;
        margin:  6px 5px 6px 6px;
    }
    .full-image {
        padding: none;
        margin: 20px;
        box-shadow: 0 1px 5px rgba(0, 0, 0, .45);
    }
    div.post div.body {
        word-wrap: break-word;
        white-space: pre-wrap;
    }
    .postMessage{
        margin: 5px !important;
    }
    div.post.reply {
        background: #37474F;
        margin: 4px 4px;
        padding: 2px;
        border:  1px #455A64;
        border-style: none none solid none;
        display: inline-block;
        max-width: 94%!important;
        border-radius: 2px;
    }
    div.post.reply.highlighted {
        background: #455A64;
        border-color: #00BCD4;
    }
    div.post-hover {
        box-shadow: 0 1px 5px rgba(0, 0, 0, 0.4) !important;
        border: 1px solid #FFEB3B !important;
    }
    div.post.reply p {
        margin: 0.3em 0 0 0;
    }
    div.post.reply p.intro{
        padding: 1px 5px 0px 2px;
    }
    div.post.reply div.body {
        margin: 0px;
            padding: 0px;
    }
    .postMessage{
        display: inline-block;
    }
    div.post.reply div.body span.spoiler a {
        color: black;
    }
    div.post.reply div.body a {
        color: #FFC107;
    }
    div.post video.post-image {
        margin: 5px 25px 5px 5px;
        padding: 0;
    }
    .new-posts {
        opacity: 0.6;
        margin-top: 1em;
    }

    .new-threads, .board-settings {
        text-align: center;
    }

/*Inputs*/
    inpunt{
        background-color: #fff;
        color: #263238 !important;
    }
    input[type="text"], input[type="password"], textarea{
        border: 1px solid #a9a9a9;
        color: #263238;
        border-radius: 1px;
    }
    input[type="submit"] {
        background-color: #b0bec5;
        color: #546E7A;
        cursor: pointer;
        text-align: center;
        padding: 3px 5px;
        display: inline-block;
        font-size: 10pt;
        border-radius: 1px;
        border: none;
        -webkit-transition-duration: 0.4s; /* Safari */
        transition-duration: 0.2s;
    }
    input[type="submit"]:hover{
        background-color: #546E7A;
        color: #b0bec5;
    }
    input[type="text"]:focus,
    input[type="password"]:focus,
    input:focus:not([type]),
    textarea:focus {
        border: 1px solid #BA68C8;
    }
    /*Custom Checkbox*/
    [type="checkbox"]:not(:checked),
    [type="checkbox"]:checked {
        position: relative;
        padding-left: 15px;
        cursor: pointer;
    }
    [type="checkbox"]:not(:checked):before,
    [type="checkbox"]:checked:before {
        content: '';
        position: absolute;
        left:0; top: 0px;
        width: 12px; height: 12px;
        border: 1px solid #9E9E9E;
        background: #f5f5f5;
        border-radius: 1px;
        box-shadow: inset 0 1px 3px rgba(0,0,0,.3);
    }
    [type="checkbox"]:not(:checked):after,
    [type="checkbox"]:checked:after {
        content: 'âœ”';
        position: absolute;
        top: 2px; left: 2px;
        font-size: 14px;
        line-height: 0.8;
        color: #607D8B;
        transition: all .5s;
    }
    [type="checkbox"]:not(:checked):after {
        opacity: 0;
        transform: scale(0);
    }
    [type="checkbox"]:checked:after {
        opacity: 1;
        transform: scale(1);
    }
    [type="checkbox"]:hover:before {
        border: 1px solid #00BCD4!important;
    }
    div.delete {
	   float: right;
	   margin-bottom: 10px;
    }

/*Pagenav*/
    div.pages{
        color: #fff;
        border: 1px solid #455A64;
        border-radius: 1px;
        padding: 5px;
        background: #37474f;
    }
    div.pages a.catalogPages{
        margin-left: 5px;
        border-left: 1px dashed #ffc107;
        padding: 5px 5px 5px 10px;
    }
    div.pages a.selected {
        color: #FFEB3B;
        font-weight: 600;
}

/* Bardlist Bottom */
    div.boardlist.bottom {
        margin-top: 20px;
        text-align: center;
        margin-bottom: 20px;
        border: 1px solid #455A64;
        border-radius: 1px;
        padding: 5px;
        background: #37474f;
    }
    p.elevator{
        color: #CDDC39;
    }

/*Warnings*/
/*Modules*/
    div.module, div.ban{
        background: #263238;
        border: 1px solid #CDDC39;
    }
    div.ban h2{
        background: #CDDC39;
        color: #263238;
    }
    fieldset {
    border: 1px dashed #607D8B;
    }

/*IBHome*/
    .box{
        background: #B0BEC5;
        border: none;
        color: #fff;
        margin: 0 0 5px 0
    }
    .box-title {
        background: #546E7A;
        color: #17191a;
        font-size: 120%;
        font-weight: 600;
        padding: 5px 5px;
    }
    .box-title a {
        color: #37474F;
    }
    .box-title small a{
        color: #F5F5F5 !important;
    }
    ul.box-content {
        background: #b0bec5;
        color: #546E7A
    }
    .description a{
        color: #fff !important; 
    }
    .velo-cell a{
        color: #546E7A !important;
        font-weight: 400;
    }
    table thead th{
        border: none;
        color: #263238;
        background: #546e7a;
    }
    li.tag-item a.tag-link{
        color: #607D8B;
    }
    tbody.board-list-tbody{
        background: #b0bec5;
    }
    table.board-list-table .board-uri a{
        color: #607D8B;
    }
    table.board-list-table .board-tags .board-cell:hover{
        background: #b0bec5;
    }
    table.board-list-table .board-tags .tag-link{
        color: #607D8B;
    }
    table.board-list-table .board-cell{
        color: #263238;
    }
    tbody.board-list-omitted td{
        font-size: 12pt;
        background: #ab47bc;
    }


/*Options*/
    div.styles {
        float: right;
        padding-bottom: 20px;
    }
    div.styles a {
        margin: 0 10px;
    }
    div.styles a.selected {
    text-decoration: none;
    }
    fieldset label {
      display: block;
    }
    #options_background, #alert_background{
        background: #37474F !important;
        opacity: 0.75 !important;
    }
    #options_div {
        background-color: #fff;
        border: 1px solid #17191a;
        display: inline-block;
        margin-top: 250px;
    }
    #alert_div{
        background: #00ACC1 !important;
        border: 1px solid #263238 !important;
    }
    #options_tablist {
        border: none;
        padding: 0px 5px;
    }
    #options_div textarea{
        color: #fff;
        background: #000;
    }
    #options_close i, #alert_close i{
        background: none;
        color: #17191a;
    }
    .options_tab {
        color: #37474F;
        background-color: #F5F5F5;
    }
    .options_tab h2 {
        border: none;
        color: #222;
    }
    .options_tab_icon {
        color: #607D8B;
    }
    .options_tab_icon.active {
        color: #FFF;
        background-color: #90A4AE;
        border-left: 3px solid #607D8B;
    }

/*Catalog*/
    .theme-catalog div.thread{
        margin: 5px;
        padding: 10px;
        background: #37474F;
        border-radius: 2px;
    }
    .theme-catalog div.thread:hover{
        background: #455A64;
    }
/*Quick Reply*/
    #quick-reply table {
        background: #fff !important;
        border: none !important;
        padding: 0px !important;
    }
    #quick-reply th {
        border: none;
        background: #ec407a;
        font-size: 11pt;
        text-transform: uppercase;
    }
    #quick-reply th .close-btn {
        float: right;
        font-size: 15pt;
        background: #fff;
        margin: 0px 2px;
        color: #ec407a;
        padding: 0 2px;
        width: 12px;
    }
    #quick-reply span.spoiler-images-option {
        display: inline-block;
        float: right;
        color: #17191a;
}
}
