css:
/* i really want this to be global */
#hdr {
    background: #333333;
    margin: 0;
    padding: 0;
    position: fixed;
    width: 100%;
    top: 0;
    left: 0;
    right: 0;
    z-index: 10000
}
#hdr h1.heading span.note a:link,
#hdr h1.heading span.note a:visited {
    margin: 0 2px;
    color: #ffffff
}
l.navigation.special li.selected a:link,
ul.navigation.special li.selected a:visited,
ul.navigation.special li.selected a:hover {
    background: #a69782;
    color: #fff;
    border-color: #a69782;
}
ul.navigation li a:link,
ul.navigation li a:visited {
    margin: 0 2px;
    color: #fee4e3;
    display: block;
    background: #333333;
    border: 1px solid #333333;
    text-decoration: none;
    padding: 15px 10px;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0
}
ul.navigation li a:hover {
    color: #ffffff
}
ul.navigation li.icon-only a {
    padding: 0;
    background: transparent;
    border-color: transparent
}
ul.navigation li.current a:link,
ul.navigation li.current a:visited,
ul.navigation li.current a:hover {
    background: #a69782;
    color: #fff;
    border-color: #a69782
}
#event_list li[data-program="mysqld"] .program a {
    color: #f08c8c;
}
#event_list li[data-program="pdns"] .program a {
    color: #f08cc1;
}
#event_list li {
    font-size: 12px;
}
#ftr #search input.text {
    font-size: 12px;
}
#event_list li time {
    color: #9999cc;
}
#event_list li .system a {
    color: #fee4e3;
}
#event_list li .program a {
    color: #78cf8a;
}
#event_list li .message {
    color: #e7d1b3;
}
#event_list li[data-severity="Error"] span.message {
    color: #f08cc1;
}
#event_list li[data-severity="Debug"] span.message {
    color: #78cf8a;
}
#event_list li[data-severity="Critical"] span.message {
    color: #ff0000;
}
#event_list li[data-severity="Emergency"] span.message {
    color: #ff0000;
}
#event_list li[data-severity="Informational"] span.message {
    color: #ff0000;
}
.ftr {
    background: #333333;
    border-top: 3px solid #fff;
    margin: 0;
    padding: 0;
    position: fixed;
    width: 100%;
    bottom: 0;
    left: 0
}
