css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("man7.org"), regexp('https?://www\\.kernel\\.org/doc/man-pages/.*') {
  /* images */
  img[src="http://www.google.com/logos/Logo_40wht.gif"] { height: 0 !important; width: 0 !important; padding-left: 85px !important; padding-top: 35px !important; background: url("http://i.imgur.com/HjT74DQ.png") no-repeat !important; background-size: contain !important; }
  
  /* highlight */
  span:hover { background-color: #000C47 !important; }
  
  /* basics */
  html, body { background-color: #000C17 !important; color: #777 !important; font-family: "Courier New" !important; }
  a { font-family: "Courier New" !important; }
  a:link { color: #00ACAF !important; background-color: transparent !important; }
  a:hover { background-color: transparent !important; color: #CCC !important; }
  a:visited { background-color: transparent !important; color: #AAA !important; }
  b { color: #F60 !important; }
  i { color: #0F0 !important; }
  strong { color: #AAA !important; }
  p { max-width: 100% !important; }
  pre { max-width: 100% !important; }
  hr { border-color: #00ACAF !important; }
  h1, h2, h3, h4, h5 { font-family: "Courier New" !important; }
  td { background-color: transparent !important; }
  
  /* headers */
  h1 { color: #AAA !important; }
  h2 { color: red !important; font-size: large !important; }
  
  /* search box */
  input { background-color: #000C17 !important; color: #00ACAF !important; border-color: #00ACAF !important; }
  body > center > form > a > img { height: 35px !important; }
  
  /* Nav bar */
  .nav-text { color: #777 !important; }
  div.nav-bar { background-color: #000C17 !important; }
  
  /* content */
  .headline, .footline { color: aqua !important; }
  
  /* footer */
  .footer { background-color: transparent !important; }
}
