css:
@-moz-document domain("builds.apache.org") {
    html {
        -webkit-filter: invert(100%) !important;
    }
    img,
    iframe {
        -webkit-filter: invert(100%) !important;
    }
    #header,
    #breadcrumbBar {
        background: #fff;
    }
    #side-panel {
        background: #ddd;
    }
    a,
    a:visited,
    .model-link,
    a:link {
        text-decoration: none;
        color: #957373;
    }
    a:hover,
    .model-link:hover {
        border-bottom: 1px solid #ccc;
    }
    #header .login a,
    #header .login a:visited {
        color: #222;
    }
    .inverse {
        color: #000;
    }
    #top-panel {
        background: #eee;
    }
    #search-box {
        border-color: #333;
    }
    #search-box:-webkit-input-placeholder {
        color: #ccc;
    }
    #search-box:-moz-placeholder {
        /* Firefox 18- */
        
        color: #ccc;
    }
    #search-box:-moz-placeholder {
        /* Firefox 19+ */
        
        color: #ccc;
    }
    #search-box:-ms-input-placeholder {
        color: #ccc;
    }
    .build-status-link img[title="In progress > Console Output"] {
        content: url('http://avanishpathak.com/uploads/ripple.gif')
    }
    .build-status-link img[title="Success > Console Output"],
    tr td [title="Success"] {
        content: url('http://findicons.com/files/icons/985/affel/128/tick.png')
    }
    .build-status-link img[title="Failed > Console Output"],
    tr td [title="Failed"] {
        content: url('https://cdn1.iconfinder.com/data/icons/ui-icons-2/512/wrong-01-128.png')
    }
    .job-index-headline.page-headline,
    #systemmessage {
        color: #953353;
    }
    #main-panel {
        background-color: #ddd;
        background: #ddd url('http://avanishpathak.com/uploads/jenkins-devil-invert.png') fixed;
        background-attachment: fixed;
        ;
        background-repeat: no-repeat;
        background-size: 212px 262px;
        background-position: bottom right;
    }
    #footer {
        background: #eee;
    }
    #jenkins-home-icon {
        content: url('http://avanishpathak.com/uploads/jenkins-orange-logo.png')
    }
    td.progress-bar-done {
        background-color: #A55373;
        border-radius: 2px;
    }
    table.progress-bar td {
        border-radius: 4px;
    }
    img[title="In progress"] {
        content: url('http://avanishpathak.com/uploads/squares.gif')
    }
    body table {
        background: #ccc;
    }
    html,
    body,
    #main-table {
        height: 100%;
    }
    #projectstatus tr:nth-child(even) {
        background: #ccc;
    }
    #projectstatus tr:nth-child(odd) {
        background: #ddd;
    }
    #projectstatus tr:nth-child(even):hover {
        background: #eee;
    }
    #projectstatus tr:nth-child(odd):hover {
        background: #eee;
    }
    .dashboard {
        opacity: 0.8;
    }
    #search-box {
        background: #FFF url('http://findicons.com/files/icons/1389/g5_system/32/toolbar_find.png') 15px 15px no-repeat;
        background-position: top 0px left 3px;
        background-size: 15px;
    }
    #login-field a {
        color: #333;
    }
}
