.modal2 .modal2-footer {
    background-color: #222;
}
.dlg-achieve-window .modal2-dialog p {
    margin-top: 30px;
    font-size: 15px;
    line-height: 1.5;
    color: #aaa;
}

	/* 	всплывающая приглашалка 	*/
.notification-bar,
#top-popup{
    position: absolute;
    left: 0;
    z-index: 109;
    width: 100%;
    font-size: 11px;
    background-color: #2E2F28;
    border-bottom: 1px solid #000;
}
	/* авторизация, и попап достижения */

.popup_message .content2,
#login-form{
    background: #2E2F28;
    padding: 5px;
    border: 1px solid #CFD0C2;
    border-radius: 8px;
    box-shadow: 0 0 20px -2px;

}
.popup-box .c{
    background: #2E2F28;
}

.popup-box .tl,
.popup-box .t,
.popup-box .tr,
.popup-box .l,
.popup-box .r,
.popup-box .bl,
.popup-box .b,
.popup-box .br{
    background:none;
}
	/*	выделение входящего непрочитанного сообщения 	*/
.profile-messages .messages-content .messages-contacts .contacts-content .contact.unread {
    background-color: #2E2F28 !important;
}

	/* юзер меню */
.userpanel .user-block .user-dropdown .active .name,
.userpanel .user-block .user-dropdown .active .mail {
    position: relative;
    background:  #2E2F28;
    color: #ccc;
    text-shadow: none;
    z-index: 111;
    height: 47px;
}
.userpanel .user-block .user-dropdown .active .name {
    border-right: 1px solid #000;
}
.userpanel .user-block .user-dropdown .dropmenu {
    z-index: 110;
    width: 300px;
    height: 250px;
    background: /*#2E2F28*/#2E2F28;
    position: absolute;
    top: 46px;
    right: 0;
    border-top-left-radius: 6px;
    border-bottom-left-radius: 6px;
    border-bottom-right-radius: 6px;
    border: 1px solid #000;
    border-top: 0;
    box-shadow: 0 1px 25px -5px rgba(0,0,0,.5);
}
.userpanel .user-block .user-dropdown .dropmenu a {
    font-size: 11px;
    font-weight: normal;
    text-decoration: underline;
    color: #ccc;
}
.userpanel .user-block .user-dropdown .dropmenu .col2 ul {
    border-left: 1px solid #000;
}
/*	?	*/
.userpanel {
    background: #aaa;
}
