css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.voz.vn") {

/* disable font boosting on chrome */
  html * {
    max-height: 999999px !important;
  }

  .page {
    min-width: 0px !important;
    width: 100% !important;
    max-width: 100% !important;
  }
  .neo_column.container {
    width: 100% !important;
  }
  .neo_column.main {
    max-width: 100% !important;
  }
 
  body, td, th, p, li, a.smallfont, div, a, .navbar strong, font[size="2"], textarea, input, font[size="2"] {
    font-size: /*[[font_size]]*/px !important;
  }
  
  font[size="1"] {
    font-size: calc(/*[[font_size]]*/px - 2px) !important;      
  }
  font[size="3"] {
    font-size: calc(/*[[font_size]]*/px + 2px) !important;      
  }
  font[size="4"] {
    font-size: calc(/*[[font_size]]*/px + 4px) !important;      
  }  
  font[size="5"] {
    font-size: calc(/*[[font_size]]*/px + 6px) !important;      
  }
  font[size="6"] {
    font-size: calc(/*[[font_size]]*/px + 8px) !important;      
  }  
  font[size="7"] {
    font-size: calc(/*[[font_size]]*/px + 10px) !important;      
  }
  
  a.bigusername {
    font-size: 50px !important;
    display: inline-block !important;
    max-width: 500px !important;
    text-overflow: ellipsis !important;
    overflow: hidden !important;
  }
  
  div[style="margin: 0 auto; padding-top: 10px; width: 970px; "] table {
    background: red !important;
  }
  
  .pagenav a {
    display: block !important;
    padding: 10px 35px !important;
  }
  
  .pagenav a:not([rel="prev"]):not([rel="next"]):not([title^="First"]):not([title^="Last"]), .pagenav strong {
    display: none !important;
  }
  
  .pagenav td {
    padding: 0px !important;
  }
      
  span[style="white-space:nowrap"] a strong, .alt2[nowrap="nowrap"] {
    white-space: normal !important;
  }
  
  .vbmenu_control {
    /*font-weight: normal !important;
    font-size: 40px !important;*/
  }
  
  div[style="white-space:nowrap; float: right;"] {
    float: none !important;
  }

  a[href="faq.php"], #community, a[href="calendar.php"], a[href="sssssearch.php?do=getnew"] {
    display: none !important;
  }
  
  td[id^="pagenav"] img[src$="images/misc/menu_open.gif"] {
    padding: 0px 20px !important;  
  }
  
  td[id^="td_threadstatusicon000"], td[id^="td_threadtitle000"], #threadbits_forum_14 tr000 {
    display: none !important;
  }
  
  a[id^="thread_title_"] + span {
    display: none !important;
  }
  
  #threadslist > tbody:first-child * {
    font-size: 25px !important;
  }
    
  .thead[width="150"], td[id^="td_threadtitle_"] + td > div, a[href^="member.php?find"] {
    white-space: normal !important;
    font-size: 20px !important;
  }

  .tcat {
    background: #869BBF !important;
  }
  .thead {
    background: #5C7099 !important;
  }
  
  div.smallfont[style="text-align: right; white-space: nowrap;"] {
    white-space: normal !important;
  }
    
  img[src="images/buttons/lastpost.gif"], img[src="images/buttons/firstnew.gif"] {
    width: 25px !important;
    padding: 10px !important;
  }
  
  div[style="margin-top: 10px"] img, td img[src*="images/buttons/reply.gif"] {
    height: 50px !important;
  }
  
  a[href^="report.php"] img {
    height: 40px !important;
  }
  
  #vozNgamGai img {
    height: 100px !important;
  }
  #vozNotification .titleWrapper {
    max-width: 500px !important;
  }

  /* chan qc */
  #neo_logobar, .middleads, TD[width="160"], div[id^="div-gpt-ad-"] {
    display: none !important;
  }

   .panelsurround .panel div[align="left"] {
    max-width: none !important;
    width: 100% !important;
  }
  
  .imagebutton img {
    height: 50px !important;
    width: auto !important;
  }
  .imagebutton img[src="clear.gif"] {
    width: 100% !important;
    height: 5px !important;
}

  .ocolor div {
    width: 50px !important;
    height: 50px !important;
  }  

  textarea {
    height: 10em !important;
  }
  
}

@-moz-document url("https://forums.voz.vn/usercp.php") {
  DIV:nth-child(3) > DIV > DIV > TABLE:nth-child(12) > TBODY > TR > TD:first-child {
    display: none !important;
  }
}

