css:
@-moz-document url-prefix("https://blogs.msdn.microsoft.com/oldnewthing"),
url-prefix("http://blogs.msdn.microsoft.com/oldnewthing"){
  /* Darker background for the site header */  
  #site-header-global-wrapper, #site-header-blog-wrapper {
    background: #998; 
  }
  
  #site-profile-menu {
    margin-bottom: 0px;
  }
  
  /* Title */
  .site-title {
    color: #FFF;
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 22px;
    font-weight: 700;
    text-decoration: underline;
  }
  
  /* Lighter background for the rest of the page */
  #page {
    background: #DDC;
  }
  
  /* Padding at the top of the overview */
  .posts-query-type, .page-header {
    margin-top: 10px;
  }
  
  /* Overview title (e.g. when displaying a certain tag) */
  .page-title {
    font-family: Verdana,Arial,Helvetica,sans-serif;
  }
  
  /* Entry title */
  .entry-title,
  .entry-title a {
    font-family: Verdana,Arial,Helvetica,sans-serif !important;
    font-size: 19.2px !important;
    font-weight: 700 !important;
    color: #700 !important;
  }
  
  /* Post date */
  time, .profile-usercard-hover, .commentmetadata a {
    color: #666;
  }
  
  /* No author needed - it's always Raymond :-) */
  .byline, .entry-meta > .avatar {
    display: none;
  }
  
  /* Tags */
  .tags a {
    color: #700;
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 16px;
    font-weight: 700;
    
    text-decoration: underline;
    text-decoration-color: #700;
  }
  
  /* Entry background and border */
  #primary {
    background: #FFE;
    border-width: 1px;
    border-color: #000;
    border-style: solid;
  }
  
  /* Fix some issues with the mobile layout */
  @media screen and (max-width: 767px) {
    /* Because we now have a visible border, use some minimal
     horizontal padding even for the mobile layout */
    #primary {
      padding: 0 5px;
    }
    
    .container-fluid {
      padding: 0 10px;
    }
    
    /* Replace the non-functional link back
    to the overview with the desktop version */
    #blog-logo * .for-desktop {
      display: initial;
    }
    
    #blog-logo * .for-mobile {
      display: none;
    }
    
    /* Reduce the indentation for nested comments */
    .comment-list .children {
      padding-left: 28px;
    }
  }
  
  /* Entry content */
  .entry-content.single,
  .entry-content {
    color: #000;
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 16px;
    line-height: 21px;
  }
  
  /* Hyperlink treatment */
  .entry-content.single a,
  .widget_text a,
  .msdn-archive-widget a,
  .tagcloud a {
    color: #700;
    font-weight: normal;
  }
  
  .entry-content.single a {
    text-decoration: underline;
  }
  
  a code {
    color: #700;
  }
  
  /* Comment area */
  #comments {
    background: white;
    color: #000;
  }
  
  /* Nav buttons */
  .page-numbers {
    background: #F1F1E9;
    background: linear-gradient(#FFF, #D1D1D1);
    line-height: 21px;
    padding: 0 7px;
    padding-bottom: 2px;
    
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 16px;
    font-weight: 400;
    color: #000;
    
    border-top-color: #F1F1E9;
    border-top-style: solid;
    border-top-width: 5px;
    margin-bottom: 5px;
    
    display: inline-block;
  }
  
  .page-numbers.current {
    background: #BBA;
    background: linear-gradient(#FFF, #D1D1D1);
    border-top-color: #BBA;
    border-top-style: solid;
  }
  
  /* Sidebar widgets */
  @media screen and (max-width: 767px) {
    .sidebar {
      margin-top: 0px !important;
    }
  }
  
  .widget {     
    margin-top: 20px;
    padding-bottom: 5px;
    border-width: 1px;
    border-color: #BBA;
    border-style: solid;
    
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 10.67px;
    font-weight: 400;
    line-height: 18px;
    color: #000;
  }
  
  .widget > * {
    padding: 0 10px;
  }

  .widget-title {
    margin-top: initial;
    padding: 8px 10px !important;
    background: #BBA;
    
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 12px;
    font-weight: 700;
  }
  
  /* The social widget has an additional div level between the main widget
     and its actual contents, so it needs some special treatment */
  .widget > .sfmsb-follow-social-buttons {
    padding: 0px;
  }
  
  .widget > .sfmsb-follow-social-buttons > * {
    padding: 0 10px;
  }
  
  /* Don't show the bullet points */
  .navigation-list {
    padding: 0px !important;
  }
  
  .navigation-list > li {
    list-style-type: none;
  }
  
  .tagcloud > a {
    background: #BBA;
    border-radius: 2px;

    font-size: 10.67px !important;
  }
  
  /* Slightly bigger sidebar fonts in mobile mode */
  @media screen and (max-width: 767px) {
    .widget {     
      font-size: 12px;
    }
  
    .widget-title {
      font-size: 14px;
    }
  
    .tagcloud > a {
      font-size: 12px !important;
    }
  }
  
  /* Blockquotes */
  pre {
    background: #FFFFD0 !important;
    color: #000;
  }
  
  /* Custom tags */
  blockquote.q {
    background: #FFFFD0;
    border: 1px dotted #000;
  }
  
  blockquote.m {
    border: 1px dotted #000;
  }
  
  blockquote.q, blockquote.m {
    font-style: normal;
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 16px;
    line-height: 21px;
    padding: 1ex;
  }
  
  kbd {
    background: #F0F0F0;
    color: #000;
    font-family: monospace;
    font-size: 13px;
    line-height: 15px;
    
    border-radius: 0px;
    box-shadow: none;
    
    border-width: 1px;
    border-style: solid;
    border-bottom-color: #A0A0A0;
    border-left-color: #FFF;
    border-right-color: #A0A0A0;
    border-top-color: #FFF;
  }
  
  code {
    font-family: Lucida Console, monospace;
    font-size: medium;
    color: #000;
    background: inherit;
  }
  
  var {
    font-family: Georgia, Times New Roman, serif;
    font-style: italic;
  }
}
