css:
@-moz-document domain("youtube.com") {
* {
    color: white !important;
    border: none !important;
}
#container,
#progress,
#page-manager,
#page-manager > ytd-watch,
#subscribe-button > ytd-subscribe-button-renderer > paper-button,
#subscribe > ytd-subscribe-button-renderer > paper-button,
#edit-buttons,
#edit-buttons > ytd-button-renderer:nth-child(1n+0) > a {
    background: black !important;
    background-color: black !important
}
#masthead-container {
    position: absolute !important;
    color: white !important;
    background-image: none !important;
    border: none !important;
    background: black !important;
    background-color: black !important
}
/*left menu*/

app-drawer {
    position: absolute !important;
}
#guide-content {
    width: 240px !important;
    color: white !important;
    background-image: none !important;
    border: none !important;
    background: black !important;
    background-color: black !important;
}
#guide-inner-content {
    width: 220px !important;
    padding-right: 45px !important;
    color: white !important;
    background-image: none !important;
    border: none !important;
    background: black !important;
    background-color: black !important;
}
body > div.gstl_50.sbdd_a > div:nth-child(2) > div.sbdd_b > div > ul,
body > div.gstl_50.sbdd_a > div:nth-child(2) > div.sbdd_b > div {
    color: white !important;
    background-image: none !important;
    border: none !important;
    background: black !important;
    background-color: black !important;
}
.html5-play-progress,
.ytp-play-progress {
    background: #000000 !important;
    background: -moz-linear-gradient(left, #191970 0%, #191970 100%) !important;
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, #1b1b1b), color-stop(100%, #FFFFFF)) !important;
    background: -webkit-linear-gradient(left, #191970 0%, #8b0000 100%) !important;
    background: -o-linear-gradient(left, #191970 0%, #8b0000 100%) !important;
    background: -ms-linear-gradient(left, #191970 0%, #8b0000 100%) !important;
    background: linear-gradient(to right, #191970 0%, #006400 50%, #8b0000 100%) !important;
}
.html5-load-progress,
.ytp-load-progress {
    background: #111111 !important;
}
.html5-scrubber-button,
.ytp-scrubber-button {
    background: #000000 !important;
    border: none !important;
    border-color: #000000 !important;
    box-shadow: 0px 0px 0px #ffffff, 0px 0px 0px #ffffff, 0px 0px 0px #ffffff !important;
}
.ytp-scrubber-button,
.html5-scrubber-button:hover,
.ytp-scrubber-button:hover {
    background: #000000 !important;
    border: none !important;
    border-color: #000000 !important;
    box-shadow: 0px 0px 0px #ffffff, 0px 0px 0px #ffffff, 0px 0px 0px #ffffff !important;
}
.ytp-volume-slider-foreground:before {
    background: #191970 !important;
    background: -moz-linear-gradient(left, #191970 0%, #191970 100%) !important;
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, #191970), color-stop(100%, #660066)) !important;
    background: -webkit-linear-gradient(left, #191970 0%, #191970 100%) !important;
    background: -o-linear-gradient(left, #00ffff 0%, #191970 100%) !important;
    background: -ms-linear-gradient(left, #00ffff 0%, #00ffff 100%) !important;
    background: linear-gradient(to right, #191970 0%, #006400 50%, #8b0000 100%) !important;
}
}

@-moz-document url-prefix("https://www.youtube.com/channel"), url-prefix("https://www.youtube.com/playlist"), url-prefix("https://www.youtube.com/user"), url-prefix("https://www.youtube.com/watch") {
#channel-container,
#page-manager,
#iydownloader-button,
#channel-header-container,
#header > app-header-layout,
#page-manager > ytd-browse:nth-child(1n+0),
#tabs,
#tabs-inner-container,
#channel-header-container,
#channel-header,
#overlays > ytd-thumbnail-overlay-resume-playback-renderer,
#progress,
#details-container > table > tbody > tr:nth-child(1n+0) > td:nth-child(2) > ytd-button-renderer > a,
#playlist,
#container,
#container > div,
#header-contents,
#main,
#container,
#items,
#contents,
#page-manager > ytd-browse:nth-child(1n+0) > ytd-playlist-sidebar-renderer {
    color: white !important;
    background-image: none !important;
    border: none !important;
    background: black !important;
    background-color: black !important
}
#related,
#movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button:nth-child(5),
#iydownloader-button {
    display: none !important
}
ytd-watch[theater] #player.ytd-watch {
    width: 1058px !important;
    height: 595px !important;
    max-width: 1058px;
    display: block !important;
    margin-left: auto !important;
    margin-right: auto !important;
}
/* Content page*/

ytd-watch[theater] #main.ytd-watch {
    width: 94%;
}
#main-link-url {
    position: fixed !important;
    right: 33px !important;
    bottom: 548px !important;
}
#info-contents > ytd-video-primary-info-renderer,
#header-contents,
#header-contents > h3,
#header-contents > h3 > yt-formatted-string,
#info-contents,
#container > h1 {
    text-align: center !important;
    padding: 0 !important;
    margin: 0 !important;
}
#info,
#container > div {
    text-align: center !important;
    margin: 0 !important;
}
}

@-moz-document url-prefix("https://www.youtube.com/analytics"), url-prefix("https://www.youtube.com/dashboard"), url-prefix("https://www.youtube.com/edit"), url-prefix("https://www.youtube.com/upload"), url-prefix("https://www.youtube.com/playlist"), url-prefix("https://www.youtube.com/my_videos") {
* {
    color: white !important;
    border: none !important;
    background: black !important;
    background-color: black !important
}
}

@-moz-document url-prefix("https://www.youtube.com/watch") {
::-webkit-scrollbar {
    display: none;
}
}
