css:
@-moz-document domain("weekplan.net") {
.inner-handle.clearfix{
    background-color: yellow;
    color: #000;
    border: 2px solid;
    border-color: #000;
}

.tag {
    font-size: .8em;
    color: #27ae60;
    font-weight: 400;
    border-radius: 9px;
    padding: 0 6px;
    margin-left: 2px;
    display: inline-block;
    background-color: #000;
}

.week .today h2 {
    color: #e0301e;
    background-color: #000;
}

.headers .col h2 {
    padding: 5px;
    font-family: "Traveling _typewriter", serif;
    text-transform:lowercase;
    font-weight: 400;
    line-height: 1em;
    display: inline-block;
    cursor: pointer;
}

.headers .col header {
    height: 73px;
    line-height: 73px;
    text-align: center;
    position: relative;
    color: #e0301e;
    background-color: #000;
}

.day h2 span {
    font-size: .8em;
    color: yellow;
}

.headers .col h2 span {
    font-size: .8em;
    color: #27ae60;
}

.text{
    font-family: "Georgia", serif;
    word-wrap: normal;
}

nav.top {
    background-color: #000;
    color: #27ae60;
    padding: 5px 5px 5px 38px;
    line-height: 26px;
}

.fa-stack.menu-icon{
    color: #000;
}

.fa-inverse{
    color: #27ae60;
}

.week .nav-left, .week .nav-right {
    top: 0;
    padding-top: 12px;
    background-color: #000;
}

.col {
    flex: 1;
    -webkit-box-flex: 1;
    -moz-box-flex: 1;
    -webkit-flex: 1;
    background-color: #000;
    border-left: 1px solid #27ae60;
    position: relative;
}

.carousel {
    margin: 0 40px;
    display: none;
    overflow: hidden;
    background-color: #000;
}

.title {
    float: left;
    width: 220px;
    padding: 0;
    margin: 0;
    background-color: #000;
}

.planner-section>header {
    position: relative;
    background-color: #000;
    color: #27ae60;
    padding: 5px;
    font-size: .8em;
}

.no-print.nav-left{
    background-color: #000;
}

.no-print.nav-right{
    background-color: #000;
}

input.action-text {
    border-width: 1px 0;
    outline: 0;
    width: 100%;
    padding: 10px 5px 10px 12px;
    -moz-box-shadow: inherit;
    -webkit-box-shadow: inherit;
    box-shadow: inherit;
    background-color: #1e1e1e;
}

.momentum-bar{
    background-color: #383d40;
}

.action .m-handle .fa-arrows {
    font-size: 10px;
    color: #000;
    background: yellow;
    padding: 2px;
    opacity: 0;
}

.md-content .md-title {
    margin: 0;
    padding: 20px;
    font-size: 1.8em;
    font-weight: lighter;
    background-color: #000;
    color: #27ae60;
}

.md-title{
    text-transform: lowercase;
    font-family: "Traveling _typewriter", serif;
}

.dialog-area.clearfix{
    background-color: #000;
}

.md-body{
    background-color: #000;
}

a.checkbox-button {
    display: block;
    margin: 15px 5px;
    padding: 5px 10px;
}
.checkbox-button {
    background-color: rgba(0,0,0,.05);
    padding: 5px;
    border-radius: 4px;
    margin: 10px 0;
}
a {
    margin: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: 0 0;
}
a, hr {
    padding: 0;
}
a {
    text-decoration: none;
    color: #27ae60;
}

#area-notes {
    padding-bottom: 20px!important;
}
.dialog-area {
    border: 4px solid #fff;
    padding: 10px;
    border-radius: 6px;
}
.dialog-area, .split>li {
    background-color: #000;
}

.nav-left i, .nav-right i {
    color: #27ae60;
    cursor: pointer;
    display: block;
    margin-bottom: 5px;
}

.community-metrics div {
    background-color: #e0301e;
    padding: 0 4px;
    display: inline-block;
    margin-left: 0;
    border-radius: 4px;
    color: #000;
    font-size: .8em;
}

.hint {
    background-color: #000;
}

.menu.hidden-opacity.on{
    background-color: #000;
}

.menu h2 {
    margin-top: 12px;
    text-align: center;
    color: #fff;
    font-size: 24px;
    font-weight: 400;
    font-family: Arial;
    margin-bottom: 26px;
}

.menu-section ul a {
    background-color: #000;
    padding: 10px;
    display: block;
}

.menu-section h3 {
    color: #e0301e;
    font-family: "Traveling _typewriter", serif;
    text-transform: lowercase;
    font-weight: 400;
    background-color: #000;
    padding: 10px 20px;
    cursor: pointer;
}

.menu-section ul {
    background-color: #1e1e1e;
}

.menu-section ul a.selected {
    background-color: #27ae60;
    color: #000;
}

.action .m-handle .fa-arrows {
    font-size: 10px;
    color: yellow;
    background: #000;
    padding: 2px;
}

fieldset .split label.field-label {
    float: none;
    color: #27ae60;
    display: block;
    margin-top: 0;
}

.dialog p, .question {
    margin-bottom: 10px;
    color: #e0301e;
}

.button, .guider_button, input[type=button], input[type=submit] {
    background-color: #25c782;
    -webkit-border-top-left-radius: 10px;
    -webkit-border-bottom-right-radius: 10px;
    -moz-border-radius-topleft: 10px;
    -moz-border-radius-bottomright: 10px;
    border-top-left-radius: 10px;
    border-bottom-right-radius: 10px;
    cursor: pointer;
    color: #000;
    margin-right: 1px;
    margin-bottom: 1px;
    padding: 8px 20px;
    border-width: 0;
    display: inline-block;
    font-size: 1em;
    line-height: 1.35em;
}

.flash.notice{
    background-color: #27ae60;
}
.flash {
    color: #000;
    padding: 10px;
    margin: 10px;
    border-radius: 4px;
    max-width: 300px;
    position: fixed;
    bottom: 0;
    z-index: 2000;
    box-shadow: 1px 1px 3px #666;
    right: -300px;
    -webkit-transition: right 1s,opacity .5s ease-out;
    -moz-transition: right 1s,opacity .5s ease-out;
    transition: right 1s,opacity .5s ease-out;
    opacity: 0;
}

.past .hint, .past .tag {
    background-color: #383640;
}

.day-quadrant{
    background-color: #000;
    color: #27ae60;
}

h3.no-print, h3.rotate{
    color: #e0301e;
    font-family: "Traveling _typewriter", serif;
    font-size: 10px;
}


.planner-section {
    background-color: #383d40;
    position: relative;
    margin-bottom: 1px;
}

}
