css:
@-moz-document domain("online.laranara.se") {
body {
    font-family: 'Roboto', sans-serif;
}
.panel {
    border: 2px solid #428bce;
    border-radius: 0px;
}
.panel-heading {
    background: #428bce !important;
    color: #fff !important;
    border: 0px solid #333 !important;
    border-radius: 0px;
}
.panel-body {
    border: 0px solid #fff !important;
    border-radius: 0px;
}
.navbar-nav {
    font-weight: bold;
}
.panel-heading > a, .panel-heading > a:link, .panel-heading > a:visited, .panel-heading > a:active {
    color: #fff;
}
.ln-overlay-panel {
    bottom: 0px;
    z-index: 999;
}
.ln-layout-footer {
    z-index: 998;
}
.ln-layout-footer, .ln-layout-footer > span, .ln-layout-footer > span > span {
	background: transparent;
    color: #fff;
    border: none;
    font-weight: bold;
}
.ln-layout-container {
    bottom: 0px;
}
.navbar-nav > li > a:active, .navbar-nav > li > a:visited, .navbar-nav > li > a:link {
    background: #fff !important;
    box-sizing: border-box !important;
    max-height: 50px;
}
.navbar-nav > li > a:focus, .navbar-nav > li > a:hover {
	border-bottom: 2px solid #428bce;
    box-sizing: border-box !important;
    max-height: 50px;
}
.panel {
    /*-webkit-box-shadow: 3px 3px 9px 1px #666;
	box-shadow: 1px 1px 3px 0px #666;*/
}

::-webkit-scrollbar {
    width: 15px;
}
::-webkit-scrollbar-track {
    background-color: #333;
}
 
::-webkit-scrollbar-thumb {
    background-color: #428bce; 
}
 
::-webkit-scrollbar-button {
    background-color: #333;
    height: 0px;
} 
 
::-webkit-scrollbar-corner {
    background-color: #333;
}
.btn-default {
    border: 2px #333 solid;
}
.btn-default:hover {
    border: 2px #333 solid;
    background: #333 !important;
    color: #fff;
}
.btn-warning {
    background: none !important;
    border: 2px #ff5b23 solid !important;
    color: #333 !important;
}
.btn-warning:hover {
	background: #ff5b23 !important;
    color: #fff !important;
}
.btn-danger {
    background: none !important;
    border: 2px #f50209 solid !important;
    color: #333 !important;
}
.btn-danger:hover {
    background: #f50209 !important;
    color: #fff !important;
}
.btn-primary {
    border: 2px #428bce solid;
    background: none;
    color: #333 !important;
}
.btn-primary:hover {
    border: 2px #428bce solid;
    background: #428bce !important;
    color: #fff !important;
}
.ln-theme .coursepart {
    border: 2px #333 solid;
    color: #333;
}
.ln-theme .coursepart:hover {
    border: 2px #428bce solid;
    color: #fff;
    background: rgba(66, 139, 206, 1);
}
.ln-layout-container {
    background: url(https://images.unsplash.com/photo-1432821596592-e2c18b78144f?ixlib=rb-0.3.5&q=80&fm=jpg&crop=entropy&s=3f9c78df0edb464244bbabb04d1797d8) no-repeat center center;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
.panel-group > .panel {
	background: #fff;
    border-radius: 0px;
    border: 2px solid #428bce;
}
.panel-group > .panel > .panel-heading > .panel-title > a {
	background: #fff;
    border-radius: 0px;
}
.panel-group > .panel > .panel-heading > .panel-title > a:active {
    background: #2569A8 !important;
    color: #fff;
}
.panel-group > .panel > .panel-collapse {
    border-top: 2px solid #428bce;
}
.ln-layout-accordion .panel-body > a:hover {
    background: #428bce !important;
    color: #fff;
}
.panel-group .panel-heading + .panel-collapse .panel-body {
    padding: 0px;
}
videogular {
    width: 100% !important;
    height: 95% !important;
}
body > div.fullheight > div > div.fullheight > div.ln-layout-container > div > div > div.ln-layout-sidebar.fullheight.col-sm-4.col-md-3.col-lg-3.collapse > div > div > div > div.panel-collapse.in > div > a.anim-linear.ln-layout-active-state {
    background-color: #428bce !important;
    color: #fff;
}

body > div.fullheight > div > div.fullheight > div.ln-layout-navbar.hidden-print > header {
    background-color: #fff !important;
    background-image: none;
    border-bottom: solid 2px #428bce;
}

body > div.fullheight > div > div.fullheight > div.ln-layout-container > div > div > div.ln-layout-sidebar.fullheight.col-sm-4.col-md-3.col-lg-3.collapse > div > div > div > div.panel-heading > h2 > a:hover {
    background-color: #428bce !important;
    color: #fff;
}
}
