css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://references.modernisation.gouv.fr/referentiel-technique-0") {

* {
  line-height: 1.5 !important;
}

body {
  font-size: 1em;
  background-color: #f8f5f5;
}
.l-content-main {
  background: none;
  max-width: 57em;
}
  
a { color: inherit !important; text-decoration: none !important; border: 1px dotted; border-width: 0 0 1px; }
a:focus,
a:hover,
a:active { color: #2868c9 !important; border-style: solid; }

/* Lisibilité des titres et CRITERES */
.page-title {
  text-align: center;
  padding-bottom: 0;
  color: #f5f5f5;
  font-size: 1px;
}
.page-title:after {
  content: 'RGAA 3';
  display: block;
  color: #2868C9;
  font-size: 46px;
}

.section__title {
}
  
.section__title h3,
.section__title h4,
.section__title h5 {
  padding-left: 0;
  padding-right: 0;
}

.section__title h4 {
  font-size: 2em;
  text-align: center;
  text-transform: uppercase;
}

.section__title h5 {
  font-size: 130%;
}

.section__links {
  display: none;
}

/* Lisibilité des textes et TESTS */
.rte {
  font-size: 1em;
  line-height: 1.5;
}
.rte blockquote, .rte dl, .rte ol, .rte p, .rte pre, .rte table, .rte ul {
  margin-bottom: 1.5em;
}

.section__content {
  padding-left: 0;
  display: block !important;
}

.section__content ul {
}
.section__content ul ul {
  font-size: .8em;
}

.section__content p {
  font-size: .75em;
  line-height: normal;
  margin-bottom: .5em;
}
.section__content p,
.section__content p * {
  color: #CCC !important;
}
.section__content p:hover,
.section__content p:hover * {
  color: #333 !important;
}

.key-words {
  display: none;
}

/* Ne garder que l'essentiel / blabla inutile */
.header-fixed,
.l-content-top,
.breadcrumb,
.footer .region-top,
.footer .region-middle {
  display: none !important;
}

article:first-child,
article:nth-child(2),
article:nth-child(3),
article:nth-child(4) {
    display: none !important;
}
  
}
