css:
@-moz-document domain("gitter.im") {
  input, textarea, select, .uneditable-input, .gtrInput {
    color: white;
    border: 1px solid #5d5d5d;
    background: black;
  }

  .room-item__container:hover, .room-item__container.active, .room-item.focus .room-item__container,
  .room-item:focus .room-item__container .room-item__container:focus {
    background-color: #4d4d4d;
    color: white;
  }

  .room-list-item__icon img {
    background: transparent;
  }

  .chat-item__action {
    color: white;
  }

  .trpContentPanel, .chat-item:not(.unread) {
    background-color: rgb(29, 29, 29) !important;
  }

  .right-toolbar.right-toolbar--collapsible {
    background: black;
    color: white;
  }

  .trpMoreBadge div {
    color: white;
  }

  h1,h2,h3,h4,h5,h6 {
    color: white !important;
  }

  #chat-container, .chat-item__text {
    color: #aaa !important;
  }

  .js-chat-name {
    color: white !important;
  }

  .chat-header, .chat-input {
    background-color: black;
  }

  a {
    color: white;
  }

  .right-toolbar {
    background-color: black;
    color: #aaa;
  }

  .chat-input__text-area {
    background-color: #000;
    padding-left: 1em;
    box-shadow: 0px 0px 5px #444;
  }

  .chat-input__box {
    background-color: #000;
  }

  .menu {
    background-color: #162E34 !important;
  }

  .menu-list__overflow-shadow {
    background: linear-gradient(to bottom,rgba(56, 52, 53, 0) 0, #162E34 100%);
  }

  .room-list-item--current-room {
    background-color: #6A797D;
  }

  .trpBtnWhite {
    background-color: #6A797D;
    border-bottom: none;
  }

  .chat-input__text-area {
    color: white;
  }

  .trpButtonMedium.trpBtnWhite.gtrCreateRoomButton:not(:hover) {
    color: white;
  }

  .room-list-item.room-list-item--current-room span {
    color: white;
  }

  .trpAddBadge.js-add-more div {
    color: white;
  }

  .popover.in {
    background: rgb(59, 59, 59) !important;
  }

  .popover.top .arrow:after {
    border-top-color: rgb(59, 59, 59) !important;
  }

  .popover.bottom .arrow:after {
    border-bottom-color: rgb(59, 59, 59) !important;
  }

  .popover.right .arrow:after {
    border-right-color: rgb(59, 59, 59) !important;
  }

  .popover.left .arrow:after {
    border-left-color: rgb(59, 59, 59) !important;
  }

  .popover-footer.trpBodySmall {
    background: rgb(59, 59, 59) !important;
  }

  .popover.in .popover-title {
    background: rgb(79, 79, 79) !important;
    color: white;
    border-color: white;
  }

  .popover-content .chat-item__text, .popover-content .chat-item__from {
    color: white !important;
  }

  .chat-header__avatar {
    background-color: black;
  }

  .js-search-input.search-input-field {
    background: black;
    box-shadow: 0px 0px 5px #444;
    color: white;
  }

  .trpPopoverDetails h1, .trpPopoverDetails h2, .trpPopoverDetails .vcard-stats,
  .trpPopoverDetails .trpUserDetails {
    color: white;
  }

  ::-webkit-scrollbar {
    width: 12px !important;
  }

  ::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3) !important;
    border-radius: 10px !important;
  }

  ::-webkit-scrollbar-thumb {
    border-radius: 10px !important;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5) !important;
    background: #668;
  }

  .chat-item__container--status {
    background: rgb(70, 70, 70) !important;
    border: 0;
  }

  .octicon-tools:before {
    color: white;
  }

  .trpAddBadge {
    color: white !important;
  }

  hr {
    border-top: 0;
    border-bottom: 1px solid rgb(59, 59, 59);
  }

  span[data-group-name="all"], .unread, .modal--default__header, .modal--default__content,
  .modal--default__footer, body, .popover-footer, .banner-wrapper .banner .mention {
    background-color: rgb(29, 29, 29) !important;
  }

  .modal--default__header__icon:hover {
    color: gray;
  }

  .permission__details, .markdown-help-content, .modal--default__content {
    color: white !important;
  }

 .markdown-help-content i {
    color: lightgray !important;
  }

  .welcome-modal__collaborators, .welcome-modal__collaborator,
  .collaborators-container, .userhome-main {
    background-color: rgb(29, 29, 29) !important;
    color: white !important;
  }

  .trpChatInput, .dropdown, div.result.selected, .result:hover,
  .textcomplete-wrapper .dropdown-menu {
    background-color: black !important;
    color: white !important;
  }

  ul.selectable .divider {
    background-color: black !important;
    border-bottom: 1px solid lightgrey !important;
  }

  span.groupmention {
    background-color: transparent !important;
  }

  .menu-list__overflow-shadow {
    background: none;
  }

  .issue.open {
    background-color: #072c1a !important;
    color: #1cce74 !important;
  }

  .issue.closed {
    background-color: #2e0905 !important;
    color: #ee3522 !important;
  }

  .commit {
    background-color: #1b0c27 !important;
    color: #ab62ec !important;
  }

  div.chat-connectivity-indicator, .chat-item__text .embed {
    -webkit-filter: invert(100%) hue-rotate(180deg)
  }

  i.search__toggle-icon.octicon.octicon-search {
    color: #333;
  }

  .right-toolbar-region__footer {
    background: black !important;
  }

  .right-toolbar-region > .right-toolbar {
    border-left: 1px solid #4d4d4d;
  }

  .room-menu__panel {
    background-color: black !important;
    border-right: 1px solid #4d4d4d;
  }

  .minibar {
    border-right: 1px solid #4d4d4d;
  }

  .room-item--one2one.focus .room-item__container,
  a#room-item__container.room-item__container:hover, .room-item__container.active,
  .room-item--search-message:hover {
    background-color: #4d4d4d !important;
  }

  .panel-footer__item--all {
    background: #333333 !important;
  }

  .right-toolbar-region__footer .menu-toggle-icon, .menu-toggle-icon,
  .right-toolbar-region__footer .menu-toggle-icon:hover {
    stroke: #8d8d8d !important;
  }

  .org-page-main {
    background-color: black;
  }

  .org-page__room-item-description, .org-page__room-item-details, .left-menu-search__input,
  .left-menu-search__clear, .right-toolbar-region .remaining-view-button-secondary-text {
    color: rgba(255, 255, 255, 0.6);
  }

  .org-page__room-item, .org-page__topic-item {
    border-top: 1px solid #4d4d4d;
    border-bottom: 1px solid #4d4d4d;
    border-right: 1px solid #4d4d4d;
  }

  div.textcomplete-wrapper ul.dropdown-menu a, ul.selectable a {
    color: #818181;
  }

  div.textcomplete-wrapper ul.dropdown-menu li.active > a, ul.selectable li.active > a {
    color: #d8d8d8;
    background-color: #163545;
  }

  div.textcomplete-wrapper ul.dropdown-menu li > a:hover {
    color: #d8d8d8;
    background-color: #163545;
  }

  div.textcomplete-wrapper ul.dropdown-menu li.active > a small, ul.selectable li.active > a small {
    color: rgba(255, 255, 255, .4) !important;
  }

  div.textcomplete-wrapper ul.dropdown-menu small, ul.selectable small {
    color: #aaa !important;
  }

  .pagination a:hover, .pagination .active a {
     background-color: #163545;
  }

  .pagination .active a {
    color: white;
  }

  .pagination a {
    border: 1px solid #4d4d4d;
  }

  /* topics (like forums? this blinded me trice) blind no one no more */
  .category-list__item--active, .topic-control__button--active,
  .topic-header__category-link, .topic-reply-list-header__filter-button--active {
    background-color: #5d5d5d;
    color: #fffefe;
    border: 1px solid #4d4d4d;
  }

  .panel--forum-follow-area, .topics-table-header {
    background-color: #222;
    border-bottom: 1px solid #4d4d4d;
  }

  .topics-table-body {
      background-color: #222;
  }

  .topics-table-body__row:nth-child(2n) {
    background-color: #333;
  }

  .topics-table-body__row {
    border-bottom: 1px solid #5d5d5d;
  }

  .topics-table-body__cell__link {
    color: lightgray;
  }

  select, option, .trpInput {
    background: #111 !important;
    color: white !important;
    border: 1px solid #4d4d4d;
  }

  #notification-options > optgroup {
    background: #333 !important;
    color: white !important;
  }

  .topic-header__tag-link {
    color: lightgray;
    border: 1px solid #4d4d4d;
  }

  .container--topic-header, .feed-item__content {
    background-color: #222;
    color: gray;
  }

  .panel--topic-body {
    background-color: #222;
    color: gray;
    border-bottom: 1px solid #4d4d4d;
  }

  .panel--reply-list-header, .panel--topic-reply-list {
    background-color: black;
  }

  .topic-reply-list-header__filter-button {
    color: lightgray;
  }

  .panel--reply-editor {
    background-color: #222;
    border-top: 1px solid #4d4d4d;
  }

  .editor--reply {
    background: black;
    border: 1px solid #4d4d4d;
  }

  .editor {
    border: 1px solid #4d4d4d;
    color: lightgray;
  }

  /* explore section, shock to the retinas by clicking here, now its nice and soft */
  .userhome-apps__button {
    color: gray;
    border: #4d4d4d 2px solid;
  }

  .userhome-apps__button:hover, .userhome-apps__button:focus {
    background: #5d5d5d;
    color: white;
    border: #4d4d4d 2px solid;
  }

  .explore-room-card-list-section {
    background-color: black;
  }

  .secondary-header-info-bar {
    border-bottom: 3px solid #4d4d4d;
  }

  .explore-header-info-bar__mobile-diagram {
    color: #fff;
  }

  .explore-header-info-bar__mobile-icon::after {
    background-color: #222;
  }

  .tag-pill {
    background-color: #5d5d5d;
    color: lightgray;
  }

  .tag-pill:hover, .tag-pill:focus {
    background-color: grey !important;
    color: white;
  }

  .tag-pill-list-pagination-more-button {
    background-color: #7d7d7d;
    border: 1px solid #7d7d7d;
    color: lightgray;
  }

  .tag-pill-list-pagination-more-button:hover, .tag-pill-list-pagination-more-button:focus {
    background-color: #9d9d9d;
    border-color: #9d9d9d;
    color: white;
  }

  .room-card {
    background-color: #5d5d5d;
    color: #fff;
  }

  .room-card__footer {
    border-top: 1px dotted #f2f0ed;
  }

  .subdued-button-pampas--xsmall {
    border: 1px solid #d5d5d5;
    color: #fff;
  }

  .room-card__tag {
    background-color: #282828;
    color: #fff;
  }
}
