css:
@-moz-document domain("pandora.com") {
/* Background Image */
body {
	background-image: url(http://www.jonathan-yanez.com/wp-content/uploads/2014/05/desktop-wallpaper-music.jpg);
	background-position: center top;
	background-size: cover;
}

/* Background Tint: rgba(0, 0, 0, 0.5) is black at 50% opacity */
#playerBar {
	background-color: rgba(0, 0, 0, 0.3);
}

#topnav {
	height: 100%;
}

#playerBar {
	border-color: transparent;
	min-width: 0;
	height: 100%;
}

#playerBar .highlight {
	height: 100%;
}

#playerBar .leftcolumn {
    display: none;
}

#playerBar .rightcolumn {
	display: table-header-group;
	float: none;
	padding: 0;
	text-align: center;
	width: auto;
}

#playerBar .rightcolumn .info a {
	color: #ffffff;
    background-color: #ff0000;
    padding: 10px;
}

#playerBar .columns {
	display: table;
	margin: 0 auto;
	position: relative;
}

#playerBar .middlecolumn {
	display: table-footer-group;
	width: 450px;
}

#playerBar .middlecolumn .buttons {
	width: auto;
}

#playerBar .progress {
	width: 100%;
}

#playerBar .progress .remainingTime {
	padding-right: 6px;
}

#playerBar .progress .progressBar {
	margin: 0 auto;
}

#playerBar .thumbDownButton {
	margin: 6px 19px 0 100px;
}

#playerBar .nowplaying {
	display: table !important;
	margin: 12px auto;
	text-align: center;
}

#playerBar .rightcolumn .albumArt {
	margin: 0 auto;
	width: auto;
}

#playerBar .rightcolumn .cd_menu {
	display: table-footer-group;
	float: none;
}

#playerBar .rightcolumn .cd_icon {
	float: none;
	margin: 0;
}

#playerBar .playerBarArt {
	position: relative;
	height: 400px;
	width: 400px;
}

#playerBar .rightcolumn .info {
	display: table-header-group;
	float: none;
	text-align: center;
	width: auto;
}

#playerBar .rightcolumn .info div:last-child {
	padding-bottom: 16px;
}

#playerBar .rightcolumn .info div {
	height: 24px;
}

#playerBar .rightcolumn .info a {
	font-size: 20px;
}

#playerBar .playerByText {
	display: none;
}

#playerBar .playerOnText {
	display: none;
}

#playerBar #playbackControl {
    margin: 10px 0 0 -5px;
    padding-left: 250px;
}

#playerBar #playbackControl .thumbDownButton a:link {
    background-image: url(https://cdn0.iconfinder.com/data/icons/emoticons-round-smileys/137/Emoticons-21-512.png);
    background-size: 36px 35px;
}

#playerBar #playbackControl .thumbUpButton a:link {
    background-image: url(http://icons.iconarchive.com/icons/paomedia/small-n-flat/1024/heart-icon.png);
    background-size: 36px 35px;
}


.skinContainer, .dropdownmenu, .searchBox, .volumePosition, #brandingBar, #footer {
	display: none;
}
}
