css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bitmex.com") {
	body {
		background: #111;
	}
	.sideWrap {
		background: #222;
	}
	.tradeSidebar .orderControls .ordTypes, .tradeSidebar .orderControls .fullPositionStatus, .tradeSidebar .orderControls .well, .tradeSidebar .orderControls pre, .tradeSidebar .orderControls .preferencesPage fieldset>.help-block, .preferencesPage .tradeSidebar .orderControls fieldset>.help-block {
		background: #222;
		color: #ddd;
	}
	.nav-tabs li.active {
		background: #006368;
	}
	#header .link, #header .dropdown-toggle {
		color: #666;
	}
	.instrumentSelectorPanel.widget-tabs .inactiveExpiry .btn {
		color: #ddd !important;
	}
	.tradeSidebar .sidebar {
		color: #ddd;
	}
	.instrumentSelectorPanel.widget-tabs .activeExpiry .btn {
		background-color: #006368;
		color: #DBCC00;
	}
	.widget {
		color: #BEBEBE;
	}
	.orderBook .asks td:nth-of-type(1), .orderBook .asks td:nth-of-type(1) .myOrder {
		color: #ddd;
	}
	.nav-tabs li.active .tabTitle {
		color: #ddd;
	}
	.nav-tabs .tabTitle {
		color: #777;
	}
	.instrumentSelectorPanel.widget-tabs .inactiveExpiry .btn {
		color: #ddd;
	}
	.nav-tabs li.active {
		background: #005155;
	}
	.btn-primary {
		color: #a7a7a7;
	}
	#header .link:active, #header .link.active, #header .dropdown-toggle:active, #header .dropdown-toggle.active {
		color: #999 !important;
	}
	.btn-primary {
		background-color: #000000 !important;
	}
	.marginDisplay .fieldLabel {
		color: #ddd;
	}
	.marginDisplay .barPositive .dataBar {
		background-color: #005E1E;
	}
	.btn-success {
		background-color: #026221;
		border-color: #47b268;
		color: #fff;
	}
	.btn-danger {
		background-color: #9C2400;
		border-color: #e24e24;
		color: #fff;
	}
	/*chart*/
	.darkTheme .chart-controls-bar {
		background: #222;
		border-top: 1px solid #555;
		height: 21px;
	}
	.darkTheme .chart-container {
		border-color: #555;
	}
	.darkTheme .favored-list-container span {
		background: #222;
		color: #eee;
		border: 1px solid #666;
	}
	.darkTheme .header-chart-panel .button, .darkTheme .header-chart-panel .button.disabled, .darkTheme .hotlist-controls .button, .darkTheme .hotlist-controls .button.disabled, .darkTheme .symbol-edit-widget .button, .darkTheme .symbol-edit-widget .button.disabled {
		border: 1px solid #666;
		background: #222 !important;
		color: #eee !important;
	}
	.darkTheme .chart-container {
		border-color: #666;
		background-color: 222;
	}
}
