css:
@-moz-document domain("tweetdeck.twitter.com") {
/*Borrowed some code from TinaDeck (For the Text Shadows) https://userstyles.org/styles/115698/tinadeck-special-lovewins-edition */

.tweet-avatar,
.avatar {
	background: transparent;
	-moz-border-radius: 100px;
	border-radius: 100px;
	-webkit-border-radius: 100px;
	/* Webkit for Chrome and Safari */
	
	-webkit-transform: scale(1);
	-webkit-transition-duration: 250ms;
	-webkit-transition-timing-function: ease-out;
	/* Webkit for Mozila Firefox */
	
	-moz-transform: scale(1);
	-moz-transition-duration: 250ms;
	-moz-transition-timing-function: ease-out;
	/* Webkit for IE( Version: 11, 10 ) */
	
	-ms-transform: scale(1);
	-ms-transition-duration: 250ms;
	-ms-transition-timing-function: ease-out;
}
.tweet-avatar:hover,
.avatar:hover {
	-webkit-animation: pulse 1.5s infinite ease-in-out;
	animation: pulse 1.5s infinite ease-in-out;
	-moz-animation: pulse 1.5s infinite ease-in-out;
	-moz-transform: scale(1.2);
	-webkit-transform: scale(1.2);
	-o-transform: scale(1.2);
	-ms-transform: scale(1.2);
	transform: scale(1.2);
	-webkit-transition-duration: 250ms;
	-moz-transition-duration: 250ms;
	-ms-transition-duration: 250ms;
	-webkit-transition-timing-function: ease-out;
	-moz-transition-timing-function: ease-out;
	-ms-transition-timing-function: ease-out;
}

.prf-img{
background-color:white;
    border-radius:100px;	
}
@-webkit-keyframes pulse {
	0% {
		-webkit-box-shadow: 0 0 12px rgba(171, 234, 255, 0.2);
	}
	50% {
		-webkit-box-shadow: 0 0 12px rgba(171, 234, 255, 0.9);
	}
	100% {
		-webkit-box-shadow: 0 0 12px rgba(171, 234, 255, 0.2);
	}
}
.tweet .account-link,
.tweet-detail-wrapper:hover .account-link {
	text-shadow: 0px 0px 0px rgba(23, 183, 242, 1);
}
.tweet:hover .account-link,
.tweet-detail-wrapper:hover .account-link {
	text-shadow: 0px 0px 1px rgba(23, 183, 242, 1);
	text-decoration: none;
}
.compose-account {
	background-color: rgba(0, 0, 0, 0);
}
}
