css:
@-moz-document domain("forums.plex.tv") {
/*=============================================
			General page style Elements
=============================================*/

html, body, body.thumbnail
{ background-color: #21333E;
    color: #009999
}

.header, .footer
{ background-color: #121A23;
    color: #EBF0F5;
    border: 0px
}

.header a
{ color: #EBF0F5;
}

.footer a
{ color: #EBF0F5;
}

h1.H a
{ color: #ff8a00;
    border: none
}

h1, h1.HomepageTitle
{ color: #ff8a00;
}

div.container-fluid h1.H
{ margin: 10px
}

.Active a
{ background-color: #618CBF !important;
}

div.P.PageDescription
{ color: #D8F3F0;
    padding-left: 0px;
    padding-bottom: 0px
}
/*=============================================
			Navigation Elements
 =============================================*/

div.container-fluid div.row
{ max-width: none;
}

nav.navbar-default .navbar-nav>li>a
{ color: #EBF0F5
}

nav.navbar.navbar-default
{ background-color: #17212c;
    border: 0px
}

div.navbar-collapse.collapse
{ background-color: #17212c;
    border-color: #ff8a00
}

div.navbar-collapse.collapse .Selected
{ background-color: #21282E;
    color: #ff8a00
}

.navbar-default .MeBox .FlyoutButton
{ color: #D8F3F0;
}
/*=============================================
			    BreadCrumb Elements
 =============================================*/

div.container-fluid span.Breadcrumbs
{ background-color: #1C2836;
    margin-bottom: 10px
}

.Breadcrumbs
{ border: 0px
}

div.container-fluid span.CrumbLabel.HomeCrumb a
{ color: #fff
}

div.container-fluid span.CrumbLabel.Last a
{ color: #ff8a00
}
/*=============================================
	       Search Box Elements
=============================================*/

div.well
{ background-color: #1C2836;
    padding: 15px
}

.well.search-form
{ border: 0px
}

div.well.search-form div.AdvancedSearch
{ max-width: none
}
/*=============================================
		Page Selector Box Elements
=============================================*/

div.PageControls
{ margin-bottom: 10px;
    margin-right: 1px;
    margin-left: 1px
}

div.PageControls a.Previous
{ background-color: #D8F3F0;
    border: #323232;
    color: #ff8a00
}

div.PageControls a.Next
{ background-color: #F1FCFC;
    border: #323232;
    color: #ff8a00
}

.NumberedPager span
{ background-color: #F1FCFC;
    border: #323232;
    color: #000
}

.NumberedPager a
{ background-color: #F1FCFC;
    border: #323232;
    color: #000
}

.NumberedPager div.QuickSearch
{ background-color: #F1FCFC;
    border: #323232
}

.NumberedPager div.QuickSearch a
{ background-color: #F1FCFC
}

.NumberedPager .Highlight
{ z-index: 2;
    color: #D8F3F0;
    background-color: #428bca;
    border-color: #ff8a00;
    cursor: default;
}

.NumberedPager a:hover
{ background-color: #D8F3F0;
}
/*=============================================
           Forum Catagory Elements
=============================================*/

div.CategoryDescription
{ color: #404A4D
}

.TitleWrap a.Title
{ padding-bottom: 5px !important;
    display: block;
    text-decoration: underline;
}

.CategoryHeading
{ background: #3B5157 !important
}

.ItemContent.Category
{ color: #F8FAFC !important
}

.Discussions .Item
{ opacity: .95;
}

.Discussions .Unread, .Discussions .New
{ background-color: #38AFA9;
}

.CategoryList .Item
{ opacity: 0.9
}

.CategoryList .Unread, .Discussions .New
{ opacity: 1
}
/* =========================================== 
        Catagory and Discussion Elements 
=============================================*/

.DataList .Item
{ border: 1px solid #323232;
    background-color: #FFFFD4;
}

.Discussions .Item
{ background-color: #FFFABF;
}

.Discussions .Unread
{ background-color: #FFF275;
}

.Discussions .New
{ background-color: #FFD646;
}

.DataList .Item a
{ color: #121A23 !important;
    font-weight: 500;
}

.DataList .Item .AuthorWrap a
{ color: #F2F2F2 !important
}

.DataList .Item .Signature a
{ color: #ff8a00 !important
}

.DataList .Item .Message a
{ color: #ff8a00 !important
}
/*=============================================
               Discussion Elements
=============================================*/

.DataList .Item .Meta
{ color: #331E00;
}

.Item .Meta .MItem a
{ color: #194775 !important;
    font-size: 120%;
    font-weight: 500;
    padding-left: 5px;
}

.Item .Meta .MItem.Category a
{ color: #ff8a00 !important;
}

.MItem.LastCommentDate
{ float: right !important
}

.MItem.LastCommentDate:after
{ content: "   -   ";
}

.MItem.LastCommentBy
{ float: right;
    padding-right: 5px;
}

.Conversation .Item .MItem
{ color: #F2F2F2 !important
}

.ItemContent .Title a
{ padding-bottom: 5px;
    text-shadow: #1D4536;
}

.MItem.LastCommentDate:before
{ content: " ";
}

div.Item-Body
{ background-color: #FAFDFE;
}

div.Message a
{ color: #ff8a00 !important
}

div.Signature a
{ color: #ff8a00 !important
}

div.Meta.DiscussionMeta
{ color: #D8F3F0
}

div.Meta.DiscussionMeta .DateCreated a
{ color: #F2F2F2 !important
}

div.Meta.Meta-Discussion .MItem.Category a
{ color: #404A4D !important
}

div.Meta.CommentMeta.CommentInfo a
{ color: #F2F2F2 !Important;
}

div.Meta.CommentMeta.CommentInfo
{ color: #FCFCFC !Important;
}

MItem MCount CommentCount
{ color: red !important
}

.Tag-Announcement
{ background-color: #5bc0de;
}

.HasNew
{ background-color: #33CC33;
    border: 1px solid #003366;
}

a.Bookmark:before
{ font-size: 14px;
    line-height: 1.42857143;
    color: #ff8a00;
    content: "\f006";
}

a.Bookmarked:before
{ content: "\f005";
}

.OptionsMenu .SpFlyoutHandle
{ color: #A0B6D2;
}
/*=============================================
          Accepted Answers Elements
=============================================*/

ul.AcceptedAnswers .Item.QnA-Item-Accepted .Item-Header
{ color: #3c763d;
    background-color: #67c25d;
    border-color: #E2E3E8;
}

ul.AcceptedAnswers .Item.QnA-Item-Accepted .Item-Header a
{ color: #404A4D
}

ul.AcceptedAnswers .Item.QnA-Item-Accepted .AuthorWrap a
{ color: #404A4D !important
}

ul.AcceptedAnswers
{ border-top: solid 2px #67c25d;
    border-bottom: solid 2px #67c25d;
    padding: 0 0 20px 0;
}
/*==========================================
                Box Tags 
============================================*/

div.Box.Tags
{ margin-top: 5px;
    padding: 15px;
    background-color: #1C2836;
    color: #D8F3F0
}

div.Box.Tags li
{ background-color: #404A4D
}

div.Box.Tags li a
{ color: #D8F3F0
}

div.Box.Tags .Count
{ background-color: #D8F3F0;
    color: #121A23
}

div.WhosOnline .Count
{ background-color: #323232
}
/*=============================================
           BoxFilter Menu Elements
=============================================*/

div.BoxNewDiscussion
{ margin-bottom: 0px
}

div.BoxFilter
{ background-color: #1C2836
}

div.BoxFilter li a
{ color: #D8F3F0
}

div.BoxFilter .Count
{ background-color: #A0A0A0;
    color: #F2F2F2
}

div.BoxFilter .FilterMenu
{ padding: 0px
}

div.BoxFilter .FilterMenu a:hover
{ background-color: #6A8F9A;
}

div.Box.Leaderboard
{ background-color: #1C2836;
    padding: 15px;
    color: #fff;
}

div.Box.Leaderboard .PanelInfo a
{ background-color: #1C2836;
    color: #3385AD;
    border: 0px
}

div.Box.Leaderboard .Count
{ background-color: #404A4D
}
/* ========================================================
               Catagory Filter Elements
=========================================================*/

div.CategoryFilter
{ margin-top: 5px;
    padding: 15px;
    background-color: #1C2836;
    color: #D8F3F0
}

div.CategoryFilter
{ display: block
}
/*=============================================
	       Who's Online Elements
=============================================*/

.Online .Author .PhotoWrap
{ padding-right: 1.5px;
    padding-left: 1.5px;
    padding-top: 1.5px;
    padding-bottom: 0px;
    background-color: #00CC00;
    outline: none;
    border-radius: 4px;
    border-color: #00CC00;
    box-shadow: 0 0 12px 1px #00CC00;
}

.Author .PhotoWrap.Online:after
{ content: "Online";
    text-align: center;
    font-size: 90%;
    font-weight: normal;
    color: #001225;
    display: list-item;
    padding: 1px 2px 0px 0px;
}

.Section-Discussion .Role_PlexPass .MItem.RoleTitle
{ top: 68px;
}

.OnlineUserWrap .OnlineUserName
{ background: #21333E;
}

.OnlineUserWrap .OnlineUserName:before
{ content: " ";
    background-color: #5bc0de;
    color: black;
    border: 5px solid #909090;
    border-color: #909090 transparent transparent transparent;
    position: absolute;
    bottom: -10px;
    left: 7px;
}

div.WhosOnline
{ margin-top: 5px !important;
    padding: 15px;
    background-color: #1C2836;
    color: #D8F3F0
}

div.WhosOnline
{ display: block
}

.ProfilePhoto, .Discussion .Author .ProfilePhoto, body.Rank-Employee .MeBox .ProfilePhoto
{ border-radius: 2px !important;
}

div.Profile dl.About
{ color: #D8F3F0;
}

.Profile .Gloss
{ display: inline-block;
    color: #ff8a00;
    margin-left: 15px;
}
/*=============================================
           Message List Elements
=============================================*/

div.MessageList div.Item
{ border: 5px #ff8a00
}

ul.MessageList.Conversation .Meta
{ background-color: #404A4D;
    border: none;
}

ul.MessageList.Conversation .Meta a
{ color: #F2F2F2 !important
}

div.Item-Header.DiscussionHeader
{ background-color: #5F818B;
    border: 0px;
}

div.Item-Header div.AuthorWrap a
{ color: #F2F2F2
}

div.Item span.AuthorInfo
{ color: #D8F3F0
}

div.Item-Header.CommentHeader
{ background-color: #6A8F9A;
    border: 0px;
}

div.Item .Meta .ChildCategories a
{ color: #D8F3F0
}

div.Item-Body
{ background-color: #FAFEFE;
}

div.Comment span.AuthorInfo
{ color: #D8F3F0
}

div.Comment span.Arrow.SpFlyoutHandle
{ color: #D8F3F0
}

div.Message p, div.Message
{ font-size: 100%;
}

div.Message p
{ color: #121A23;
}

.Form-Body
{ background-color: #6A8F9A;
}

div.Form-BodyWrap
{ background-color: #F2F2F2
}

div.Form-BodyWrap div.Buttons
{ background-color: #E7F4FC;
    border-top: 10px solid #121E22;
}

div.MessageForm
{ border: 0px
}

div.container-fluid.dark.footer div.row
{ padding: 20px
}

div.Profile h2.H
{ color: #D8F3F0
}

div.Profile div.Title
{ color: #A0A0A0
}

div.Profile dl.About
{ color: #404A4D
}

div.Photo.PhotoWrap.PhotoWrapLarge
{ background-color: #323232;
    border: 0px
}

div.BoxDiscussionFilter
{ display: block;
}

ul.Flyout.MenuItems
{ background-color: #E3EAF8;
}

span.ToggleFlyout div.Flyout
{ background: #E7F4FC
}

.thumbnail a>img
{ display: inline
}

div.AdvancedSearch
{ color: #F2F2F2
}

table.PreferenceGroup
{ background-color: #F2F2F2
}
/*=============================================
		  Square Icons
=============================================*/

.Activity-Registration .ProfilePhoto, .Activity-WallPost .ProfilePhoto, .OnlineUserWrap .ProfilePhoto, .ItemDiscussion .ProfilePhoto, .Discussion .Author .ProfilePhoto, .Leaderboard .ProfilePhoto, .MeBox > .PhotoWrap > .ProfilePhoto, .Tile .ProfilePhoto
{ border-radius: 3px !important;
}
/*=============================================
		    Editor Elements
=============================================*/

.editor
{ background-color: #F2F2F2;
    color: #777
}

.editor
{ background-color: #EEF8FD;
    color: #777;
}

div.editor-help-text
{ color: #404A4D
}

div.FormWrapper
{ color: #F2F2F2
}

.bodybox-wrap
{ background-color: #E7F4FC;
    padding: 8px;
    border-radius: 5px;
}
/* New Additions Follow */

.MenuItems.table.PreferenceGroup
{ background-color: #F7F9FC;
}

.InfoMessage
{ background-color: #9ACBEA;
    border-color: #CECE1D;
    color: #262C61;
}

.sidebar .SideMenu .PanelInfo li a
{ position: relative !important;
    margin-top: -1px !important;
    line-height: 20px !important;
}

.sidebar .SlideMenu .PanelInfor li a:hover
{ background-color: black;
}

.SideMenu.Active a
{ background: #CE3636 !important;
}

.sidebar .SideMenu .PanelInfo li.Active a
{ color: #428bca !important;
    background: #f9f9f9 !important;
}

div.Box.Group.SideMenu
{ background-color: #A0A0A0
}

div.Box.Group.SideMenu a
{ background-color: #D8F3F0;
    border: 2px solid #323232;
    color: #777
}
/****** playing with buttons ******/

.btn-default, .Button, .ActionBlock .React
{ color: #000;
    background-color: #F7F9FC;
    border-color: #428bca;
}

.InputBox
{ background-color: #F7F9FC;
}
/******* Discussion Styles ********/

div.ButtonGroup.Multi.Primary.Action.NewDiscussion.Big
{ visibility: hidden;
    display: none;
}

blockquote.UserQuote
{ font-size: 100%;
    background: rgba(12, 149, 220, 0.05);
    border-left: 4px solid rgba(0, 0, 0, 0.1);
    margin: 1em;
    padding: 1ex 10px;
}
/*===============================================================
              Activity/Profile Page Elements
===============================================================*/

.ItemContent.Activity
{ color: #404A4D;
    background-color: #F7F9FC;
}

.ItemContent.Activity div.Title
{ color: #E49000
}

.Item.Activity:hover
{ background-color: #6A8F9A;
}

div.Profile h2.H
{ color: #D8F3F0
}

div.Profile div.Title
{ color: #A0A0A0
}

div.Profile dl.About
{ color: #D8F3F0
}

.bodybox-wrap
{ padding: 15px;
    border-radius: 4px;
    border: 5px solid #6A8F9A;
    background-color: #fff;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
}

a.Delete
{ color: black;
    opacity: .9;
}

.Item.Activity .ItemContent
{ border-radius: 4px;
}

.DataCounts
{ background-color: #fff;
    border: solid 4px #6A8F9A;
    border-radius: 5px;
}

.SideMenu.Active a
{ background: #D5E6F3 !important;
}

li.item.item-Search.Dataist .Item a
{ color: red;
}
/* -------------------------------------------------------- */
}
