css:
@-moz-document domain("drive2.ru") {
/* Убираем верхний блок с фотками проплаченных машин */

.promocars {
	display: none;
}

/* Менее контрастный логотип */

.logo {
	background-color: gray;
}

/* Основной заголовок чуть мельче*/

.journal-entry-title {
	font-size: 18px;
}

/* Фотографии на страницах поменьше */

.journal-entry-pic, .journal-entry-pic img {
	filter: gray;
	-webkit-filter: grayscale(100%);
	max-width: 200px;
	height: auto;
	margin: 0;
	-webkit-transition: max-width 0.2s ease-in-out;
    -moz-transition: max-width 0.2s ease-in-out;
    -o-transition: max-width 0.2s ease-in-out;
    transition: max-width 0.2s ease-in-out;
}
.journal-entry-pic img:hover {
	filter: none;
	-webkit-filter: grayscale(10%);
}
.journal-entry-pic:hover, .journal-entry-pic img:hover {
	max-width: 920px;
}
.journal-entry-pic-desc {
	left: 0 !important;
}
.albums-add-picwrapper {
	width: 100% !important;
}

/* Фото в ленте */

.postitem-pic,
.postcard-pic,
.carcard-pic,
.logbook-list-pic,
.feed-item-cotd-pic {
	filter: gray;
	-webkit-filter: grayscale(100%);
}
.postitem-pic:hover,
.postcard-pic:hover,
.carcard-pic:hover,
.logbook-list-pic:hover,
.feed-item-cotd-pic:hover {
	filter: none;
	-webkit-filter: grayscale(10%);
}

/* Слайдер */

.slideshow {
	filter: gray;
	-webkit-filter: grayscale(100%);
}
.slideshow:hover {
	filter: none;
	-webkit-filter: grayscale(10%);
}

/* Видео поменьше */

.post-video {
	max-width: 200px;
	height: auto;
}

/* Аватарки */

.c-user-card__pic {
	width: 50px;
	height: auto;
}
.c-user-card__info {
	margin-left: 60px;
}
.c-user-card__pic img {
	width: 100%;
	height: auto;
}

/* Кнопки */

.xbutton-big {
	font-size: 12px;
	height: auto;
	line-height: 1.5;
}
.like-button,
.xbutton-blue,
.c-button {
	border-color: gray;
	background-color: gray;
	color: white;
}
.gsc-cursor-current-page {
	border-color: gray;
	background-color: gray;
}
.like-button:hover {
	border-color: darkgray;
	background-color: darkgray;
}

/* Шилдики */

.top-nav-num {
	border-color: darkgray;
	background-color: darkgray;
}

/* Заголовки */

h1 {
	font-size: 16px;
}
h2 {
	font-size: 16px;
}
.car-caption {
	font-size: 16px;
}

/* Подвал */

.footer {
	display: none;
}
.l-content,
.l-main {
	padding-bottom: 0;
}
}
