css:
@-moz-document domain("wikimart.ru") {
/* Ссылки */

a {
	color: #363b41 !important;
	text-decoration: underline !important;
	font-size: 14px !important;
	line-height: 18px;
}
a:hover {
	color: red !important;
}
/* Уебанская навигация */

.header-middle-placement,
.header-middle {
	background: #3f3f3f;
}
.header-sale__name {
	color: white;
	text-decoration: underline !important;
}
.departments-gallery__navigation {
	border-bottom: none;
}
/* Баннеры */

.homepage-banners,
.free-banners,
.footer-baners {
	display: none !important;
}
/* Заголовки */

h1 {
	font-size: 18px !important;
}
h2 {
	font-size: 16px !important;
}
h3 {
	font-size: 16px !important;
	text-align: left !important;
}
.TitCat {
	color: #363b41;
}
.page__content {
	background: none;
}
/* Скрываем сео блок */

.catalog-other-categories {
	display: none;
}
.vertical-margin-bottom-24 {
	margin: 0;
}
.listing-category-description {
	display: none;
}
/* Нахуй маркетинговые уловки */

.catalog-product-item__price-new {
	color: #363b41;
}
.catalog-product-item__price-old {
	display: none;
}
.catalog-product-item__discount {
	display: none;
}
.departments-gallery__navigation {
	height: auto;
}
.striked-outer {
	display: none;
}
.currency {
	font-weight: bold;
	color: #363b41 !important;
}
.listing-list-promo-shields {
	display: none;
}
.shield-type-discount {
	display: none;
}
.articl {
	font-weight: normal !important;
}
.old-price {
	display: none;
}
.footer__header-type-social,
.footer__social-icons {
	display: none;
}
.listing-pics__price-before-discount {
	display: none;
}
.shield-type-hit {
	display: none;
}
/* Мелкий шрифт */

.public-offer-disclaim {
	color: red;
	font-size: 14px !important;
}
/* Убираем уебанский стиль 90х */

table,
table td {
	border: 0 !important;
}
.roundGrayBorder {
	background-color: #FBFBFB;
	box-shadow: none;
}
div.j-accessories .category-selector li span {
	color: #363b41;
}
.cont,
.top {
	border: none !important;
	background: none !important;
}
.bot {
	display: none;
}
.listing-pics__item,
.listing-pics__item:hover {
	border: none;
	box-shadow: none;
}
.listing-pics-carousel__nav-prev:before,
.listing-pics-carousel__nav-next:before {
	border: none !important;
}
/* Что за пидар придумал всплывающие сообщения */

#notifyContent,
#sociaplus_comparePlus {
	display: none !important;
}
/* Копирайт */

.footer__copyright:after {
	content: '© Вася Пупкин 2015';
}
/* Исправляем косяки верстки */

div.j-accessories .item .link {
	height: 3rem;
}
.footer__link {
	padding: 4px 0;
}
.footer__apps {
	padding: 0;
}
.catalog-product-item__name {
    height: 60px;
}
/* Убираем мешающийся sidebar */

.header-middle-fixed {
	position: relative;
}
}
