css:
@-moz-document domain("eds.kedschools.se") {
.menu-tail {
    background-color: #BFCCFF;
    background-image: -webkit-linear-gradient(top, #8899FF, #BFCCFF);
    background-image: -moz-linear-gradient(top, #8899FF, #BFCCFF);
    background-image: -o-linear-gradient(top, #8899FF, #BFCCFF);
    background-image: linear-gradient(to bottom, rgba(12, 140, 189, 0.79), #0C8CBD);
    line-height: 22px;
}
#menu li.menu-item a {
    background-color: #BFCCFF;
    background-image: -webkit-linear-gradient(top, #8899FF, #BFCCFF);
    background-image: -moz-linear-gradient(top, #8899FF, #BFCCFF);
    background-image: -o-linear-gradient(top, #8899FF, #BFCCFF);
    background-image: linear-gradient(to bottom, #0C8CBD, rgba(12, 140, 189, 0.75));
    border-right: 1px solid #CCC;
    color: #000;
    display: block;
    font-weight: bold;
    padding: 0 20px;
    text-decoration: none;
}
table th {
    background-color: #BFCCFF;
    background-image: -webkit-linear-gradient(top, #0C8CBD, #0C8CBD);
    background-image: -moz-linear-gradient(top, #BFCCFF, #8899FF);
    background-image: -o-linear-gradient(top, #BFCCFF, #8899FF);
    background-image: linear-gradient(to bottom, #8899FF);
    background-size: 100% 100%;
}
footer {
    background-color: #333399;
    background-image: -webkit-linear-gradient(left, #333399, #BFCCFF);
    background-image: -moz-linear-gradient(left, #333399, #BFCCFF);
    background-image: -o-linear-gradient(left, #333399, #BFCCFF);
    background-image: linear-gradient(to right, #0C8CBD, #0C8CBD);
    border: 1px solid #CCC;
    clear: both;
    display: block;
    float: left;
    line-height: 21px;
    margin-bottom: 10px;
    margin-left: -1px;
    width: 100%;
}
table tr.odd {
    background-color: rgba(253, 253, 253, 0.8);
}
.selected {
    background-color: #0C8CBD;
    background-image: -webkit-linear-gradient(top, #0C8CBD, #0C8CBD);
    background-image: -moz-linear-gradient(top, #BFCCFF, #8899FF);
    background-image: -o-linear-gradient(top, #BFCCFF, #8899FF);
    background-image: linear-gradient(to bottom #8899FF);
}
.completed {
    background-color: #4ABD23;
    background-image: -webkit-linear-gradient(top, #4ABD23, #4ABD23);
    background-image: -moz-linear-gradient(top, #BBEEAA, #88AA88);
    background-image: -o-linear-gradient(top, #BBEEAA, #88AA88);
    background-image: linear-gradient(to bottom, #4ABD23, #4ABD23);
}
header {
    background-image: url('http://s8.postimg.org/nq44l4mr7/EDS_Omslag_4.png');
    background-repeat: no-repeat;
    display: block;
    height: 60px;
    margin-bottom: 1px;
    width: 100%;
}
#profile-panel {
    -moz-border-radius: 8px;
    -webkit-border-radius: 8px;
    background-color: #FFF;
    border: 2px #0C8CBD solid;
    border-radius: 10px;
    float: right;
    font-size: x-small;
    height: 50px;
    margin-right: 0px;
    margin-top: 4px;
    width: 210px;
}
#notification-icon {
    background-image: url('http://www.veryicon.com/icon/32/Application/iOS8%20Cirtangle%20Concept/Message.png');
    height: 32px;
    width: 32px;
    }

}
