css:
@-moz-document domain("btshoufa.com") {
body{
background: url("http://g.hiphotos.baidu.com/image/pic/item/ac4bd11373f0820214fb3fc94dfbfbedaa641bde.jpg") no-repeat  fixed center / cover !important;
}
#hd > DIV > DIV:first-child > H2 > A > IMG{
    border-radius:155px !important;}
/*搜索栏*/
.scbar_txt_td, .scbar_type_td,.scbar_icon_td {background:none !important}
#scbar_txt {height:30px !important; border:1px dotted rgba(177,176,177,0.7)!important;background:none!important;color:#000B0D!important;}
*{font-size: 13px}
.wp {width:1100px}
.scbar_btn_td,.tedt .pt,.txt.px.vm,.tedt .area,SPAN{background:none !important}
#scbar_btn:before
{ 
content:"搜索";   font-size: 12px;font-family: 微软雅黑 !important;color:#000000;top: 2px!important;position:relative!important;

}
#scbar_btn {background : rgba(228,228,228,0.5) !important;height:35px !important;width:55px !important; padding:7px 3px 15px 0px; left:-24px;position:relative!important;
border:1px dotted rgba(177,176,177,0.7)!important; } 
#scbar_btn:hover{background : rgba(28,228,228,0.5) !important;border:1px dotted rgb(250,0,255)!important;}
#scbar_btn:hover::before {color: #FF6A6A  !important;font-size:13px!important;text-shadow: 0px 0px .4px }
#scbar_txt{font-size:15px!important;padding:3px 10px 0px;}
#scbar_type { padding-left: 8px;}
/*页码*/
.pg a, .pg strong, .pgb a, .pg label{background:none!important;}
.pg a.prev{ display:none !important; }
.pg a:hover, .pg strong, .pgb a:hover{ background-color:rgba(28,238,238,0.5)  !important;}
.pg, .pgb {left:-3px !important;position:relative!important;}
.pg a.nxt {padding-right: 15px;} 
.fl .bm_h,.scbar_icon_td,.pls,#nv,#nv li,#toptb{background:none!important;} 
.bm,.bm_h,#scbar,.tl .ts th, .tl .ts td ,.tl .th,.ttp a, .ttp strong,#extcreditmenu.a, #g_upmine.a,#nv ,#qmenu,.frame, .frame-tab  {background:rgba(238,238,238,0.3)  !important;}
    .bm_h ,#scbar{
    border-top:  1px dotted rgb(204, 204, 204)!important;
    border-bottom:  1px dotted rgb(204, 204, 204)!important;} 
    #nv li a  {
    color: #696969;
}
   #nv li.a a {
    color: #000000;   background: rgb(235, 258, 130);transition: all 0.3s linear 0s;text-decoration:none !important;border-radius:5px 
} 
    #nv li a:hover{
    color:  #FF6A6A;
    background: rgb(225, 258, 0);transition: all 0.3s linear 0s;text-decoration:none !important;border-radius:5px 
}
.tl th, .tl td {
    border-bottom: 1px dotted rgb(204, 204, 204)!important;}
    .p_pop, .p_pof, .sllt{background : rgba(228,228,228,0.9) !important;} 
    
    .zoom{ width: 70%;}
    
    
    
    
    
}
