css:
@-moz-document domain("mightytext.net") {
body {
    background-color: #282E31;
    background-size:cover;
}

div#pinnedContent {
    background-color: #282E31;
        background-image: url(http://www.animated-gifs.eu/perfect-loops-graphics/0089.gif) !important;
        background-image: url(http://www.animated-gifs.eu/perfect-loops-graphics/clic.php?url=./0001.gif) !important;
    
    background-size: cover;
    opacity:1;
    background-position:center center;
}

#singleMessageContainer .messageThread {
    background-color: rgba(0, 0, 0, 0.65);
}

#navBarTabs .thread-tab:hover {
    background: rgba(0, 0, 0, 0.47);
    border-top: 1px solid #1D1C1C;
    border-bottom: 1px solid #1F1F1F;
}

div#threadListHolder {
    border-right: 1px solid #2F2F2F;
    background-color: rgba(0, 0, 0, 0.47);
}

#navBarTabs > .active > a, #navBarTabs > .active > a:hover {
    background: #153038;
    border-top: 1px solid #419AB1 !important;
    border-bottom: 1px solid #419AB1 !important;
}

#singleMessageContainer .responseArea {
    background-color: rgba(0, 0, 0, 0.47);
    background: #232323;
    border: 1px solid rgba(0, 0, 0, 0.47);
}

#singleMessageContainer .threadItem {
    text-shadow: 1px 2px 1px rgba(65, 154, 177, 0.3);
    box-shadow: inset 0 3px 8px rgba(65, 154, 177, 0.18),0 9px 8px rgba(0, 0, 0, 0.23);
}
.sentText {
	background: #102429;
	border: 1px solid rgba(0, 0, 0, 0.47);
	
	box-shadow: inset 0 1px rgba(31, 31, 31, 0.6);
}

.threadItem {
    color: #FFF;
}

.receivedText {
    background: #05323E;
    border: 1px solid rgba(0, 0, 0, 0.47);
}

#navBarTabs > li * {
    color: #419AB1 !important;
    text-shadow: #000 0 1px 3px !important;
    border-color: #000 !important;
    transition: all 0.8s ease !important;
}

div#sideNavigation {
    background: rgba(0, 0, 0, 0.46);
    border-right: 1px solid #050607;
    box-shadow: #000 0 0 23px;
}

#navBarTabs > li {
    background-color: rgba(0, 0, 0, 0.36);
	width: 17em;
}
.textResponse{
	font-size:13px;
	font-weight: 400;
	border-radius: 3px;
	background-color: #111;
	border: 1px solid #419AB1;
	-webkit-box-shadow: 0 1px 0px 0px #FFF;
	-moz-box-shadow: 0 1px 0px 0px white;
	box-shadow: 0 1px 0px 0px #000;
	color:#FFF;
	text-shadow: rgba(8, 204, 255, 0.36) 0 0 12px;
}

.receivedText:before {
    border-color: rgba(191, 191, 191, 0);
    border-right-color: #419AB1 !important;
    border-width: 11px;
}
.receivedText:after {
    border-color: rgba(232, 232, 232, 0);
    border-right-color: #05323E;
    border-width: 10px;
}

.sentText:after {
	border-color: rgba(232, 232, 232, 0) !important;
	border-left-color: #102429 !important;
	border-width: 10px;
}
.sentText:before {
	border-color: rgba(191, 191, 191, 0) !important;
	border-left-color: #419AB1 !important;
	border-width: 11px;
}

::-webkit-scrollbar {

	width: 5px;
	height: 0;
	border: none;
	background: rgba(150, 150, 150, 0) !important;
}
::-webkit-scrollbar-thumb {
    width: 4px;
	background: #337382;
	border-radius: 0;
	border: none;
    box-shadow:cyan 0 0 12px;
}
.messageThread {
    background-color: rgba(6, 6, 6, 0.47);
    box-shadow: rgba(0, 0, 0, 0.74) 0 1em 1em;
}

.responseArea {
    background-color: rgba(0, 0, 0, 0.56);
    border: 1px solid rgba(65, 154, 177, 0.35);
}
div#album-list-container {
    background-color: rgba(0, 0, 0, 0.43);
    color: #FFF;
}
#schedulerTableWrapper {
    background: rgba(0, 0, 0, 0.47);
    color: #FFF !important;
}

#contactGroupsContainer span:first-child, .schedulerSubText span:first-child {
    color: #49c0fc !important;
}


.genericTableStyling {
    background-color: rgba(0, 0, 0, 0.48);
    color: #FFF;
}

div#individualContactsPane * {
    color: #38C9EF !important;
    text-shadow: #000 0 2px 2px;
}

.contactRow:hover {
    background: rgba(0, 73, 95, 0.5);
    border-top: 1px solid #060606;
    border-bottom: 1px solid #1AB1EF;
}

.contactActions a {
    background-color: rgba(0, 0, 0, 0.74);
}
#settingsPane {
    color: #419AB1;
    text-align: left;
    
}

.batbar {
    background-color: rgb(1, 255, 216) !important;
    box-shadow: rgb(1, 255, 216) 0 0 1em;
}
#share-button-main {
    display:none;
}

div#sideNavigation {
    background: rgba(0, 0, 0, 0.46);
    border-right: 1px solid #050607;
    box-shadow: #000 0 0 23px;
    transform: translateX(-80%);
    transition: all .8s ease;
}
div#sideNavigation:hover {
	background: rgba(0, 0, 0, 0.76);
    transform: translateX(0%) ;
}
div#pinnedContent {
    left: 3em;
}

div#threadListHolder {
    width: 5em;
    transition: all .8s ease;
}

div#threadListHolder:hover {
    width: 245px;
}
#singleMessageContainer .textResponse
{
    height: auto;
    position: absolute;
    box-shadow: rgba(0,0,0,.5) 0 -2em 2em;
    top: initial;
    bottom: 4em;
    width: 100%;
}
.responseArea
{
    height:auto !important;
    bottom:0;
    position:absolute;
}
.save-draft-button-holder,#singleMessageContainer .templateDropUpIcon {
    display: none;
}

#singleMessageContainer .threadConversationHolder {
    bottom: initial;
}
div#composeMessageHolder div#sendTo {
    position: absolute;
    bottom: 0;
    z-index: 100;
    height: 10px !important;
    display: inline-block;
}

div#composeMessageHolder .responseArea {
    margin-bottom: 6em;
}

ul.typeahead.dropdown-menu {
    transform: translateY(-130%);
    margin-bottom: 4em;
}

.adContainer.mightyClearfix {
    display: none !important;
}
div#heart-mt-button {
    display: none !important;
}

div#composeNewContentPanelHeader {
    top: 0em;
    position: relative;
}

div#composeMessageHolder {background-color: black;left: 0;width: 98.8%;box-shadow: black 0 -6em 9em;}

.composeNewMessageWrapper {
    position: relative;
    top: 0;
    left: 0.2em;
    height: 31em;
    width: 100%;
    background-color: black;
}

div#newMessageContentToHide {
    top: 0em;
    height: auto;
    position: relative;
    padding: 0;
    width: 100%;
    box-sizing: border-box;
}

div#sendTo {
    top: -25em;
    position: absolute;
}

div#sendTo {
    position: relative !important;
    top: 0;
    background-color: black;
    left: 0;
    width: 100%;
    box-sizing: border-box;
}

input#selectContactForSingleCompose {
    background-color: black;
    color: #999;
}

.responseArea {
    position: relative;
    /* height: 100%; */
    margin: 0;
    width: 100% !important;
    padding: 0;
}

div#send-one-text {
    resize: vertical;
    height: 26vh !important;
}

.responseButtonsHolder {
    margin: 0 !important;
    padding: 0;
}


div#undefined_MMSCanvasArea, .emoji-selector-container, #composeMessageHolder #newSMS_Emoji, button#newSMS_Scheduler {
    margin: 0 !important;
    margin-top: 0 !important;
    padding: 0 !important;
}
div#fancybox-content {
    background-color: black;
}

body #EEModal {
    background-color: black;
}

div#EEModalFooter {
    background-color: black;
}

div#fancybox-wrap {
    width: 90% !important;
    left: 50% !important;
    transform: translateX(-50%);
    position: absolute;
    margin: 0 !important;
    padding: 0 !important;
}

div#fancybox-content,div#fancybox-content>div {
    width: 100% !important;
}

#EEModal > .modal-body > .tab-content {
    display: flex;
    text-align: center;
}

.gifWrapper,.gifWrapper img {
    float: initial;
    display: inline-block;
    /* height:2em; */
}

#singleMessageContainer .responseButtonsHolder {
    position: absolute;
    bottom: 0;
    right: 2em;
}
}
