/* i really want this to be global */
/* ---------------Hackerplace.de CSS Style von Dimaa--------------- */
/* ---------------Main--------------- */

div.main {
    background: #e9e9e9 url("http://81.169.238.180/hacker/bg.png") top left repeat;
}
body {
    background: #e9e9e9 url("http://81.169.238.180/hacker/bg.png") top left repeat;
}
.bodybackground {
    background: #e9e9e9 url("http://81.169.238.180/hacker/bg.png") top left repeat;
}
a {
    color: #147E8A;
    text-decoration: none;
    font-weight: bold;
}
a:hover {
    text-shadow: 0em 0em 0.01em;
}
h1 {
    text-decoration: none;
    color: #FF0000;
}
h2 {
    color: #68e02e;
    text-decoration: none;
}
h3 {
    color: #68e02e;
}
textarea {
    background-image: url("http://81.169.238.180/hacker/textarea.png");
}
.textbox {
    background-image: url("http://81.169.238.180/hacker/textbox.png");
}
.button {
    background-image: url("http://81.169.238.180/hacker/button_new.png");
}
.button_back {
    background-image: url("http://81.169.238.180/hacker/button_back_new.png");
}
.button_logout {
    background-image: url("http://81.169.238.180/hacker/button_logout_new.png");
}
.button_big {
    background-image: url("http://81.169.238.180/hacker/button_big.png");
}
div#messagebeginnertutorial[style] {
    background: url("http://81.169.238.180/hacker/tutorial.png") right center no-repeat !important;
}
div#messageattack {
    /*display: block !important; //  http://81.169.238.180/hacker/anivote.gif */
    
    font-family: cursive;
    font-size: smaller;
    color: rgba(0, 183, 229, 0.9);
}
fieldset {
    border: 1px solid #68E02E;
}
.statusbarback {
    display: inline-block;
    width: 200px;
    height: 20px;
    border: 1px solid #cacaca;
    border-radius: 18px;
    background-image: url("http://81.169.238.180/hacker/status_bar_back.png");
}
.statusbarfront {
    background-image: url("http://81.169.238.180/hacker/status_bar_front.png");
    border-radius: 18px;
}
div.screens {
    background-color: rgba(104, 224, 46, 0.25);
    border: 1px solid #68e02e;
}
div.screens img {
    border: 1px solid rgb(104, 224, 46);
}
div.footer div {
    background: url("http://81.169.238.180/hacker/footer.png") repeat-x;
}
/* ------------------------------------------- */
/* ---------------Computerverwaltung--------------- */

.start_pc_work {
    background-image: url("http://81.169.238.180/hacker/pc_on.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.start_pc_active {
    background-image: url("http://81.169.238.180/hacker/pc_on.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.start_pc_inactive {
    background-image: url("http://81.169.238.180/hacker/pc_on.png");
    width: 34px;
    height: 34px;
    border: 0px;
}
.stop_pc_work {
    background-image: url("http://81.169.238.180/hacker/pc_off.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.stop_pc_active {
    background-image: url("http://81.169.238.180/hacker/pc_off.png");
    width: 34px;
    height: 34px;
    background-position: 0px -78px;
}
.stop_pc_inactive {
    background-image: url("http://81.169.238.180/hacker/pc_off.png");
    width: 34px;
    height: 34px;
}
.turn_power_off {
    background-image: url("http://81.169.238.180/hacker/power_off.png");
}
/* ------------------------------------------- */
/* ---------------Box--------------- */

div.boxhead {
    background: url("http://81.169.238.180/hacker/box_ol.png") left top no-repeat;
}
div.boxhead div {
    background: url("http://81.169.238.180/hacker/box_or.png") right top no-repeat;
}
div.boxhead div div {
    background: url("http://81.169.238.180/hacker/box_o.png") repeat-x;
}
div.boxbody {
    background: url("http://81.169.238.180/hacker/box_l.png") left repeat-y;
}
div.boxbody2 {
    background: url("http://81.169.238.180/hacker/box_r.png") right repeat-y;
}
div.boxbody3 {
    background: none;
    background-color: #fff;
    margin-left: 20px;
    margin-right: 20px;
}
div.boxfoot {
    background: url("http://81.169.238.180/hacker/box_ul.png") left bottom no-repeat;
}
div.boxfoot div {
    background: url("http://81.169.238.180/hacker/box_ur.png") right bottom no-repeat;
}
div.boxfoot div div {
    background: url("http://81.169.238.180/hacker/box_u.png") bottom repeat-x;
}
div.selecthead1 {
    background: url("http://81.169.238.180/hacker/select_ol.png");
}
div.selecthead2 {
    background: url("http://81.169.238.180/hacker/select_or.png");
}
div.selecthead3 {
    background: url("http://81.169.238.180/hacker/select_o.png") repeat-x;
}
div.selectbody1 {
    background: url("http://81.169.238.180/hacker/select_l.png") repeat-y;
}
div.selectbody2 {
    background: url("http://81.169.238.180/hacker/select_r.png") repeat-y;
}
div.selectbody3 {
    background-color: #f0eef0;
}
div.selectfoot1 {
    background: url("http://81.169.238.180/hacker/select_ul.png");
}
div.selectfoot2 {
    background: url("http://81.169.238.180/hacker/select_ur.png");
}
div.selectfoot3 {
    background: url("http://81.169.238.180/hacker/select_u.png");
}
/* ------------------------------------------- */
/* ---------------Menü--------------- */

div.menu {
    height: 70px;
    font-size: 32pt;
    text-align: center;
    text-shadow: 0em 0em 0.1em #68e02e;
    background: url("http://81.169.238.180/hacker/menu_ul.png") bottom no-repeat;
}
ul.menu li {
    display: block;
    text-align: center;
    height: 37px;
    width: 146px;
    cursor: pointer;
    padding: 13px 0 0 45px;
    margin-left: 34px;
    background-position: 0px 0px;
    background: url("http://81.169.238.180/hacker/menue_button_new.png") top no-repeat;
}
div.startvmenue {
    background-image: url("http://81.169.238.180/hacker/v_menue_left.png");
}
div.stopvmenue {
    background-image: url("http://81.169.238.180/hacker/v_menue_right.png");
}
ul.vmenue li {
    background: url("http://81.169.238.180/hacker/v_menue_top.png") repeat-x;
}
div.vmenuecut {
    background: url("http://81.169.238.180/hacker/v_menue_cut.png") right bottom no-repeat;
    padding-right: 2px;
}
ul.vmenue li:hover div div div div {
    background: url("http://81.169.238.180/hacker/v_menue_left_hover.png") left bottom no-repeat;
}
ul.vmenue li:hover div div div div div {
    background: url("http://81.169.238.180/hacker/v_menue_right_hover.png") right bottom no-repeat;
}
ul.vmenue li:hover div div div div div div {
    background: url("http://81.169.238.180/hacker/v_menue_top_hover.png") repeat-x;
}
ul.vmenue li ul li:hover div div div {
    background: url("http://81.169.238.180/hacker/v_menue_top_hover.png") repeat-x;
}
ul.vmenue li ul li:hover div div {
    background: url("http://81.169.238.180/hacker/v_menue_right_hover.png") right bottom no-repeat;
}
ul.vmenue li ul li:hover div {
    background: url("http://81.169.238.180/hacker/v_menue_left_hover.png") left bottom no-repeat;
}
/* ------------------------------------------- */
/* ---------------Remotedesktop--------------- */

#screen {
    background-color: #5A5A5A;
}
div#startmenu[style] {
    border-left-width: 1px !important;
    border-left-color: rgb(220, 162, 4) !important;
    border-right-width: 1px !important;
    border-right-color: rgb(220, 162, 4) !important;
    background-color: rgba(220, 162, 4, 0.8) !important;
}
#prgdl {
    background-color: #fff;
}
#prgscn {
    background-color: #fff;
}
#prgbnc {
    background-color: #fff;
}
#prghkt {
    background-color: #fff;
}
#prgdtm {
    background-color: #fff;
}
#prgsmg {
    background-color: #fff;
}
#prghkttodo {
    color: #fff;
}
.head {
    background-image: url("http://81.169.238.180/hacker/programm_leiste_head.png");
}
.mini {
    background-image: url("http://81.169.238.180/hacker/ablegen_grau.png");
}
.mini:hover {
    background-image: url("http://81.169.238.180/hacker/ablegen_blau.png");
}
.maxi {
    background-image: url("http://81.169.238.180/hacker/maximieren_grau.png");
}
.maxi:hover {
    background-image: url("http://81.169.238.180/hacker/maximieren_blau.png");
}
.maxi2 {
    background-image: url("http://81.169.238.180/hacker/minimieren_grau.png");
}
.maxi2:hover {
    background-image: url("http://81.169.238.180/hacker/minimieren_blau.png");
}
.close {
    background-image: url("http://81.169.238.180/hacker/schliessen_grau.png");
}
.close:hover {
    background-image: url("http://81.169.238.180/hacker/schliessen_blau.png");
}
.taskbar {
    background-image: url("http://81.169.238.180/hacker/inaktiv.png");
}
.taskbar:hover {
    background-image: url("http://81.169.238.180/hacker/inaktiv_hover.png");
}
.taskbar2 {
    background-image: url("http://81.169.238.180/hacker/aktiv.png");
}
.taskbar2:hover {
    background-image: url("http://81.169.238.180/hacker/aktiv_hover.png");
}
.bar {
    position: absolute;
    top: 475px;
    height: 25px;
    width: 100%;
    background-image: url("http://81.169.238.180/hacker/start_bar.png");
}
.startbutton {
    float: left;
    height: 22px;
    width: 0px;
    padding-top: 3px;
    padding-left: 28px;
    margin-right: 40px;
    background-image: url("http://81.169.238.180/hacker/start.png");
}
.startbutton:hover {
    background-image: url("http://81.169.238.180/hacker/start_hover.png");
}
.borderleft {
    width: 1px;
    background-color: #dca204;
}
.borderright {
    width: 1px;
    background-color: #dca204;
}
.borderbottom {
    height: 1px;
    background-color: #dca204;
}
.borderleftbottom {
    width: 1px;
    height: 1px;
    background-color: #dca204;
}
.borderrightbottom {
    left: 197px;
    width: 1px;
    height: 1px;
    background-color: #dca204;
}
.contextmenu {
    background-color: #fff;
    border: 1px solid #dca204;
}
/* ------------------------------------------- */
/* ---------------Xmas--------------- */

div.xmasheader div {
    background: url("http://81.169.238.180/hacker/hackerplace_xmas.png") bottom no-repeat;
}
div.xmasheader div div div {
    background: url("http://81.169.238.180/hacker/head_top.png") top repeat-x;
}
div.xmasheader div div div div {
    background: url("http://81.169.238.180/hacker/1.png") bottom repeat-x;
    /*Gibt es nicht */
    
    height: 250px;
}
