css:
@-moz-document url("http://bulaoge.cn/"), url("http://bulaoge.cn/sign_in.blg"), url("http://bulaoge.net/"), url("http://bulaoge.net/sign_in.blg"), url-prefix("http://bulaoge.cn/manage"), url-prefix("http://bulaoge.net/manage") {
.gg,
.warning,
img,
#footer,
#topLink,
h1 {
    display: none !important
}
body {
    background: #EBEBEB
}
#banner,
#footer,
#container,
#center,
h2,
#right,
.sidebar {
    background: transparent
}
.content h2,
.sidebar h2 {
    background: #404040;
    border: 0;
    color: #fff;
    font-weight: normal;
    font-size: 17px;
    text-align: center
}
/* #404040*/

.content h2 {
    line-height: 25px;
    height: 25px
}

.blg-content p {
    background: #fff;
    border: none !important;
}

a:hover {
    text-decoration: none !important;
    color: #ff3366;
    -webkit-transition: all 0.5s ease-in-out;
    -moz-transition: all 0.5s ease-in-out
}
a {
    -webkit-transition: all 0.5s ease-in-out;
    -moz-transition: all 0.5s ease-in-out;
    text-decoration: none !important
}
#right,
#banner {
    border: 0
}
.sidebar a {
    display: table;
    border-collapse: collapse;
}
.sidebar p {
    display: table;
    border-collapse: collapse;
    line-height: 100%
}
.sidebar a:hover {
    letter-spacing: 3px
}
#banner h2 {
    width: 120px;
    margin-right: 60px
}
#right {
    width: 150px;
    margin-right: 50px
}
#center {
    margin-top: -30px
}
.table-title {
    border: 0
}
.table-row img {
    display: inline !important
}
}
