css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("privateinternetaccess.com") {

#Frame, #Head, #wrapBody, #Content, #wrapperFoot,
.MessageList, .DataList, .Comments,
.Item, .ItemComment, .P, .Signature span,
.MessageForm, .CommentForm, .FormTitleWrapper,
.Message span, .Message div, .Message a,
.FormWrapper, .FormWrapper-Condensed, 
.Signature a, .Flyout, pre, code
{
background-color: #330000 !important;
}
.Box, .BoxCategories, .Count
{
background-color: #139F00 !important;
}
#Menu li:hover, .BigButton:hover
{
background-color: #0055CC !important;
}
iframe, html, input, select
{
color: #FFFFFF !important;
background-color: #000000 !important;
}
.Discussion > .Item, .PanelCategories .Active,
.PanelCategories li:hover, .TagCloud li
{
background: #330000 !important;
}
pre, code
{
border:1px #139F00 solid !important;
}
.UserSignature
{
border-top:1px #139F00 solid !important;
}
#Head
{
border-bottom:3px #330000 solid !important;
}
.Discussion > .Item, .Item, .ItemComment, .Box,
blockquote, .Quote, input, select, .Flyout 
{
border:1px #139F00 solid !important;
}
blockquote, .Quote
{
padding: 5px !important;
}
.NavButton, .ReactButton, .Tag-Announcement, 
.Tag-Closed, .OptionsTitle
{
box-shadow: none !important;
text-shadow: none !important;
background: #0055CC !important;
border-radius: 1px !important;
border: 1px #0055CC solid !important;
}
a, #Menu li a:hover, .BigButton:hover
{
color: #FFFFFF !important;
text-decoration:underline !important;
}
a:hover
{
color: #139F00 !important;
text-decoration:underline !important;
}
.Count
{
font-weight: bold !important;
}
.Message, .Username, .Permalink, .Box h4,
.InlineTags, .Meta, .Tag_slow_speed, dd,
.CommentHeading, .Message span, .H, .Box,
.PostEdited, .Signature, .UserSignature,
.PageDescription, dt, .Empty, .DataList, 
.About, .Name, .Joined, .Visits, .LastActive, 
.Roles, .Activities, .Count, .CheckBoxLabel,
label, .Email, .token-input-token, .SplashInfo,
.MItem, .MenuItems a, .MenuItems a:active, .MenuItems a:link, .MenuItems a:visited, .Message div
{
color: #FFFFFF !important;
}
}
