css:
@-moz-document domain("userstyles.org") {
hmtl,
body {
    box-sizing: border-box !important;
}
body {
    width: 1100px !important;
    max-width: 1100px !important;
    margin: 0 auto !important;
}
*,
*::before,
*::after {
    box-sizing: inherit !important;
}
a {
    color: #09F !important;
    padding: 2px !important;
}
a:hover {
    background-color: #09F !important;
    color: #FFF !important;
    text-decoration: none !important;
}
a:focus {
    background-color: tomato !important;
    color: #FFF !important;
    text-decoration: none !important;
}
#main-header {
    border-radius: 0 !important;
    background-color: #222 !important;
    color: #FFF !important;
    margin: 0 0 10px 0 !important;
}
#main-header h1 {
    height: 50px !important;
    padding: 0 20px !important;
}
#main-header h1 a {
    line-height: 50px !important;
    color: #09F !important;
}
#left-sidebar {
    margin: 26px 0 0 -1100px !important;
}
#left-sidebar nav {
    margin: 0 !important;
    padding: 0 !important;
    border-radius: 0 !important;
    background-color: #FFF !important;
    overflow: hidden !important;
}
#left-sidebar nav ul {
    width: 100% !important;
    overflow: hidden !important;
}
#left-sidebar nav li {
    width: 100% !important;
    float: left !important;
}
#search-terms {
    width: 100% !important;
    height: 30px !important;
    padding: 0 5px !important;
    outline: none !important;
    border: 3px solid #09F !important;
}
#show-advanced-search {
    margin: 0 0 2px 0 !important;
}
#advanced-search {
    background-color: #222 !important;
    color: #FFF !important;
}
#search-terms:focus {
    border-color: tomato !important;
}
#sidebar-search input[type=submit] {
    display: none !important;
}
#advanced-search {
    padding: 6px !important;
}
#left-sidebar nav li:not(#sidebar-search) {
    padding: 6px 10px !important;
    background-color: #F2F2F2 !important;
    margin: 0 0 4px 0 !important;
    font-weight: bold !important;
    color: tomato !important;
}
#left-sidebar nav li:not(#sidebar-search) a {
    display: block !important;
    width: 100% !important;
    height: 100% !important;
    color: #09F !important;
    text-decoration: none !important;
    font-weight: normal !important;
}
#left-sidebar nav li:not(#sidebar-search) a:hover {
    background-color: transparent !important;
    color: #555 !important;
    font-weight: bold !important;
}
#left-sidebar nav li:not(#sidebar-search) a:active {
    background-color: transparent !important;
}
#main-article {
    width: 880px !important;
    float: left !important;
    margin: 26px 0 0 220px !important;
    background-color: #F2F2F2 !important;
    padding: 30px !important;
}
#main-article > *:first-child {
    margin-top: 0 !important;
}
#main-article *:not(.CodeMirror) {
    color: #444 !important;
}
#main-article table,
thead {
    background-color: tomato !important;
}
#main-article thead tr th {
    color: #FFF !important;
}
#main-article tbody {
    background-color: #FFF !important;
    font-size: 12px !important;
}
#main-article ul {
    padding-left: 30px !important;
}
.pagination * {
    display: block !important;
    height: 30px !important;
    float: left !important;
    line-height: 28px !important;
    text-align: center !important;
}
.pagination a,
.pagination em,
.pagination .disabled {
    width: 40px !important;
    color: #FFF !important;
    background-color: rgba(0, 0, 0, 0.4) !important;
    margin: 0 2px !important;
    text-decoration: none !important;
}
.pagination em {
    background-color: tomato !important;
}
.pagination a:hover {
    background-color: rgba(0, 0, 0, 0.6) !important;
}
.pagination a:active {
    background-color: tomato !important;
}
.pagination .next_page,
.pagination .previous_page,
.disabled {
    width: 100px !important;
}
.pagination .gap,
.pagination .disabled {
    width: 20px !important;
    height: 30px !important;
    background-color: rgba(0, 0, 0, 0.2) !important;
}
#related {
    padding: 10px !important;
    margin: 0 !important;
    border-radius: 0 !important;
    background-color: #F2F2F2 !important;
}
#related h2 {
    margin: 0 0 10px 0 !important;
}
.install-status {
    border-radius: 0 !important;
    background-image: none !important;
    background-color: #0089C8 !important;
}
.install-status a {
    color: #F2F2F2 !important;
}
#stylish-installed-style-needs-update,
#stylish-installed-style-needs-update-chrome {
    background-color: #69B41C !important;
    color: #FFF !important;
}
#discussions-area,
#additional-info {
    border-left: 2px solid tomato !important;
    margin: 30px 0 !important;
    padding: 10px 0 10px 20px !important;
    background-color: #FAFAFA !important;
}
#discussions-area h2,
#additional-info h2 {
    margin: 0 0 20px 0 !important;
}
#style-author-info {
    margin: 0 !important;
}
#info-tables table tr > * {
    padding: 0 20px 0 6px !important;
    height: 26px !important;
    background-color: #FAFAFA !important;
}
}
