css:
@-moz-document domain("web.telegram.org"){
/*v1.4*/

a, a.md_modal_action, a.tg_checkbox, a.tg_radio, body, .btn-md-primary, .btn-md-primary:focus, .btn-md-primary:hover, .composer_stickerset_title, .composer_stickerset_title:hover, .composer_rich_textarea, .composer_textarea, .countries_scrollable_wrap a.countries_modal_country, h1, h2, h3, h4, h5, .im_dialog_message_text, .im_dialog_message_text:hover, .login_footer_wrap, .login_form_head, .im_message_text, .im_selected_count, .md-input, .logo_footer_learn_more_link, .logo_footer_learn_more_link:active, .logo_footer_learn_more_link:focus, .md_modal_head, .peer_modal_profile_description, .tg_head_btn, .tg_head_connecting_text, .tg_page_head .navbar-inverse .navbar-quick-nav > li > a, .dropdown-menu>li>a {color: #FFF;}

a:focus, a:hover, a.im_dialog:hover .im_dialog_message_text, a.im_dialog_selected .im_dialog_message_text, a.im_dialog:hover .im_dialog_date, a.im_dialog_selected .im_dialog_date, .btn-link, .btn-link:hover, .btn-md, .btn-md:focus, .btn-md:hover, .composer_emoji_tooltip_tab_emoji, .composer_emoji_tooltip_tabs_stickers_active .composer_emoji_tooltip_tab_stickers, .contacts_modal_members_list .active a.contacts_modal_contact .contacts_modal_contact_status, .contacts_modal_members_list .active a.contacts_modal_contact .md_modal_list_peer_description, a.contacts_modal_contact:hover .contacts_modal_contact_status, a.contacts_modal_contact:hover .md_modal_list_peer_description, .countries_scrollable_wrap a.countries_modal_country:hover .countries_modal_country_code, .im_dialog_chat_from_wrap, .im_dialog_message_media, .im_dialog_message_service, .im_message_date:focus, .im_message_fwd_date:focus, .im_message_author, .im_message_fwd_author, .im_message_selected .im_message_date, .im_history_select_active .im_message_outer_wrap:hover .im_message_date, .im_submit, .md_modal_list_peer_description .status_online, .md_modal_list_peer_description.status_online, .session_active_date_online {color: #69BB35;}

.login_form_lead, .error_modal_description, .im_history_empty, .im_history_not_selected, .composer_emoji_tooltip_tab_stickers, .composer_emoji_tooltip_tabs_stickers_active .composer_emoji_tooltip_tab_emoji, .composer_rich_textarea:empty::before, .md-input-label, .md_simple_form_description, .navbar-offline-text, .im_service_message, a.im_dialog:hover .im_short_message_text, a.im_dialog_selected .im_short_message_text {color: #A4A4A4;}

a.md_modal_action:hover, .btn-md:hover, .btn-primary {color: #000;}

@media (max-width: 768) {.im_message_body {background: #151515;} .im_message_selected .im_message_body, .im_message_selected .im_message_document, .im_message_selected .im_message_audio {background: #303030;} .composer_rich_textarea {border: 1px solid #303030;}}
a.composer_emoji_btn:hover, .login_form_wrap, .composer_rich_textarea:empty, .composer_rich_textarea:active, .composer_rich_textarea:focus {background: #151515;}
a.im_dialog:hover .im_dialog_unread, a.im_dialog_selected .im_dialog_unread {background: #B8FF8B;}
.active .im_dialog_unread {background-color: #3EA000;}

a.tg_radio_on:hover i.icon-radio, .btn-primary.active, .btn-primary:active, .btn-primary:focus, .btn-primary:hover, .open .btn-primary .dropdown-toggle, .btn-primary.disabled, .btn-primary[disabled], .tg_radio.tg_radio_on i.icon-radio {background-color: #69BB35;}
.btn-md:hover, .btn-primary, .btn-primary.disabled:hover, .btn-primary[disabled]:hover, .composer_emoji_tooltip_tab_shadow, .dropdown.open .tg_head_btn, .tg_head_btn:hover, .icon-message-status, .im_history_col .nano > .nano-pane > .nano-slider, .contacts_modal_col .nano > .nano-pane > .nano-slider, .sessions_modal_col .nano > .nano-pane > .nano-slider, .stickerset_modal_col .nano > .nano-pane > .nano-slider, .im_dialogs_modal_col .nano > .nano-pane > .nano-slider, .composer_emoji_tooltip .nano > .nano-pane > .nano-slider, .composer_dropdown_wrap .nano > .nano-pane > .nano-slider, .md_modal_head, .tg_head_btn:hover,.tg_head_btn,.tg_head_split,.nano-slider, .tg_page_head .navbar-inverse {background: #69BB35;}

.tg_head_peer_media_dropdown .dropdown-toggle .icon-bar, .navbar-toggle-wrap .navbar-toggle .icon-bar {background: #FFF}

.btn-md:hover, .btn-primary, .contacts_modal_search_field  {border-radius: 2px;}
.btn-primary.disabled, .btn-primary[disabled] {background:#2B2F30;}
.btn-primary.disabled, .btn-primary[disabled] {color: #000!important;}
.btn-primary.disabled:hover, .btn-primary[disabled]:hover {color: #FFF!important;}

.composer_emoji_tooltip_content_wrap {background: #363636;}
.composer_emoji_tooltip_categories, .icon-tooltip-tail, .composer_emoji_tooltip_tabs, .dropdown-menu .divider, .im_history_select_active .im_message_outer_wrap:hover, .im_message_selected {background: #303030;}
.composer_rich_textarea:empty, .composer_rich_textarea:active, .composer_rich_textarea:focus {border: none;}
.composer_emoji_tooltip_category {filter: invert(1); -webkit-filter: invert(1);}
.composer_emoji_tooltip {background: #363636; border: none;}
.composer_emoji_tooltip_tab_shadow {
    -webkit-box-shadow: 0 1px 0 0 #69BB35;
    -moz-box-shadow: 0 1px 0 0 #69BB35;
	box-shadow: 0 1px 0 0 #69BB35;}
.composer_rich_textarea, .composer_textarea {box-shadow:0px 1px 0px 0px rgba(107,107,107,0.65);}
.composer_rich_textarea:focus, .composer_textarea:focus {box-shadow: 0 2px 0 0 #69BB35;}

.contacts_modal_members_list a.contacts_modal_contact {color: #DDD;}
.contacts_modal_members_list .active a.contacts_modal_contact, .contacts_modal_members_list .active a.contacts_modal_contact:hover, .contacts_modal_members_list a.contacts_modal_contact:hover {background: #505050;}
.contacts_modal_search {background: #242424;}
.contacts_modal_search_field, .form-control.no_outline:focus, .form-control {border: 1px solid #303030!important;}

.countries_modal_col .nano > .nano-pane > .nano-slider {background: rgba(3, 79, 46, 0.22);}
.countries_modal_search_field {border: 1px solid #69BB35;}
.countries_modal_search_field:active, .countries_modal_search_field:active {background-color: #242424;}
.countries_modal_search_field:focus, .countries_modal_search_field:focus {background-color: #242424;}
.countries_scrollable_wrap a.countries_modal_country:hover {border-radius: 2px; background: #505050;}

.dropdown-menu {background-color: #333;}

.form-control, .form-control.no_outline:focus {color: #A4A4A4;	background-color: #242424;}

.has-scrollbar .nano-pane .nano-slider {margin-left:1px!important;}
.has-scrollbar .nano-slider {background:rgb(33,84,86); margin:0 -2px!important; width:8px; -webkit-border-radius:5px; -moz-border-radius:5px; border-radius:5px;}

html {background: #242424!important;}

.im_dialogs_scrollable_wrap a.im_dialog:hover,.im_dialogs_scrollable_wrap a.im_dialog_selected,.im_history_select_active .im_message_outer_wrap:hover,.im_message_outer_wrap:hover,.im_history_message_wrap .im_message_outer_wrap:hover,.im_dialogs_scrollable_wrap .active a.im_dialog {background:#505050!important;}
.im_dialogs_scrollable_wrap .im_dialog_wrap {background:#161616!important;}
.im_dialogs_scrollable_wrap a.im_dialog,.im_page_wrap {background-color:#242424!important;}
.im_dialog_badge_muted {background:#1ABEC5;}
.im_dialog_date:hover {color: #CACACA!important;}
.im_dialog_peer {color:#999;}
.im_dialog_unread {background:#5C9239;}
.im_dialogs_col_wrap {border-right:2px solid #303030;}
.im_dialogs_search_field {background-color:#505050;border:1px solid #303030;border-radius:5px;}
.im_dialogs_search_field:active,.im_dialogs_search_field:focus {background-color:#454545;}
.im_edit_panel_border, .md-input-group, .md_modal_iconed_section_wrap {border-bottom:1px solid #303030;}
.im_history_col .nano > .nano-pane > .nano-slider, .contacts_modal_col .nano > .nano-pane > .nano-slider, .sessions_modal_col .nano > .nano-pane > .nano-slider, .stickerset_modal_col .nano > .nano-pane > .nano-slider, .im_dialogs_modal_col .nano > .nano-pane > .nano-slider, .composer_emoji_tooltip .nano > .nano-pane > .nano-slider, .composer_dropdown_wrap .nano > .nano-pane > .nano-slider {-webkit-border-radius: 5px; -moz-border-radius: 5px; border-radius: 5px; right: 1px;}
.im_history_col .nano > .nano-pane, .contacts_modal_col .nano > .nano-pane, .sessions_modal_col .nano > .nano-pane, .stickerset_modal_col .nano > .nano-pane, .im_dialogs_modal_col .nano > .nano-pane {background: rgba(216, 223, 225, 0);}
.im_history_select_active .im_message_outer_wrap:hover,.im_message_selected .im_message_outer_wrap {background:#F2F6FA;}
.im_message_selected .im_message_outer_wrap, .im_history_message_wrap .im_message_selected .im_message_outer_wrap:hover {background:#060606!important;}
.im_message_unread_split {background:#6F6F6F;color:#D2D2D2;}
.im_message_reply_wrap, .im_message_reply {color:#727272;}
.im_message_webpage_description {color:rgb(164,164,164);}
.im_page_wrap {border-left: 1px solid #303030; -webkit-box-shadow:none; border-right:1px solid #303030; border-bottom:1px solid #303030;}

i.icon-verified, .icon-emoji, .icon-camera, .icon-new-group, .icon-contacts, .icon-settings, .icon-faq, .icon-about, .icon-paperclip, .im_message_selected .icon-select-tick, .im_history_selectable .im_message_outer_wrap:hover .icon-select-tick, .md_modal_split_action_msg, .md_modal_split_action_camera, .md_modal_split_action_twitter, .composer_emoji_tooltip_category.active, .composer_emoji_tooltip_category.active:hover, .is_2x .im_submit, .is_2x .im_submit:active, .is_2x .im_submit:hover, input[type=search] {filter: hue-rotate(240deg); -webkit-filter: hue-rotate(240deg);}
.icon-tooltip-tail {border-color: #363636;}
.icon-tg-logo, .telegram_modal_logo {filter: hue-rotate(240deg) saturate(2) drop-shadow(0px 0px 15px #69BB25); -webkit-filter: hue-rotate(240deg) saturate(2) drop-shadow(0px 0px 15px #69BB25);}
.icon-tg-title {filter: drop-shadow(0px 0px 15px #FFF); -webkit-filter: drop-shadow(0px 0px 15px #FFF);}

.login_footer_about_wrap {background: #242424; margin-top: 12px; padding: 44px 65px; 
	-webkit-box-shadow: 0 1px 0 #242424; 
	-moz-box-shadow: 0 1px 0 #242424; 
	box-shadow: 0 1px 0 #242424; border-radius: 3px; border: 1px solid #242424;}

.login_head_bg, .md-input {background: transparent;}
.login_page {margin: 0px auto 90px; max-width: 404px; top: -131px; position: relative;}

.logo_footer_learn_more_link, .logo_footer_learn_more_link:active, .logo_footer_learn_more_link:focus {font-weight: 700;}
.logo_footer_learn_more_link:hover {color: #69BB35!important; font-weight: 700;}

.md-input-focused {border-bottom: 2px solid #69BB35;}
.md_modal_footer {background: #242424;}
.modal-content {background-color: #242424;}
.nano {background: #242424;}

.page_wrap{background-image:url(http://i.imgur.com/AMJjcoL.jpg)!important; background-size:contain!important; padding-bottom: 100%;}

::selection {background-color:#69BB35!important;}

.tg_checkbox.tg_checkbox_on i.icon-checkbox-inner {background: #9BC26B;}
.tg_checkbox.tg_checkbox_on span.icon-checkbox-outer {background-color: #D9EDBF;}
.tg_head_logo_dropdown .dropdown-menu > li > a:hover,.tg_head_peer_dropdown .dropdown-menu > li > a:hover{background:#505050;}
.tg_head_logo_dropdown .dropdown-menu {border-radius: 0; padding: 9px 0 7px; margin: -1px 0 0; float: none; right: 0; border: 1px solid rgba(0, 0, 0, 0.2); border-left-color: rgba(0, 0, 0, 0.2); border-right-color: rgba(0, 0, 0, 0.2); -webkit-box-shadow: 0px 1px 3px 0px rgba(60, 75, 87, 0.27); -moz-box-shadow: 0px 1px 3px 0px rgba(60, 75, 87, 0.27); box-shadow: 0px 1px 3px 0px rgba(60, 75, 87, 0.27); overflow: hidden;}
.tg_head_peer_dropdown .dropdown-menu > li > a,.tg_head_logo_dropdown .dropdown-menu > li > a{color:#D2D2D2;}
.tg_head_peer_dropdown .dropdown-menu {max-width: 100%; border-radius: 0; padding: 9px 0; margin: -1px 0 0 0; border: 1px solid rgba(31, 96, 15, 0.2); 
	-webkit-box-shadow: 0px 1px 3px 0px rgba(69, 87, 60, 0.27); 
	-moz-box-shadow: 0px 1px 3px 0px rgba(60, 75, 87, 0.27); 
	box-shadow: 0px 1px 3px 0px rgba(65, 87, 60, 0.27);}
.tg_radio.tg_radio_on .icon-radio-outer {border-color: #69BB35;}
.tg_slider_thumb {background: #9BC26B;}
.tg_slider_track {position: absolute; background: #DAECC4;}
.tg_slider_track_fill {background: #AFD283;}
}
