css:
@-moz-document url-prefix("https://www.duolingo.com/") {
body {background: #414B4C !important;}
.persistent-notification {background: #2F3739 !important; box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
main:not(.full-width) .page-main {background: #2F3739 !important; box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
h1 {color: #DEDEDE !important;}
.skill-badge-small {color: #DEDEDE !important;}
.bonus-row {background: #2F3739 !important; border: none !important;}
.btn.disabled, .btn[disabled], fieldset[disabled] .btn {background: #414B4C !important;    border: 1px solid #2F3739 !important;color: #DEDEDE !important;}
.skill-icon.locked {background: #414B4C !important;}
.btn-yellow {background: #627273 !important;color: #DEDEDE !important;}
.btn-store {background: #414B4C !important;color: #DEDEDE !important;border: 1px solid #2F3739 !important;}
.bg-white {background: #2F3739 !important; box-shadow: 0px 3px 2px #2F3739 !important;}
h2 {color: #DEDEDE !important;}
.skill-icon.skill-icon-placeholder {border: 2px dashed #414B4C !important;}
.btn-primary, .btn-blue {background: #414B4C !important;color: #DEDEDE !important;border: 1px solid #2F3739 !important;}
.btn-strengthen-skills .strength {    background: #FBB430 !important;}
.box-gray {background: #2F3739 !important; box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
.btn-white {background: #414B4C !important;color: #DEDEDE !important;border: 1px solid #2F3739 !important;}
.gray-dark {color: #DEDEDE !important;}
.popover {background: #2F3739 !important; box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
.list-notifications {color: #DEDEDE !important;}
.list-notifications>li {border: none !important;}
.store-shelf>li {border: none !important;}
h4 {color: #DEDEDE !important;}
body {color: #DEDEDE !important;}
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {background: #414B4C !important;}
table#vocab-list>tbody>tr>td:nth-child(2), table#vocab-list>tbody>tr>td:nth-child(3) {color: #DEDEDE !important;}
.table th, .table td {border: none !important;}
table#vocab-list>tbody>tr.active>td, table#vocab-list>tbody>tr:hover>td {background: #343D3E !important; border: 1px solid #414B4C !important;}
.stream-item {    border-left: 3px solid #586465 !important;}
.stream-item-type {border-top: 3px solid #586465 !important;    border-bottom: 3px #586465 !important; }
.nav-tabs {    border-bottom: 2px solid #6D6D6D !important;}
.dropdown-menu {background: #475254 !important; box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
.dropdown-menu>li>a {color: #DEDEDE !important;}
.list-discussion-item {border: none !important;}
input[type=text], input[type=password], .fake-input {background: #414B4C !important;border: 2px solid #2F3739 !important;color: #DEDEDE !important;}
.sticky-discussion-message {background: #414B4C !important;border: 2px solid #2F3739 !important;color: #DEDEDE !important;}
.vote-module-small .vote-count {background: #414B4C !important;border: 2px solid #2F3739 !important;color: #DEDEDE !important;}
.nav-sidebar a {color: #DEDEDE !important;}
.nav-sidebar>li.divider {border: none !important;}
.nav-sidebar>li:hover {background: #414B4C !important;}
.btn-outline.btn-standard {border: 2px solid #414B4C !important;color: #DEDEDE !important;background: #414B4C !important;}
.discussion-header {border: none !important;}
.locked-discussion-message {color: #DEDEDE !important;background: #414B4C !important;}
.discussion-comments-list-item {border-top: 1px solid #414B4C !important;}
.locked-discussion-message .icon-lock {    opacity: 0.9 !important;}
.speech-bubble .inner {background: #414B4C !important; box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
.list-document-item {border: none !important; }
.translator-stats .document-info {border-top: 1px solid #414B4C !important;}
.sidebar-progress {border-top: 1px solid #414B4C !important;}
.language-progress-bar-small {background: #414B4C !important;}
.modal {background: #414B4C !important;}
#app.wiki-translation .lesson-like-document .document-header-new {background-color: #2F3739 !important;border:none !important;}
#app.wiki-translation .lesson-like-document #document-hider {background-color: #2F3739 !important;}
#app.wiki-translation .lesson-like-document .wiki-document-body .wiki-paragraph, #app.wiki-translation .lesson-like-document .wiki-document-body p {color: #DEDEDE !important;}
#app.wiki-translation .lesson-like-document #document-omnibox .legend-text {color: #DEDEDE !important;}
.hint-popover .inner {color: #DEDEDE !important;    background-color: #414B4C !important;}
.hint-table table tr td {border:none !Important;}
.hint-popover .inner:before, .hint-popover .inner:after { background: #414B4C !important;}
#app.wiki-translation .lesson-like-document .translation-footer {color: #DEDEDE !important; }
#app.wiki-translation .lesson-like-document .instructions {  border: 1px solid #414B4C !important;box-shadow: 0px 3px 2px #2B3335 !important;}
.translation-sidebar .legend-circle.not-yet-translated {border: 1px solid #414B4C !important;}
.translation-sidebar .legend-circle.needs-checking {border: 1px solid #414B4C !important;}
.translation-sidebar .legend-circle.checked-by-community {background: #414B4C !important;color: #DEDEDE !important;border: 1px solid #414B4C !important;}
.translation-sidebar .legend-circle.checked-by-user {background: #414B4C !important;border: 1px solid #414B4C !important;}
hr {border-top: 1px solid #414B4C !important;}
.tab-switch {border: 1px solid #414B4C !important;}
.tab-switch>li>a.active, .tab-switch>li>a:active {color: #DEDEDE !important;    background: #414B4C !important;}
.tab-switch>li>a {color: #DEDEDE !important; }
.document-progress-bar {    border: 2px solid #414B4C !important;}
.list-checkable input[type="checkbox"] {    border-color: #2F3739 !important;}
input[type=checkbox] {   background: #414B4C !important;}
#app.wiki-translation .lesson-like-document .document-header-new .progress-bar-dynamic {background: #414B4C !important;}
.simple-revisions>li {border-top: none !important;}
.simple-revisions>li:hover {background: #414B4C !important;}
.tips-notes-panel table tr td:first-child {border-left: none !Important;}
.tips-notes-panel table td {border-right: none !Important;}
.tips-notes-panel table td {border-bottom: 2px solid #414B4C !important;}
.player-container {background: #2F3739 !important; box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
.player-footer {background: #2F3739 !important;}
.player-header {border:none !Important;}
.progress-bar-distinct>li {background: #414B4C !important;border-left: 2px solid #2F3739 !important;}
.text-to-translate {background: #414B4C !important;}
textarea, .fake-textarea {background: #414B4C !important;}
.textarea-translate, .input-listen[type="text"], .listen-input, .one-line-input[type=text] {border: 1px solid #414B4C !important;box-shadow: 0px 3px 2px #2F3739 !important;border: none !important;}
.progress-bar-dynamic.strength-bar {    background-color: #414B4C !important;   }
.progress-bar-dynamic.strength-bar .skill-icon-container .challenge-num {color: #0E0E0E !important;}
textarea, .fake-textarea {color: #DEDEDE !important;}
.badge-correct-big, .badge-wrong-big {    background: #414B4C !important;}
.btn-faded.btn-green.btn-outline {    border-color: #414B4C !important;}
.btn-faded.btn-red.btn-outline {border-color: #414B4C !important;}
.blue-select {-webkit-box-shadow: none !Important;box-shadow: none !Important;}
.player-footer h2 { background: #414B4C !important;color: #DEDEDE !important;}
.progress-bar-dynamic.strength-bar .bar .bubbles {background-image: none !important;}
.progress-xp .col-left {border-right: none !important;}
.session-end-footer  { background-color: #2F3739  !important;   }
.carousel-dot.active {    border: 6px solid #414B4C !important;}
.list-lesson-item.locked .inner {    background: #414B4C !important;    box-shadow: 0px 3px 2px #252B2B !important;}
.list-lesson-item.past .inner {    background-color: #3C4B4C !important;    box-shadow: 0px 3px 2px #252B2B !important;}
.select-images.hover-effect>li { background: #414B4C !important;    box-shadow: 0px 3px 2px #252B2B !important;    border: 2px solid #414B4C !important;}
input[type=radio].player-radio {    background: #2F3739 !important;}
.list-tilted-images>li {    border: 5px solid #414B4C !important;}
.lingot-bill {    background: #414B4C !important;}
.lingot-bill-items>li {border:none !important;}
.list-judge-options.hover-effect .white-label {background-color:#414B4C !Important;}
.list-judge-options.hover-effect .white-label input[type=checkbox] {border-color: #2F3739 !important;}
.list-judge-options.hover-effect .white-label {    border: 2px solid #2F3739 !important;}
.modal-footer {border:none !Important;}
.modal-footer {    color: #E6E6E6 !important;}
.skill-progress-headline span {color: #E6E6E6 !important;}
.skill-progress-headline {    color: #DEDEDE !important;}
}
