css:
@-moz-document domain("t411.ch") {
.toolbar {
    display: initial !important;
}

:focus {
    outline: none !important;
}

body {
    background: #FFFFFF
}

div.loginBar .down {
    color: #ee3c39!important;
}

td.down {
    color: #a80318!important;
}
/* 1 - Header ***********************************************************/

.headerPlace {
    height: 100% !important;
    margin: 0px !important;
    min-width: inherit !important;
    position: fixed !important;
    width: 200px !important;
    z-index: 100 !important;
}

.content {
    max-width: 99999999999px;
}

.loginWrapper {
    display: none !important;
    overflow: visible !important;
    position: fixed !important;
    height: 0px !important;
    width: 0px !important;
    margin: 0% 50% !important;
    padding-left: 100px !important;
    top: 0px !important;
    min-height: 0 !important;
}

.active .loginWrapper {
    display: block !important;
}
/* Menu général */

.userBox {
    position: relative;
    background: #fff !important;
    height: 390px !important;
    width: 500px !important;
    margin: 20px auto !important;
    margin-left: -320px !important;
    margin-top: 100px !important;
    padding: 40px 60px 30px 80px !important;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5) !important;
    z-index: 20 !important;
}

.userBox .column {
    width: auto !important;
}

.userBox .column li {
    background: #036BA8 !important;
    height: 45px !important;
    line-height: 45px !important;
    margin: 13px !important;
    text-align: center !important;
    transition: all 0.3s !important;
    width: 210px !important;
    text-shadow: 0 0 5px rgba(0, 0, 0, 0.5) !important
}

.userBox .column li a {
    display: block !important;
    height: 100% !important;
    text-decoration: none !important;
    width: 100% !important;
    color: white !important;
}

.userBox .column li:hover {
    background: #024268 !important;
}

.userBox .column:nth-of-type(3) {
    margin-left: 120px !important;
}

.userBox .avatar-big {
    display: none !important;
}
/* Fin du Menu général */
/* A - Boite de connexion */
/* A1 - Boite de connexion - Partie haute*/

.loginBox {
    overflow: hidden !important;
    margin-top: 100px !important;
    margin-left: -280px !important;
    padding: 10px !important;
    padding-top: 30px !important;
    width: 560px !important;
    height: 350px !important;
    background: white !important;
    border-radius: 5px !important;
    box-shadow: 0 0 0px 2000px rgba(0, 0, 0, .5), 0 0 10px black !important;
}

#loginform {
    width: 330px !important;
    height: 124px !important;
    margin-left: 90px !important;
    text-align: right !important;
    padding-top: 40px !important;
    padding-bottom: 40px !important;
}

.userInput, .passInput {
    -moz-appearance: none !important;
    font-size: 16px !important;
    padding: 5px 10px !important;
    margin: 0 !important;
    height: 40px !important;
    width: 180px !important;
    margin-bottom: 10px !important;
    color: #555 !important;
    font-family: 'Open Sans', sans-serif !important;
    background: #EDEDED !important;
    border: 1px solid #D6D6D6 !important;
    border-radius: 3px !important;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.07) !important;
}

.userInput:focus, .passInput:focus {
    -moz-appearance: none !important;
    box-shadow: rgba(14, 78, 134, 0.5) 0 0 8px !important;
    border: 1px solid #bbb !important;
}

.username, .password {
    margin-right: 15px !important;
    font-family: " Open Sans ", sans-serif !important;
    font-size: 14px !important;
    font-weight: normal !important;
    color: #444 !important;
}

.loginbtn {
    -moz-appearance: none !important;
    width: 200px !important;
    height: 40px !important;
    margin: 20px 40px 10px !important;
    padding: 12px !important;
    border: none !important;
    border-radius: 4px !important;
    font-size: 13px !important;
    color: white !important;
    background: #036BA8 !important;
    transition: all 0.3s !important;
}

.loginbtn:hover, .loginbtn:focus {
    background: #024268 !important;
}

.loginbtn:focus {
    padding-left: 150px !important;
    background: #024268 !important;
}

.rememberMe {
    margin-left: 140px !important;
    margin-top: 4px !important;
}

.remMe {
    margin-top: 0 !important;
}
/* A2 - Boite de connexion - Partie basse*/

.register {
    position: relative !important;
    width: 580px !important;
    margin-top: 40px !important;
    margin-right: -150px !important;
    height: 70px !important;
    text-align: center !important;
    background: #eaeaea !important;
    border-top: 1px solid #e5e5e5 !important;
    padding: 0 !important;
}

.register li {
    float: none !important;
    margin-top: 10px !important;
    border-left: none !important;
}

.register li a {
    font-family: " Open Sans ", sans-serif !important;
    font-size: 14px !important;
    font-weight: normal !important;
    display: inline-block !important;
    padding: 5px !important;
}

.register li:first-of-type:before {
    content: 'Vous n\'avez pas encore de compte ? \00a0';
    font-family: " Open Sans ", sans-serif !important;
    font-size: 14px !important;
    font-weight: normal !important;
}
/* A - FIN - Boite de connexion */
/* B - Menu latéral */

.headWrapper {
    height: 100% !important;
    padding-top: 0px !important;
    position: relative !important;
    width: 200px !important;
    box-shadow: rgba(0, 0, 0, 0.8) 0 0 5px !important;
    background: #024268 !important;
}
/* B1 - Menu latéral - Partie Haute*/

.head {
    height: auto !important;
    padding-top: 0px !important;
    width: auto !important;
}

.logo {
    position: relative !important;
    height: 120px !important;
    background-position: 50% 50% !important;
}

.toolbar {
    float: inherit !important;
    height: auto !important;
    position: relative !important;
    right: 0 !important;
    top: 0 !important;
}

.searchBar {
    float: inherit !important;
    height: auto !important;
    width: auto !important;
}

.searchBtn {
    display: none !important;
}

.searchInput {
    border-radius: 0px !important;
    height: 25px !important;
    margin: 0 !important;
    transition: all 0.5s !important;
    width: 189px !important;
    z-index: 1000;
    opacity: 0.5;
}

.searchInput:hover {
    opacity: 0.85;
}

.searchInput:hover ~ .searchHistory {
    opacity: 0.875;
}

.searchInput:hover:focus {
    opacity: 1;
}

.searchInput:hover:focus ~ .searchHistory {
    opacity: 1;
}

.searchInput:focus {
    width: 280px !important;
    box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.8) !important;
    border: 0 !important;
    opacity: 1;
}

.searchInput:focus ~ .searchHistory {
    width: 291px !important;
    box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.8) !important;
    border: 0 !important;
    opacity: 1;
}

.searchHistory {
    transition: all 0.5s !important;
    background: #036BA8 !important;
    clear: both !important;
    display: block !important;
    height: 5px !important;
    margin-top: 0px !important;
    width: 200px !important;
    opacity: 0.5;
}

.loginBar {
    background: none !important;
    clear: both !important;
    float: inherit !important;
    height: auto !important;
    padding: 3px !important;
    text-align: center !important;
}

.loginBar a.logout {
    background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -1209px 3px no-repeat !important;
    padding: 12px !important;
    padding-top: 15px !important;
    font-size: 0px !important;
    transition: all 0.4s !important;
    opacity: 0.6 !important;
}

.loginBar a.logout:hover {
    opacity: 1 !important;
}

.loginBar a.reg {
    margin-left: 0px !important;
    margin-right: 4px !important;
}

.loginBar a.mail {
    background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -1573px -1px !important;
    transition: all 0.4s !important;
    opacity: 0.6 !important;
    text-decoration: none !important;
}

.loginBar a.mail:hover {
    opacity: 1 !important;
}

.loginBar span {
    display: inline-block !important;
    float: inherit !important;
    /*margin: 0 !important;*/
    
    margin-left: 2px !important;
    margin-right: 2px !important;
    color: white !important;
}

.loginBar span.sep {
    display: none !important;
}

.loginBar span.slideToo {
    background: url('http://i.imgur.com/SJqfRxC.png') 78px 10px no-repeat !important;
    background-position: center;
    clear: both !important;
    color: transparent !important;
    display: block !important;
    font-size: 20px !important;
    margin-bottom: 20px !important;
    padding: 0px !important;
    padding-top: 20px !important;
    text-align: center !important;
    transition: all 0.4s !important;
    opacity: 0.6 !important;
}

.loginBar span.slideToo:hover {
    opacity: 1 !important;
}

.active .loginBar span.slideToo {
    opacity: 1 !important;
}

.active .loginBar span.slideToo:after {
    transition: background 0.3s !important;
    top: 0px !important;
    background: rgba(0, 0, 0, 0.5) !important;
}

.loginBar span.slideToo:after {
    transition: background 0.3s !important;
    content: '' !important;
    display: block !important;
    position: fixed !important;
    top: 5000px !important;
    left: 200px !important;
    height: 100% !important;
    width: 100% !important;
    z-index: -1 !important;
    background: rgba(0, 0, 0, 0) !important;
}

.loginBar span:nth-of-type(2) {
    clear: both !important;
}

.loginBarSide {
    display: none !important;
}
/* B12 - Menu latéral - Partie basse*/

#lavamenu {
    margin: 0 !important;
}

#lavamenu li {
    border: none !important;
    border-radius: 0 !important;
    height: 38px !important;
    margin: 0px !important;
    padding: 0px 0px !important;
    text-shadow: 0 0 3px black !important;
    width: 200px !important;
}

#lavamenu li a {
    margin: 0px !important;
    padding: 10px 0px !important;
}

#lavamenu li ul {
    background: #024268 !important;
    border: 0 !important;
    border-radius: 0 10px 10px 0px !important;
    left: 200px !important;
    margin: 0px !important;
    overflow: hidden !important;
    padding: 0px !important;
    position: relative !important;
    top: -114px !important;
    z-index: -1 !important;
    box-shadow: inset 10px 0px 10px -10px rgba(0, 0, 0, 0.8) !important;
}

#lavamenu li ul li:hover {
    background: #036BA8 !important;
    box-shadow: inset 10px 0px 10px -10px rgba(0, 0, 0, 0.8) !important;
}

#lavamenu li:nth-of-type(7) ul {
    top: -38px !important;
}

#lavamenu li:nth-of-type(8) ul {
    top: -260px !important;
}

#lavamenu .rss {
    padding: 10px 0px 10px 20px !important;
    background-position: 7% 50% !important;
}

.backLava {
    background: #036BA8 !important;
}
/* B - FIN - Menu latéral */

.ui-autocomplete {
    z-index: 101 !important;
}
/* 2 - Content *****************************************************/

.content {
    margin-left: 200px !important;
    margin-right: 0 !important;
    padding: 0 !important;
}

.block>div {
    padding: 0 !important;
}

.block h2 {
    background: #036BA8 !important;
    border: none !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    margin: 0 !important;
    padding: 7px !important;
}

.block h2 span {
    border: none !important;
    border-radius: 0 !important;
    font-weight: 300;
    font-size: 25px;
}
/*Boutons URL raccourcie*/

h2 .shortlink {
    display: block !important;
    position: absolute !important;
    font-size: 0px !important;
    height: 40px !important;
    width: 50px !important;
    top: -7px !important;
    right: -6px !important;
    padding: 0 !important;
    margin: 0 !important;
}

h2 .shortlink:hover {
    background: #024268 !important;
}

h2 .shortlink:before {
    display: block !important;
    position: absolute !important;
    content: "" !important;
    background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -1177px 5px no-repeat !important;
    height: 30px !important;
    width: 30px !important;
    top: 4px !important;
    left: 11px !important;
}
/*Boite URL raccourcie*/

#shortlink .inner .shorthref {
    -moz-appearance: none !important;
    box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.4) !important;
    border: none !important;
    padding: 5px 10px !important;
    width: 355px !important;
}

#shortlink .inner hr {
    display: none !important;
}

.btn {
    -moz-appearance: none !important;
    color: #fff !important;
    cursor: pointer !important;
    font-size: 13px !important;
    font-weight: 700 !important;
    min-width: 80px !important;
    display: block !important;
    float: left !important;
    text-align: center !important;
    padding: 7px 10px !important;
    margin: 4px !important;
    background: #036BA8 !important;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5) !important;
    border: 0 !important;
    border-radius: 3px !important;
    transition: all 0.3s !important;
}

.btn:hover {
    background: #024268 !important;
}

.btn:active {
    background: #036BA8 !important;
    box-shadow: inset 1px 1px 3px rgba(0, 0, 0, 0.5) !important;
}
/*Telecharger plus tard*/

.share>.alignleft a {
    position: fixed !important;
    bottom: -1px !important;
    left: 242px !important;
    display: block !important;
    background: #036BA8 !important;
    color: white !important;
    padding: 13px 20px !important;
    transition: all 0.3s !important;
    font-size: 0 !important;
    font-weight: 700 !important;
    border: 0 !important;
    text-decoration: none !important;
    z-index: 11;
}

.share>.alignleft a:before {
    font-size: 13px !important;
    content: "Télécharger plus tard" !important;
}

.share>.alignleft a:hover {
    background: #024268 !important;
}

.share>.alignleft a:active {
    background: #036BA8 !important;
    box-shadow: inset 1px 1px 3px rgba(0, 0, 0, 0.5) !important;
}
/*Boutons suivant et précédents*/

.navigation {
    -moz-appearance: none !important;
    padding: 0 !important;
    background: #036BA8 !important;
    margin: 0 !important;
    width: 100% !important;
    height: 42px !important;
    position: fixed !important;
    bottom: 0 !important;
    margin-left: 0 !important;
    z-index: 10 !important;
    overflow: visible !important;
    border-top: 1px solid #024268 !important;
}

.navigation .alignright {
    margin-right: 200px !important;
}

.navigation .alignleft a, .navigation .alignright a {
    font-size: 0 !important;
    color: black !important;
    display: block !important;
    position: relative !important;
}

.navigation .alignleft a:after, .navigation .alignright a:before {
    font-size: 14px !important;
    color: black !important;
    background: red !important;
    padding: 21px !important;
    display: block !important;
}

.navigation .alignleft a:after {
    content: " " !important;
    background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #036BA8 -393px 12px no-repeat !important;
    box-shadow: inset 10px 0px 10px -10px rgba(0, 0, 0, 0.8) !important;
}

.navigation .alignleft a:hover:after {
    background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #024268 -393px 12px no-repeat !important;
}

.navigation .alignright a:before {
    content: " " !important;
    background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #036BA8 -422px 12px no-repeat !important;
}

.navigation .alignright a:hover:before {
    background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #024268 -422px 12px no-repeat !important;
}
/*Editer et Supprimer*/

.block.navigation {
    /*width: 150px !important;*/
    height: 40px !important;
    position: absolute !important;
    right: 50px !important;
    top: 0px !important;
    margin-left: 0px !important;
    border: 0 !important;
}

.block.navigation a {
    display: block !important;
    padding: 0px !important;
    margin: 0px !important;
    border: none !important;
    background: none !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    font-size: 0px !important;
    height: 40px !important;
    width: 50px !important;
    min-width: 0px !important;
}

